html.p:
R_TRIG_Activate(CLK := xActivate);
F_TRIG_Activate(CLK := xActivate);


(*Activation of FB*)
IF R_TRIG_Activate.Q THEN


	xActive 		:= TRUE;
	wCodeResp		:= WORD#16#0000;
	wParameterCount	:= WORD#16#0000;
	wResult			:= WORD#16#0000;
	iIndexDst		:= 0;
	iIndexEnd		:= 0;
	iIndexSrc		:= 0;
	xError			:= FALSE;
	xErrorReqCode	:= FALSE;
	xReady			:= FALSE;
	xErrorResult	:= FALSE;
	wCode			:= WORD#16#0000;
	wDiagCode		:= WORD#16#0000;
	wAddDiagCode	:= WORD#16#0000;
	iDataLength		:= 0;




(*Deactivation of FB*)
ELSIF F_TRIG_Activate.Q THEN


	xActive 		:= FALSE;
	wCodeResp		:= WORD#16#0000;
	wParameterCount	:= WORD#16#0000;
	wResult			:= WORD#16#0000;
	iIndexDst		:= 0;
	iIndexEnd		:= 0;
	iIndexSrc		:= 0;
	xError			:= FALSE;
	xErrorReqCode	:= FALSE;
	xReady			:= FALSE;
	xErrorResult	:= FALSE;
	wCode			:= WORD#16#0000;
	wDiagCode		:= WORD#16#0000;
	wAddDiagCode	:= WORD#16#0000;
	iDataLength		:= 0;


END_IF;
