		iActualPosition										:=	60;
	251..349:
		udtLumgateCurrentConsumption.arrActualOutput[7]		:=	True; (* 3,0 *)
		iActualPosition										:=	90;
	351..449:
		udtLumgateCurrentConsumption.arrActualOutput[9]		:=	True; (* 4,0 *)
		iActualPosition										:=	120;
	451..549:
