			arrLumgateConfig[iLoop].udtCloMaintenance		:=	arrLumgates[iLoop].udtCloMaintenance;
			arrLumgateConfig[iLoop].udtCurrentCheck			:=	arrLumgates[iLoop].udtCurrentCheck;
			arrLumgateConfig[iLoop].xFailMode				:=	arrLumgates[iLoop].xFailMode;
			arrLumgateConfig[iLoop].xFailsaveRelay1			:=	arrLumgates[iLoop].xFailsaveRelay1;
			arrLumgateConfig[iLoop].xFailsaveRelay2			:=	arrLumgates[iLoop].xFailsaveRelay2;
			arrLumgateConfig[iLoop].xRelay1_NC				:=	arrLumgates[iLoop].xRelay1_NC;
			arrLumgateConfig[iLoop].xRelay2_NC				:=	arrLumgates[iLoop].xRelay2_NC;


		End_for;
		xChangedLumgateFileExist			:=	True;
		xLumgateConfigFileExist				:=	False;


	1005:
		udtFileHandler.iAmountOverwriteChar	:=	0;
		udtFileHandler.udiAmountData		:=	Int_To_Udint(iBufferPosition - 1);
		udtFileHandler.iTasktype			:=	0;
		udtFileHandler.strFileNameTarget	:=	'ATS_Threshold_1_Configuration_1.csv';
		udtFileHandler.xRequest				:=	True;
		iWriteLumgateConfig					:=	6;


		(* update compare dataset *)
		udtActThresholdConfig1				:=	udtLevelConfiguration_L20_1;
		arrActScenarioConfig_L20_1			:=	arrScenarioConfiguration_L20_1;
		xChangedThreshold1FileExist			:=	True;
		xThreshold1ConfigFileExist			:=	False;


	2005:
		udtFileHandler.iAmountOverwriteChar	:=	0;
		udtFileHandler.udiAmountData		:=	Int_To_Udint(iBufferPosition - 1);
		udtFileHandler.iTasktype			:=	0;
		udtFileHandler.strFileNameTarget	:=	'ATS_Threshold_2_Configuration_1.csv';
		udtFileHandler.xRequest				:=	True;
		iWriteLumgateConfig					:=	6;


		(* update compare dataset *)
		udtActThresholdConfig2				:=	udtLevelConfiguration_L20_2;
		arrActScenarioConfig_L20_2			:=	arrScenarioConfiguration_L20_2;
		xChangedThreshold2FileExist			:=	True;
		xThreshold2ConfigFileExist			:=	False;


	3005:
		udtFileHandler.iAmountOverwriteChar	:=	0;
		udtFileHandler.udiAmountData		:=	Int_To_Udint(iBufferPosition - 1);
		udtFileHandler.iTasktype			:=	0;
		udtFileHandler.strFileNameTarget	:=	'ATS_Threshold_3_Configuration_1.csv';
		udtFileHandler.xRequest				:=	True;
		iWriteLumgateConfig					:=	6;


		(* update compare dataset *)
		udtActThresholdConfig3				:=	udtLevelConfiguration_L20_3;
		arrActScenarioConfig_L20_3			:=	arrScenarioConfiguration_L20_3;
		xChangedThreshold3FileExist			:=	True;
		xThreshold3ConfigFileExist			:=	False;


	4005:
		udtFileHandler.iAmountOverwriteChar	:=	0;
		udtFileHandler.udiAmountData		:=	Int_To_Udint(iBufferPosition - 1);
		udtFileHandler.iTasktype			:=	0;
		udtFileHandler.strFileNameTarget	:=	'ATS_Threshold_4_Configuration_1.csv';
		udtFileHandler.xRequest				:=	True;
		iWriteLumgateConfig					:=	6;


		(* update compare dataset *)
		udtActThresholdConfig4				:=	udtLevelConfiguration_L20_4;
		arrActScenarioConfig_L20_4			:=	arrScenarioConfiguration_L20_4;
		xChangedThreshold4FileExist			:=	True;
		xThreshold4ConfigFileExist			:=	False;


	5005:
		udtFileHandler.iAmountOverwriteChar	:=	0;
		udtFileHandler.udiAmountData		:=	Int_To_Udint(iBufferPosition - 1);
		udtFileHandler.iTasktype			:=	0;
		udtFileHandler.strFileNameTarget	:=	'ATS_Network_Configuration_1.csv';
		strDiag								:=	'File "ATS_Network_Configuration_1.csv" was written';
		udtFileHandler.xRequest				:=	True;
		iWriteLumgateConfig					:=	6;


		(* update compare dataset *)
		udtActNetworkConfiguration			:=	udtNetworkConfiguration;
		xChangedNetFileExist				:=	True;
		xNetworkConfigFileExist				:=	False;


	6005:
		udtFileHandler.iAmountOverwriteChar	:=	0;
		udtFileHandler.udiAmountData		:=	Int_To_Udint(iBufferPosition - 1);
		udtFileHandler.iTasktype			:=	0;
		udtFileHandler.strFileNameTarget	:=	'ATS_L20_Configuration_1.csv';
		strDiag								:=	'File "ATS_L20_Configuration_1.csv" was written';
		udtFileHandler.xRequest				:=	True;
		iWriteLumgateConfig					:=	6;


		(* update compare dataset *)
		arrActL20Inputs						:=	arr_Inputs_L20;
		udtActL20Config						:=	udtL20Config;
		xChangedL20FileExist				:=	True;
		xL20ConfigFileExist					:=	False;


	6:
		If TON_Delay_Write.Q then
			iWriteLumgateConfig	:=	7;
		End_If;

	7:
		If not udtFileHandler.xRequest then
			For iLoop := 0 to 5000 do
				arrFBuffer[iLoop] := Byte#0;
			End_For;
		End_If;
		iBufferPosition	:=	0;


		If xPartLeft then (* Continue with the existing text *)


		(*	iPositionActuelText	:=	iPositionActuelText + 1;*)
			For iLoop := iPositionActuelText to iLenght do
				arrFBuffer[iBufferPosition]	:=	Int_To_Byte(Get_Char(strTempText,iLoop));
				iBufferPosition				:=	iBufferPosition + 1;
			End_For;
			iPositionActuelText	:=	0;
			xPartLeft			:=	False;
		End_If;


		If iTempPointer < 241 then
			iWriteLumgateConfig	:=	10;
		Else
			iTempPointer		:=	0;
			iWriteLumgateConfig	:=	11;
			strDiag				:=	'File "ATS_Lumgate_Configuration_1.csv" was written';
			xTakeActualConfig	:=	False;
		End_If;
		If iConfigType > 4 then
			iTempPointer		:=	0;
			iWriteLumgateConfig	:=	11;
			xTakeActualConfig	:=	False;
		End_if;

	10:
		arrFBuffer[iBufferPosition]	:=	Byte#16#D;
		iBufferPosition				:=	iBufferPosition + 1;
		arrFBuffer[iBufferPosition]	:=	Byte#16#A;
		iBufferPosition				:=	iBufferPosition + 1;


		If iConfigType = 0 then
			iTempPointer	:=	iTempPointer + 1;
			If iTempPointer < 241 then


				If arrLumgates[iTempPointer].xInConfigFile and not xTakeActualConfig then
					iWriteLumgateConfig		:=	2;
				Elsif arrLumgates[iTempPointer].xAvailable and xTakeActualConfig then
					arrLumgates[iTempPointer].xInConfigFile	:=	True;
					iWriteLumgateConfig		:=	2;
				Elsif iTempPointer = 240 then
					If iBufferPosition = 2 then (* No extra data anymore *)
						iWriteLumgateConfig	:=	11;
						strDiag				:=	'File "ATS_Lumgate_Configuration_1.csv" was written';
						xTakeActualConfig	:=	False;
					Else
						iWriteLumgateConfig	:=	5;
					End_If;
				Else
					iBufferPosition									:=	iBufferPosition - 2;
					arrLumgates[iTempPointer].iModuleLevel			:=	0;
					arrLumgates[iTempPointer].iModuleNumberGeneral	:=	0;
					arrLumgates[iTempPointer].iModuleNumber			:=	0;
					arrLumgates[iTempPointer].xAvailable			:=	False;
					arrLumgates[iTempPointer].xInConfigFile			:=	False;
				End_If;
			Else
				If iBufferPosition = 2 then (* No extra data anymore *)
					iWriteLumgateConfig	:=	11;
					strDiag				:=	'File "ATS_Lumgate_Configuration_1.csv" was written';
					xTakeActualConfig	:=	False;
				Else
					iWriteLumgateConfig	:=	5;
				End_If;
			End_If;
		Elsif iConfigType = 1 then
			iTempPointer	:=	iTempPointer + 1;
			If iTempPointer < 26 then
				iWriteLumgateConfig	:=	1002;
			Else
				If iBufferPosition = 2 then (* No extra data anymore *)
					strDiag				:=	'File "ATS_Threshold_1_Configuration_1.csv" was written';
					iWriteLumgateConfig	:=	11;
				Else
					iWriteLumgateConfig	:=	1005;
				End_If;
			End_If;
		Elsif iConfigType = 2 then
			iTempPointer	:=	iTempPointer + 1;
			If iTempPointer < 26 then
				iWriteLumgateConfig	:=	2002;
			Else
				If iBufferPosition = 2 then (* No extra data anymore *)
					strDiag				:=	'File "ATS_Threshold_2_Configuration_1.csv" was written';
					iWriteLumgateConfig	:=	11;
				Else
					iWriteLumgateConfig	:=	2005;
				End_If;
			End_If;
		Elsif iConfigType = 3 then
			iTempPointer	:=	iTempPointer + 1;
			If iTempPointer < 26 then
				iWriteLumgateConfig	:=	3002;
			Else
				If iBufferPosition = 2 then (* No extra data anymore *)
					strDiag				:=	'File "ATS_Threshold_3_Configuration_1.csv" was written';
					iWriteLumgateConfig	:=	11;
				Else
					iWriteLumgateConfig	:=	3005;
				End_If;
			End_If;
		Elsif iConfigType = 4 then
			iTempPointer	:=	iTempPointer + 1;
			If iTempPointer < 26 then
				iWriteLumgateConfig	:=	4002;
			Else
				If iBufferPosition = 2 then (* No extra data anymore *)
					strDiag				:=	'File "ATS_Threshold_4_Configuration_1.csv" was written';
					iWriteLumgateConfig	:=	11;
				Else
					iWriteLumgateConfig	:=	4005;
				End_If;
			End_If;
		Elsif iConfigType = 5 then
			iWriteLumgateConfig	:=	5005;
		Elsif iConfigType = 6 then
			iWriteLumgateConfig	:=	6005;
		End_if;


	11:	xRequestWritingLumgateConfig	:=	False;
		iWriteLumgateConfig	:=	0;
End_Case;

(* FB_FileHandling / FileManager *)
Case iFileHandling Of


0:
	If udtFileHandler.xRequest Then
		Case udtFileHandler.iTasktype Of
			0:	iFileHandling	:=	 20; (* Write data from ram to file *)
			2:	iFileHandling	:=	 60; (* Remove file *)
			3:	iFileHandling	:=	 80; (* Read data from file to ram *)
		End_Case;
	End_If;


20:
	xFoExecute		:=	 True; 
	strFoName			:=	 udtFileHandler.strFileNameTarget;
	iFileHandling	:=	 21;


21:
	If xFoDone Then
		uiFileHandleTarget	:=	 uiFoHandle;
		xFoExecute		:=	 False;
		xFsExecute		:=	 True;
		uiFsHandle		:=	 uiFileHandleTarget;
		diFsPosition	:=	 Int_To_Dint(Neg(udtFileHandler.iAmountOverwriteChar));
		(*If udtFileHandler.iFileNumber <> 0 & udtFileHandler.iFileNumber < 81 Then
			iTempNumber	:=	 udtFileHandler.iFileNumber;
			strFileMem.ArrFileInfo[iTempNumber].udiEndPointer	:=	 strFileMem.ArrFileInfo[iTempNumber].udiEndPointer - Int_To_Udint(udtFileHandler.iAmountOverwriteChar);
		End_If;*)
		uiFsMode		:=	 Uint#2;
		iFileHandling	:=	 22;
	End_If;


22:
	If xFsDone Then
		xFsExecute		:=	 False;
		xFwExecute		:=	 True;
		uiFwHandle		:=	 uiFileHandleTarget;
		udFwLength		:=	 udtFileHandler.udiAmountData;
	(*	If udtFileHandler.iFileNumber <> 0 & udtFileHandler.iFileNumber < 81 Then
			iTempNumber	:=	 udtFileHandler.iFileNumber;
			strFileMem.ArrFileInfo[iTempNumber].udiEndPointer	:=	 strFileMem.ArrFileInfo[iTempNumber].udiEndPointer + udFwLength;
		End_If;*)
		iFileHandling	:=	 23;
	End_If;


23:
	If xFwDone Then
		xFwExecute		:=	 False;
		xFcExecute		:=	 True;
		uiFcHandle		:=	 uiFileHandleTarget;
		iFileHandling	:=	 24;
	End_If;


24:	
	If xFcDone Then
		xFcExecute		:=	 False;
		iFileHandling	:=	 100;
	End_If;


60:	
	xFrExecute		:=	 True;
	strFrName			:=	 udtFileHandler.strFileNameTarget;
	iFileHandling	:=	 61;


61:	
	If xFrDone or xFrError Then
		xFrExecute		:=	 False;
		iFileHandling	:=	 100;
	End_If;


80:	
	xFoExecute		:=	 True;
	strFoName			:=	 udtFileHandler.strFileNameSource;
	iFileHandling	:=	 81;


81:	
	If xFoDone Then
		uiFileHandleSource	:=	 uiFoHandle;
		xFoExecute			:=	 False;
		xFsExecute			:=	 True;
		uiFsHandle			:=	 uiFileHandleSource;
		diFsPosition		:=	 Udint_To_Dint(udtFileHandler.udiReadPosition);
		uiFsMode			:=	 Uint#0;
		iFileHandling		:=	 82;
	End_If;


82:	
	If xFsDone Then
		xFsExecute		:=	 False;
		xFreExecute		:=	 True;
		uiFreHandle		:=	 uiFileHandleSource;
		udFreMaxLength	:=	 Udint#5001;
		iFileHandling	:=	 83;
	End_If;


83:	
	If xFreDone Then
		xFreExecute		:=	 False;
		xFcExecute		:=	 True;
		udtFileHandler.udiReadPosition	:=	 udtFileHandler.udiReadPosition + udFreLengthRead;
		udiTempAmount	:=	 udFreLengthRead;
		uiFcHandle		:=	 uiFileHandleSource;
		iFileHandling	:=	 84;
	End_If;


84:	
	If xFcDone Then
		xFcExecute			:=	 False;
		xWaitFileHandling	:=	 True;
		iFileHandling		:=	 85;
	End_If;	


85:	
	If Not xWaitFileHandling Then
			udtFileHandler.xRequest	:=	 False;
		iFileHandling				:=	 0;
	End_If;


100:
	udtFileHandler.iAmountOverwriteChar	:=	 0;
	udtFileHandler.iFileNumber			:=	 0;
	udtFileHandler.iTasktype			:=	 0;
	udtFileHandler.strFileNameSource	:=	 '';
	udtFileHandler.strFileNameTarget	:=	 '';
	udtFileHandler.udiAmountData		:=	 Udint#0;
	udtFileHandler.udiReadPosition		:=	 Udint#0;
	udtFileHandler.xMultipleRun			:=	 False;
	udtFileHandler.xRequest				:=	 False;
	udtFileHandler.xFileMemorySource	:=	 False;
	iFileHandling						:=	 0;
End_Case;

(* FB_FileHandling / ServiceBlocks *)
(* File Open *)


File_Open_Flash(Execute	:=	xFoExecute,
Name					:=	strFoName);


xFoDone					:=	File_Open_Flash.Done;
uiFoHandle				:=	File_Open_Flash.Handle;
xFoError				:=	File_Open_Flash.Error;
uiFoErrorId				:=	File_Open_Flash.ErrorID;


(************************************************************************)
(* File Seek *)


File_Seek_Flash(Execute	:= xFsExecute,
Handle					:= uiFsHandle,
Position				:= diFsPosition,
Mode					:= uiFsMode);


xFsDone					:=	File_Seek_Flash.Done;
xFsError				:=	File_Seek_Flash.Error;
uiFsErrorId				:=	File_Seek_Flash.ErrorID;


(************************************************************************)
(* File Write *)


File_Write_Flash(Execute:= xFwExecute,
Handle					:= uiFwHandle,
Buffer					:= arrFBuffer,
Length					:= udFwLength);


xFwDone					:=	File_Write_Flash.Done;
udFwLengthWritten		:=	File_Write_Flash.LengthWritten;
arrFBuffer				:=	File_Write_Flash.Buffer;
xFwError				:=	File_Write_Flash.Error;
uiFwErrorId				:=	File_Write_Flash.ErrorID;


(************************************************************************)
(* File Read *)


File_Read_Flash(Execute	:= xFreExecute,
Handle					:= uiFreHandle,
Buffer					:= arrFBuffer,
MaxLength				:= udFreMaxLength);


xFreDone				:=	File_Read_Flash.Done;
udFreLengthRead			:=	File_Read_Flash.LengthRead;
arrFBuffer				:=	File_Read_Flash.Buffer;
xFreError				:=	File_Read_Flash.Error;
uiFreErrorId			:=	File_Read_Flash.ErrorID;


(************************************************************************)
(* File Close *)


File_Close_Flash(Execute:= xFcExecute,
Handle					:= uiFcHandle);


xFcDone					:=	File_Close_Flash.Done;
xFcError				:=	File_Close_Flash.Error;
uiFcErrorId				:=	File_Close_Flash.ErrorID;


(************************************************************************)
(* File Remove *)


File_Remove_Flash(Execute:= xFrExecute,
Name					:= strFrName);


xFrDone					:=	File_Remove_Flash.Done;
xFrError				:=	File_Remove_Flash.Error;
uiFrErrorId				:=	File_Remove_Flash.ErrorID;


(************************************************************************)
(* Buf To String *)


TranslateData(REQ		:= xTranslateRequest,
BUF_FORMAT				:= False,
BUF_OFFS				:= diTranslateOffset,
BUF_CNT					:= diTranslateCount,
BUFFER					:= arrFBuffer,
DST						:= strLineData);


xTranslateDone			:=	TranslateData.DONE;
xTranslateError			:=	TranslateData.ERROR;
iTranslateStatus		:=	TranslateData.STATUS;
arrFBuffer				:=	TranslateData.BUFFER;
strLineData				:=	TranslateData.DST;

(* InterbusStartup *)
R_TRIG_Interbus(CLK		:=	xInterbusRun);
F_TRIG_InterbusRun(CLK	:=	MASTER_DIAG_STATUS_REG_RUN);


If xActivate then
	If not xInterbusRun then
		strDiag		:=	'Warning: System is not ready to work';
		xPreAlarm	:=	True;
	End_if;
	Case iIB_Startup of


		0:	(* Activate Interbus Startup FB *)
			xActivateStartup		:=	True;
			iIB_Startup				:=	1;
			xError					:=	False;
			strDiag					:=	'Start up Interbus';


		1:	
			If xActiveStartup and xReadyStartup then	(* Startup FB is ready *)
				xInitNewConfig		:=	True;
				iIB_Startup			:=	2;


			Elsif xErrorStartup or TON_Timeout.Q then
				iIB_Startup			:=	999;
			End_If;


		2:	(* Interbus device detection complete *)
			If xDoneStartup then 
				xActivateProcessData	:=	True; 		(* IBS Process data FB *)
				xActivateOutputData		:=	False;		(* Set Output data to 2 V per Lumgate *)
				xTeach					:=	True;
				xActive					:=	True;
				iIB_Startup				:=	3;
			Elsif xErrorStartup then
				strDiag					:=	'Interbus Error';
				xActivateStartup		:=	False;
				xInitNewConfig			:=	False;
				iIB_Startup				:=	999;
			End_If;


		3:
			If TON_IBS_Startup.Q then
				xActivateOutputData		:=	True;
				strDiag					:=	'System startup complete';
				xPreAlarm				:=	False;
				iIB_Startup				:=	4;
			End_if;


		4:	(* Target step. Leave only in case of failure or rescan command *)
			If not MASTER_DIAG_STATUS_REG_RUN then		(* Rescan after a failure occurs *)
				xActive					:=	False;
				xActivateOutputData		:=	True;
				iIB_Startup				:= 	5;
			Elsif xManualTrig then						(* Manual Rescan *)
				xActive					:=	False;
				xActivateOutputData		:=	True;
				iIB_Startup				:= 	7;
			End_If;


		5:	(* Restart after failure *)
			xActivateStartup		:= 	False;
			xInitNewConfig			:= 	False;
			xActivateProcessData	:= 	False;


			For iLoop:= 1 to 256 do	(* Reset Interbus device structure *)
				If iLoop < 241 then
					arrLumgates[iLoop].iModuleNumberGeneral		:=	0;
				End_if;
				arrIB_Configuration[iLoop].xDeviceCopyed		:= 	False;
				arrIB_Configuration[iLoop].iPdLenCompl			:= 	0;
				arrIB_Configuration[iLoop].iPdLenIn				:= 	0;
				arrIB_Configuration[iLoop].iPdLenOut			:= 	0;
				arrIB_Configuration[iLoop].iPntrPdBeginIn		:= 	0;
				arrIB_Configuration[iLoop].iPntrPdBeginOut		:= 	0;
				arrIB_Configuration[iLoop].iReservedBytesIn		:= 	0;
				arrIB_Configuration[iLoop].iReservedBytesOut	:= 	0;
				arrIB_Configuration[iLoop].iCntPdd				:= 	0;
				arrIB_Configuration[iLoop].bModuleID			:= 	Byte#0;
			End_For;
			iIB_Startup	:= 	55;


		55:	
			If F_DiagnosticRoutine.Q then		(* Wait for the end of the IBS diagnostic routine *)
				xStartTimer			:=	True;	(* Start delay timer *)
				xManualTrig			:= 	False;
				iIB_Startup			:= 	6;
			End_If;


		6:	
			If TON_DelayTest.Q then
				xStartTimer			:= 	False;	(* Restart IBS Startup *)
				xActivateStartup	:= 	True;
				iIB_Startup			:= 	1;
			End_If;


		7:	
			xManualTrig				:=	False;	(* Start delay timer *)
			xStartTimer				:= 	True;
			iIB_Startup				:= 	8;


		8:	
			If TON_DelayTest.Q then
				xStartTimer			:= 	False;
				iIB_Startup			:= 	9;		(* Reset IBS Structure *)
			End_If;

		9:	
			xActivateStartup		:= 	False;
			xInitNewConfig			:= 	False;
			xActivateProcessData	:= 	False;


			For iLoop := 1 to 256 do
				If iLoop < 241 then
					arrLumgates[iLoop].iModuleNumberGeneral		:=	0;
				End_if;
				arrIB_Configuration[iLoop].xDeviceCopyed		:= 	False;
				arrIB_Configuration[iLoop].iPdLenCompl			:= 	0;
				arrIB_Configuration[iLoop].iPdLenIn				:= 	0;
				arrIB_Configuration[iLoop].iPdLenOut			:= 	0;
				arrIB_Configuration[iLoop].iPntrPdBeginIn		:= 	0;
				arrIB_Configuration[iLoop].iPntrPdBeginOut		:= 	0;
				arrIB_Configuration[iLoop].iReservedBytesIn		:= 	0;
				arrIB_Configuration[iLoop].iReservedBytesOut	:= 	0;
				arrIB_Configuration[iLoop].iCntPdd				:= 	0;
				arrIB_Configuration[iLoop].bModuleID			:= 	Byte#0;

			End_For;


			xStartTimer				:= 	True;
			xManualTrig				:= 	False;
			iIB_Startup				:= 	6;


		999:
			xActivateProcessData	:= 	False;
			xInitNewConfig			:=	False;
			xActivateStartup		:=	False;
			xError					:=	True;
			iIB_Startup				:= 	0;
	End_case;


Else
	xActivateOutputData		:=	False;
	xActivateProcessData	:= 	False;
	xInitNewConfig			:=	False;
	xActivateStartup		:=	False;
	iIB_Startup				:=	0;
	xError					:=	False;
	xActive					:=	False;
End_if;


F_DiagnosticRoutine(CLK	:=	MASTER_DIAG_STATUS_REG_DTC);
TON_DelayTest(IN		:=	xStartTimer,	PT	:=	T#1s);
TON_Timeout(IN			:=	iIB_Startup = 2,PT	:=	t#2m);
TON_IBS_Startup(IN		:=	iIB_Startup = 3,PT	:=	t#5s);

(* InterbusStartup *)
xDone				:=	False;
Case iTeach of


	0:	
		If xTeach then
			iLoop3	:= 	0;
			For iLoop := 1 to 240 do	(* Reset availability *)
				arrLumgates[iLoop].xAvailable	:=	False;
			End_For;
			iTeach	:= 	5;
		End_If;


	1: (* not used *)
		For iLoop := 1 to 4 do
			iNumberLumgate	:= 	0;
			wTemp			:= 	int_to_word(iLoop - 1);


			For iActualIB_Device := 1 to 256 do
				(* Detect and sort Lumgates *)
				If arrIB_Configuration[iActualIB_Device].wModuleLevel = wTemp and arrIB_Configuration[iActualIB_Device].bModuleID = Byte#16#33 then
					iNumberLumgate								:=	iNumberLumgate + 1;
					iLoop3										:= 	iLoop3 + 1;
					arrLumgates[iLoop3].iModuleLevel			:= 	iLoop - 1;
					arrLumgates[iLoop3].iModuleNumberGeneral	:= 	iActualIB_Device;
					arrLumgates[iLoop3].iModuleNumber			:=	iNumberLumgate;
					arrLumgates[iLoop3].xAvailable				:=	True;
				End_If;
			End_For;
		End_For;


		iNumberLumgate	:= 	0;
		iTeach			:= 	17;	(* Check for invalid bus devices *)
		xTeach			:= 	False;
		xDone			:=	True;
		xIB_ConfigSaved	:=	True;


	5:
		iNumberLumgate	:= 	0;
		iNumberLumgate1	:=	0;


		For iActualIB_Device := 1 to 90 do	(* Search for Lumgates *)
			If arrIB_Configuration[iActualIB_Device].wModuleLevel = Word#0 and arrIB_Configuration[iActualIB_Device].bModuleID = Byte#16#33 then
				iNumberLumgate	:= 	iNumberLumgate + 1;
				For iLoop2 := 1 to 240 do	(* Search for the fitting configuration *)
					If arrLumgates[iLoop2].iModuleLevel = 0 and arrLumgates[iLoop2].iModuleNumber = iNumberLumgate then
						iNumberLumgate1								:= 	iNumberLumgate1 + 1;
						arrLumgates[iLoop2].iModuleNumberGeneral	:= 	iActualIB_Device;
						arrLumgates[iLoop2].xAvailable				:= 	True;


						arrIB_Configuration[iActualIB_Device].xDeviceCopyed	:=	True;
						Exit;
					End_If;
				End_For;
			End_If;
		End_For;
		iTeach	:= 	6;


	6:
		For iActualIB_Device := 91 to 180 do	(* Split the searching process in a couple of parts to reduce the cycle time *)
			If arrIB_Configuration[iActualIB_Device].wModuleLevel = Word#0 and arrIB_Configuration[iActualIB_Device].bModuleID = Byte#16#33 then
				iNumberLumgate	:=	iNumberLumgate + 1;
				For iLoop2 := 1 to 240 do
					If arrLumgates[iLoop2].iModuleLevel = 0 and arrLumgates[iLoop2].iModuleNumber = iNumberLumgate then
						iNumberLumgate1								:= 	iNumberLumgate1 + 1;
						arrLumgates[iLoop2].iModuleNumberGeneral	:=	iActualIB_Device;
						arrLumgates[iLoop2].xAvailable				:= 	True;


						arrIB_Configuration[iActualIB_Device].xDeviceCopyed	:=	True;
						Exit;
					End_If;
				End_For;
			End_If;
		End_For;
		iTeach	:= 	7;

	7:
		For iActualIB_Device	:= 181 to 256 do
			If arrIB_Configuration[iActualIB_Device].wModuleLevel = Word#0 and arrIB_Configuration[iActualIB_Device].bModuleID = Byte#16#33 then
				iNumberLumgate	:=	iNumberLumgate + 1;
				For iLoop2:= 1 to 240 do
					If arrLumgates[iLoop2].iModuleLevel = 0 and arrLumgates[iLoop2].iModuleNumber = iNumberLumgate then
						iNumberLumgate1								:= 	iNumberLumgate1 + 1;
						arrLumgates[iLoop2].iModuleNumberGeneral	:=	iActualIB_Device;
						arrLumgates[iLoop2].xAvailable				:= 	True;


						arrIB_Configuration[iActualIB_Device].xDeviceCopyed	:=	True;
						Exit;
					End_If;
				End_For;
			End_If;
		End_For;
		iTeach	:= 	8;


	8:
		iNumberLumgate	:= 	0;
		iNumberLumgate2	:=	0;
		For iActualIB_Device := 1 to 90 do
			If arrIB_Configuration[iActualIB_Device].wModuleLevel = word#1 and arrIB_Configuration[iActualIB_Device].bModuleID = Byte#16#33 then
				iNumberLumgate	:=	 iNumberLumgate + 1;
				For iLoop2 := 1 to 240 do
					If arrLumgates[iLoop2].iModuleLevel = 1 and arrLumgates[iLoop2].iModuleNumber = iNumberLumgate then
						iNumberLumgate2								:= 	iNumberLumgate2 + 1;
						arrLumgates[iLoop2].iModuleNumberGeneral	:= 	iActualIB_Device;
						arrLumgates[iLoop2].xAvailable				:=	True;


						arrIB_Configuration[iActualIB_Device].xDeviceCopyed	:=	True;
						Exit;
					End_If;
				End_For;
			End_If;
		End_For;
		iTeach	:= 	9;


	9:
		For iActualIB_Device := 91 to 180 do
			If arrIB_Configuration[iActualIB_Device].wModuleLevel = word#1 and arrIB_Configuration[iActualIB_Device].bModuleID = Byte#16#33 then
				iNumberLumgate	:= 	iNumberLumgate + 1;
				For iLoop2 := 1 to 240 do
					If arrLumgates[iLoop2].iModuleLevel = 1 and arrLumgates[iLoop2].iModuleNumber = iNumberLumgate then
						iNumberLumgate2								:= 	iNumberLumgate2 + 1;
						arrLumgates[iLoop2].iModuleNumberGeneral	:= 	iActualIB_Device;
						arrLumgates[iLoop2].xAvailable				:= 	True;


						arrIB_Configuration[iActualIB_Device].xDeviceCopyed	:=	True;
						Exit;
					End_If;
				End_For;
			End_If;
		End_For;
		iTeach	:= 	10;

	10:
		For iActualIB_Device := 181 to 256 do
			If arrIB_Configuration[iActualIB_Device].wModuleLevel = Word#1 and arrIB_Configuration[iActualIB_Device].bModuleID = Byte#16#33 then
				iNumberLumgate	:= 	iNumberLumgate + 1;
				For iLoop2 := 1 to 240 do
					If arrLumgates[iLoop2].iModuleLevel = 1 and arrLumgates[iLoop2].iModuleNumber = iNumberLumgate then
						iNumberLumgate2								:= 	iNumberLumgate2 + 1;
						arrLumgates[iLoop2].iModuleNumberGeneral	:=	iActualIB_Device;
						arrLumgates[iLoop2].xAvailable				:= 	True;


						arrIB_Configuration[iActualIB_Device].xDeviceCopyed	:=	True;
						Exit;
					End_If;
				End_For;
			End_If;
		End_For;
		iTeach	:= 	11;


	11:
		iNumberLumgate	:= 	0;
		iNumberLumgate3	:=	0;
		For iActualIB_Device := 1 to 90 do
			If arrIB_Configuration[iActualIB_Device].wModuleLevel = Word#2 and arrIB_Configuration[iActualIB_Device].bModuleID = Byte#16#33 then
				iNumberLumgate	:= 	iNumberLumgate + 1;
				For iLoop2 := 1 to 240 do
					If arrLumgates[iLoop2].iModuleLevel = 2 and arrLumgates[iLoop2].iModuleNumber = iNumberLumgate then
						iNumberLumgate3								:= 	iNumberLumgate3 + 1;
						arrLumgates[iLoop2].iModuleNumberGeneral	:= 	iActualIB_Device;
						arrLumgates[iLoop2].xAvailable				:= 	True;


						arrIB_Configuration[iActualIB_Device].xDeviceCopyed	:=	True;
						Exit;
					End_If;
				End_For;
			End_If;
		End_For;
		iTeach	:= 	12;


	12:
		For iActualIB_Device := 91 to 180 do
			If arrIB_Configuration[iActualIB_Device].wModuleLevel = Word#2 and arrIB_Configuration[iActualIB_Device].bModuleID = Byte#16#33 then
				iNumberLumgate	:= 	iNumberLumgate + 1;
				For iLoop2 := 1 to 240 do
					If arrLumgates[iLoop2].iModuleLevel = 2 and arrLumgates[iLoop2].iModuleNumber = iNumberLumgate then
						iNumberLumgate3								:= 	iNumberLumgate3 + 1;
						arrLumgates[iLoop2].iModuleNumberGeneral	:=	iActualIB_Device;
						arrLumgates[iLoop2].xAvailable				:= 	True;


						arrIB_Configuration[iActualIB_Device].xDeviceCopyed	:=	True;
						Exit;
					End_If;
				End_For;
			End_If;
		End_For;
		iTeach	:= 	13;

	13:
		For iActualIB_Device := 181 to 256 do
			If arrIB_Configuration[iActualIB_Device].wModuleLevel = Word#2 and arrIB_Configuration[iActualIB_Device].bModuleID = Byte#16#33 then
				iNumberLumgate	:=	iNumberLumgate + 1;
				For iLoop2 := 1 to 240 do
					If arrLumgates[iLoop2].iModuleLevel = 2 and arrLumgates[iLoop2].iModuleNumber = iNumberLumgate then
						iNumberLumgate3								:= 	iNumberLumgate3 + 1;
						arrLumgates[iLoop2].iModuleNumberGeneral	:= 	iActualIB_Device;
						arrLumgates[iLoop2].xAvailable				:= 	True;


						arrIB_Configuration[iActualIB_Device].xDeviceCopyed	:=	True;
						Exit;	
					End_If;
				End_For;
			End_If;
		End_For;
		iTeach	:= 	14;


	14:
		iNumberLumgate	:=	0;
		iNumberLumgate4	:=	0;
		For iActualIB_Device := 1 to 90 do
			If arrIB_Configuration[iActualIB_Device].wModuleLevel = Word#3 and arrIB_Configuration[iActualIB_Device].bModuleID = Byte#16#33 then
				iNumberLumgate	:=	iNumberLumgate + 1;
				For iLoop2 := 1 to 240 do
					If arrLumgates[iLoop2].iModuleLevel = 3 and arrLumgates[iLoop2].iModuleNumber = iNumberLumgate then
						iNumberLumgate4								:= 	iNumberLumgate4 + 1;
						arrLumgates[iLoop2].iModuleNumberGeneral	:= 	iActualIB_Device;
						arrLumgates[iLoop2].xAvailable				:= 	True;


						arrIB_Configuration[iActualIB_Device].xDeviceCopyed	:=	True;
						Exit;
					End_If;
				End_For;
			End_If;
		End_For;
		iTeach	:=	 15;


	15:
		For iActualIB_Device := 91 to 180 do
			If arrIB_Configuration[iActualIB_Device].wModuleLevel = Word#3 and arrIB_Configuration[iActualIB_Device].bModuleID = Byte#16#33 then
				iNumberLumgate	:= 	iNumberLumgate + 1;
				For iLoop2 := 1 to 240 do
					If arrLumgates[iLoop2].iModuleLevel = 3 and arrLumgates[iLoop2].iModuleNumber = iNumberLumgate then
						iNumberLumgate4								:= 	iNumberLumgate4 + 1;
						arrLumgates[iLoop2].iModuleNumberGeneral	:= 	iActualIB_Device;
						arrLumgates[iLoop2].xAvailable				:= 	True;


						arrIB_Configuration[iActualIB_Device].xDeviceCopyed	:=	True;
						Exit;
					End_If;
				End_For;
			End_If;
		End_For;
		iTeach	:= 	16;

	16:
		For iActualIB_Device := 181 to 256 do
			If arrIB_Configuration[iActualIB_Device].wModuleLevel = Word#3 and arrIB_Configuration[iActualIB_Device].bModuleID = Byte#16#33 then
				iNumberLumgate	:= 	iNumberLumgate + 1;
				For iLoop2 := 1 to 240 do
					If arrLumgates[iLoop2].iModuleLevel = 3 and arrLumgates[iLoop2].iModuleNumber = iNumberLumgate then
						iNumberLumgate4								:= 	iNumberLumgate4 + 1;
						arrLumgates[iLoop2].iModuleNumberGeneral	:=	iActualIB_Device;
						arrLumgates[iLoop2].xAvailable				:=	 True;


						arrIB_Configuration[iActualIB_Device].xDeviceCopyed	:=	True;
						Exit;
					End_If;
				End_For;
			End_If;
		End_For;
		iTeach				:= 	161;


	161:
		(* Search for Lumgates without a configuration *)
		For iActualIB_Device := 1 to 65 do
			If not arrIB_Configuration[iActualIB_Device].xDeviceCopyed and arrIB_Configuration[iActualIB_Device].bModuleID = Byte#16#33 then
				For iLoop2 := 1 to 240 do	(* Add all not configured Lumgates at the end of the structure *)
					If not arrLumgates[iLoop2].xAvailable and not arrLumgates[iLoop2].xInConfigFile then
						arrLumgates[iLoop2].iModuleNumberGeneral	:=	iActualIB_Device;
						arrLumgates[iLoop2].xAvailable				:=	True;
						arrLumgates[iLoop2].iModuleLevel			:= 	word_to_int(arrIB_Configuration[iActualIB_Device].wModuleLevel);


						(* Enter a basic configuration *)
						arrLumgates[iLoop2].iActualOutputLevel		:=	222;
						arrLumgates[iLoop2].iOutputLevelFailsave	:=	222;
						arrLumgates[iLoop2].iTargetOutputLevel		:=	222;
						arrLumgates[iLoop2].strDescription			:=	'Dev. not configured';
						arrLumgates[iLoop2].xFailMode				:=	True;


						If arrLumgates[iLoop2].iModuleLevel = 0 then
							iNumberLumgate1	:=	iNumberLumgate1 + 1;
							arrLumgates[iLoop2].iModuleNumber			:=	iNumberLumgate1;
						Elsif arrLumgates[iLoop2].iModuleLevel = 1 then
							iNumberLumgate2	:=	iNumberLumgate2 + 1;
							arrLumgates[iLoop2].iModuleNumber			:=	iNumberLumgate2;
						Elsif arrLumgates[iLoop2].iModuleLevel = 2 then
							iNumberLumgate3	:=	iNumberLumgate3 + 1;
							arrLumgates[iLoop2].iModuleNumber			:=	iNumberLumgate3;
						Elsif arrLumgates[iLoop2].iModuleLevel = 3 then
							iNumberLumgate4	:=	iNumberLumgate4 + 1;
							arrLumgates[iLoop2].iModuleNumber			:=	iNumberLumgate4;
						End_if;


						arrIB_Configuration[iActualIB_Device].xDeviceCopyed	:=	True;
						Exit;
					End_If;
				End_For;
			End_If;
		End_For;


		iTeach				:= 	162;


	162:
		(* Search for Lumgates without a configuration *)
		For iActualIB_Device := 66 to 130 do
			If not arrIB_Configuration[iActualIB_Device].xDeviceCopyed and arrIB_Configuration[iActualIB_Device].bModuleID = Byte#16#33 then
				For iLoop2 := 1 to 240 do	(* Add all not configured Lumgates at the end of the structure *)
					If not arrLumgates[iLoop2].xAvailable and not arrLumgates[iLoop2].xInConfigFile then
						arrLumgates[iLoop2].iModuleNumberGeneral	:=	iActualIB_Device;
						arrLumgates[iLoop2].xAvailable				:=	True;
						arrLumgates[iLoop2].iModuleLevel			:= 	word_to_int(arrIB_Configuration[iActualIB_Device].wModuleLevel);


						(* Enter a basic configuration *)
						arrLumgates[iLoop2].iActualOutputLevel		:=	222;
						arrLumgates[iLoop2].iOutputLevelFailsave	:=	222;
						arrLumgates[iLoop2].iTargetOutputLevel		:=	222;
						arrLumgates[iLoop2].strDescription			:=	'Dev. not configured';
						arrLumgates[iLoop2].xFailMode				:=	True;


						If arrLumgates[iLoop2].iModuleLevel = 0 then
							iNumberLumgate1	:=	iNumberLumgate1 + 1;
							arrLumgates[iLoop2].iModuleNumber			:=	iNumberLumgate1;
						Elsif arrLumgates[iLoop2].iModuleLevel = 1 then
							iNumberLumgate2	:=	iNumberLumgate2 + 1;
							arrLumgates[iLoop2].iModuleNumber			:=	iNumberLumgate2;
						Elsif arrLumgates[iLoop2].iModuleLevel = 2 then
							iNumberLumgate3	:=	iNumberLumgate3 + 1;
							arrLumgates[iLoop2].iModuleNumber			:=	iNumberLumgate3;
						Elsif arrLumgates[iLoop2].iModuleLevel = 3 then
							iNumberLumgate4	:=	iNumberLumgate4 + 1;
							arrLumgates[iLoop2].iModuleNumber			:=	iNumberLumgate4;
						End_if;


						arrIB_Configuration[iActualIB_Device].xDeviceCopyed	:=	True;
						Exit;
					End_If;
				End_For;
			End_If;
		End_For;


		iTeach				:= 	163;
	163:
		(* Search for Lumgates without a configuration *)
		For iActualIB_Device := 131 to 195 do
			If not arrIB_Configuration[iActualIB_Device].xDeviceCopyed and arrIB_Configuration[iActualIB_Device].bModuleID = Byte#16#33 then
				For iLoop2 := 1 to 240 do	(* Add all not configured Lumgates at the end of the structure *)
					If not arrLumgates[iLoop2].xAvailable and not arrLumgates[iLoop2].xInConfigFile then
						arrLumgates[iLoop2].iModuleNumberGeneral	:=	iActualIB_Device;
						arrLumgates[iLoop2].xAvailable				:=	True;
						arrLumgates[iLoop2].iModuleLevel			:= 	word_to_int(arrIB_Configuration[iActualIB_Device].wModuleLevel);


						(* Enter a basic configuration *)
						arrLumgates[iLoop2].iActualOutputLevel		:=	222;
						arrLumgates[iLoop2].iOutputLevelFailsave	:=	222;
						arrLumgates[iLoop2].iTargetOutputLevel		:=	222;
						arrLumgates[iLoop2].strDescription			:=	'Dev. not configured';
						arrLumgates[iLoop2].xFailMode				:=	True;


						If arrLumgates[iLoop2].iModuleLevel = 0 then
							iNumberLumgate1	:=	iNumberLumgate1 + 1;
							arrLumgates[iLoop2].iModuleNumber			:=	iNumberLumgate1;
						Elsif arrLumgates[iLoop2].iModuleLevel = 1 then
							iNumberLumgate2	:=	iNumberLumgate2 + 1;
							arrLumgates[iLoop2].iModuleNumber			:=	iNumberLumgate2;
						Elsif arrLumgates[iLoop2].iModuleLevel = 2 then
							iNumberLumgate3	:=	iNumberLumgate3 + 1;
							arrLumgates[iLoop2].iModuleNumber			:=	iNumberLumgate3;
						Elsif arrLumgates[iLoop2].iModuleLevel = 3 then
							iNumberLumgate4	:=	iNumberLumgate4 + 1;
							arrLumgates[iLoop2].iModuleNumber			:=	iNumberLumgate4;
						End_if;


						arrIB_Configuration[iActualIB_Device].xDeviceCopyed	:=	True;
						Exit;
					End_If;
				End_For;
			End_If;
		End_For;


		iTeach				:= 	164;
	164:
		(* Search for Lumgates without a configuration *)
		For iActualIB_Device := 196 to 256 do
			If not arrIB_Configuration[iActualIB_Device].xDeviceCopyed and arrIB_Configuration[iActualIB_Device].bModuleID = Byte#16#33 then
				For iLoop2 := 1 to 240 do	(* Add all not configured Lumgates at the end of the structure *)
					If not arrLumgates[iLoop2].xAvailable and not arrLumgates[iLoop2].xInConfigFile then
						arrLumgates[iLoop2].iModuleNumberGeneral	:=	iActualIB_Device;
						arrLumgates[iLoop2].xAvailable				:=	True;
						arrLumgates[iLoop2].iModuleLevel			:= 	word_to_int(arrIB_Configuration[iActualIB_Device].wModuleLevel);


						(* Enter a basic configuration *)
						arrLumgates[iLoop2].iActualOutputLevel		:=	222;
						arrLumgates[iLoop2].iOutputLevelFailsave	:=	222;
						arrLumgates[iLoop2].iTargetOutputLevel		:=	222;
						arrLumgates[iLoop2].strDescription			:=	'Dev. not configured';
						arrLumgates[iLoop2].xFailMode				:=	True;


						If arrLumgates[iLoop2].iModuleLevel = 0 then
							iNumberLumgate1	:=	iNumberLumgate1 + 1;
							arrLumgates[iLoop2].iModuleNumber			:=	iNumberLumgate1;
						Elsif arrLumgates[iLoop2].iModuleLevel = 1 then
							iNumberLumgate2	:=	iNumberLumgate2 + 1;
							arrLumgates[iLoop2].iModuleNumber			:=	iNumberLumgate2;
						Elsif arrLumgates[iLoop2].iModuleLevel = 2 then
							iNumberLumgate3	:=	iNumberLumgate3 + 1;
							arrLumgates[iLoop2].iModuleNumber			:=	iNumberLumgate3;
						Elsif arrLumgates[iLoop2].iModuleLevel = 3 then
							iNumberLumgate4	:=	iNumberLumgate4 + 1;
							arrLumgates[iLoop2].iModuleNumber			:=	iNumberLumgate4;
						End_if;


						arrIB_Configuration[iActualIB_Device].xDeviceCopyed	:=	True;
						Exit;
					End_If;
				End_For;
			End_If;
		End_For;


		iTeach				:= 	17;
		xTeach				:= 	False;
		xDone				:=	True;
		xIB_ConfigSaved		:=	True;
		xActivateOutputData	:=	True;


	17:	(* Check Interbus devices *)
		xDone			:=	True;
		iCountOfRBT		:=	0;
		iCountOfAI		:=	0;
		iCountOfLumgate	:=	0;
		iCountOfOther	:=	0;


		For iLoop := 1 to 256 do
			If arrIB_Configuration[iLoop].bModuleID = Byte#4 then (* RBT *)
				iCountOfRBT		:= iCountOfRBT + 1;
			End_If;


			If arrIB_Configuration[iLoop].bModuleID = Byte#16#7F then (* Analog input module *)
				iCountOfAI		:= iCountOfAI + 1;
			End_If;


			If arrIB_Configuration[iLoop].bModuleID = Byte#16#33 then (* Lumgate *)
				iCountOfLumgate	:= iCountOfLumgate + 1;
			End_If;


			If arrIB_Configuration[iLoop].bModuleID <> Byte#16#33 and arrIB_Configuration[iLoop].bModuleID <> Byte#16#4 
			and arrIB_Configuration[iLoop].bModuleID <> Byte#16#7F and arrIB_Configuration[iLoop].bModuleID <> Byte#0 then (* other devices *)
				iCountOfOther	:= iCountOfOther + 1;
			End_If;
		End_For;


		xError						:= False;
		iTotalAmountWithConfig		:= 0;


		For iLoop := 1 to 240 do
			If arrLumgates[iLoop].xInConfigFile and not arrLumgates[iLoop].xAvailable then
				xError				:= True;
			End_If;
			If arrLumgates[iLoop].xAvailable then
				iTotalAmountWithConfig	:= iTotalAmountWithConfig + 1;
			End_If;
		End_For;


		If iCountOfRBT > 4 then
			strDiag	:= 'Error: More than 4 RBT modules are found';
			xError	:= True;
		End_If;


		If iCountOfRBT < 4 then
			strDiag	:= 'Error: Less than 4 RBT modules are found';
			xError	:= True;
		End_If;


		If iCountOfAI = 0 then
			strDiag	:= 'Error: No analog input module found';
			xError	:= True;
		End_If;


		If iCountOfAI > 1 then
			strDiag	:= 'Error: More than 1 analog input module found';
			xError	:= True;
		End_If;


		If iCountOfLumgate > 240 then
			strDiag	:= 'Error: More than 240 Lumgates found';
			xError	:= True;
		End_If;


		If iCountOfOther > 0 then
			strDiag	:=	'Error: Invalid Interbus devices are found';
			xError	:=	True;
		End_if;


		iTeach	:=	0;
End_Case;

(* InterbusStartup *)
For iLoop := 1 to 240 do
	If arrLumgates[iLoop].xAvailable and xActivateOutputData then
		iModuleNumber							:=	arrLumgates[iLoop].iModuleNumberGeneral;
		arrIB_Configuration[iModuleNumber].arrPdOut[1].X6:= arrLumgates[iLoop].xFailMode;						(* 0 = Hold, 1 = Failsave values *)
		arrIB_Configuration[iModuleNumber].arrPdOut[1].X7:= arrLumgates[iLoop].xSafe; 							(* Rising Edge: Load failsave values *)


		arrLumgates[iLoop].xInput1				:= 	arrIB_Configuration[iModuleNumber].arrPdIn[1].X4; 			(* digital input 1 *)
		arrLumgates[iLoop].xInput2				:= 	arrIB_Configuration[iModuleNumber].arrPdIn[1].X5;			(* digital input 2 *)
		arrLumgates[iLoop].iActualOutputLevel	:= 	limit_int(0,arrLumgates[iLoop].iActualOutputLevel,1070); 	(* analog value *)


		wTempValueMeasurement					:=	word#0; 													(* measurement *)
		wTempValueMeasurement.B0				:=	arrIB_Configuration[iModuleNumber].arrPdIn[2];
		wTempValueMeasurement.X8				:=	arrIB_Configuration[iModuleNumber].arrPdIn[1].X0;
		wTempValueMeasurement.X9				:=	arrIB_Configuration[iModuleNumber].arrPdIn[1].X1;


		If xLumgateType then
			arrLumgates[iLoop].iMeasurement			:=	word_to_int(wTempValueMeasurement) * 5;
		Else
			arrLumgates[iLoop].iMeasurement			:=	word_to_int(wTempValueMeasurement) * 8;
		End_if;
		arrLumgates[iLoop].xErrorOutputLevel	:=	arrIB_Configuration[iModuleNumber].arrPdIn[1].X2;			(* Error output data *)
		arrLumgates[iLoop].xSafeSet				:=	arrIB_Configuration[iModuleNumber].arrPdIn[1].X6;			(* Safe set *)
		arrLumgates[iLoop].xSafeFinish			:=	arrIB_Configuration[iModuleNumber].arrPdIn[1].X7;			(* Safe finish *)
	End_If;


	If TON_Startup.Q then		(* delay calculated output *)
		wTempValue								:= 	int_to_word(arrLumgates[iLoop].iActualOutputLevel) or word#1;	(* Set lowest Bit *)
		arrActualOutput[iLoop]					:=	arrLumgates[iLoop].iActualOutputLevel;					(* Store calculated output remanent *)
		arrActualRelayState1[iLoop]				:=	arrLumgates[iLoop].xRelay1;
		arrActualRelayState2[iLoop]				:=	arrLumgates[iLoop].xRelay2;
		arrActualModuleNumber[iLoop]			:=	arrLumgates[iLoop].iModuleNumberGeneral;


		iBS_Devices								:=	iTotalDevices;


		If arrLumgates[iLoop].xAvailable and xActivateOutputData then
			iModuleNumber						:=	arrLumgates[iLoop].iModuleNumberGeneral;
			If iModuleNumber > 0 then
				arrIB_Configuration[iModuleNumber].arrPdOut[2]		:= 	wTempValue.B0;
				arrIB_Configuration[iModuleNumber].arrPdOut[1].X0	:= 	wTempValue.X8;
				arrIB_Configuration[iModuleNumber].arrPdOut[1].X1	:= 	wTempValue.X9;
				arrIB_Configuration[iModuleNumber].arrPdOut[1].X2	:= 	wTempValue.X10;
				arrIB_Configuration[iModuleNumber].arrPdOut[1].X4	:=	arrLumgates[iLoop].xRelay1; 		(* Relay 1 *)
				arrIB_Configuration[iModuleNumber].arrPdOut[1].X5	:=	arrLumgates[iLoop].xRelay2;			(* Relay 2 *)
			End_if;
		End_if;


	Elsif xIBS_DeviceCountOK then
	(* Supply the Lumgate outputs with the failsafe values, in case of an bus error or a power up,
	 if the count of Lumgates is equal to the count befor the error occurs *)
		If ((arrActualModuleNumber[iLoop] > 0) and (arrLumgates[iLoop].iModuleNumberGeneral = 0)) then
			arrLumgates[iLoop].iModuleNumberGeneral		:=	arrActualModuleNumber[iLoop];
		End_if;
		If arrLumgates[iLoop].xFailMode then
			arrLumgates[iLoop].iActualOutputLevel		:=	arrLumgates[iLoop].iOutputLevelFailsave;


			If arrLumgates[iLoop].xRelay1_NC then
				arrLumgates[iLoop].xRelay1					:=	not arrLumgates[iLoop].xFailsaveRelay1; 		(* Relay 1 *)
			Else
				arrLumgates[iLoop].xRelay1					:=	arrLumgates[iLoop].xFailsaveRelay1; 		(* Relay 1 *)
			End_if;
			If arrLumgates[iLoop].xRelay2_NC then
				arrLumgates[iLoop].xRelay2					:=	not arrLumgates[iLoop].xFailsaveRelay2; 		(* Relay 2 *)
			Else
				arrLumgates[iLoop].xRelay2					:=	arrLumgates[iLoop].xFailsaveRelay2; 		(* Relay 2 *)
			End_if;
		Elsif arrActualOutput[iLoop] <> 0 then
			arrLumgates[iLoop].iActualOutputLevel		:=	arrActualOutput[iLoop];			(* Set last valid calculated output until the startup is complete *)


			arrLumgates[iLoop].xRelay1					:=	arrActualRelayState1[iLoop]; 		(* Relay 1 *)
			arrLumgates[iLoop].xRelay2					:=	arrActualRelayState2[iLoop]; 		(* Relay 2 *)
		End_if;			
		wTempValue										:= 	int_to_word(arrLumgates[iLoop].iActualOutputLevel) or word#1;


		If iModuleNumber > 0 then
			arrIB_Configuration[iModuleNumber].arrPdOut[2]		:= 	wTempValue.B0;
			arrIB_Configuration[iModuleNumber].arrPdOut[1].X0	:= 	wTempValue.X8;
			arrIB_Configuration[iModuleNumber].arrPdOut[1].X1	:= 	wTempValue.X9;
			arrIB_Configuration[iModuleNumber].arrPdOut[1].X2	:= 	wTempValue.X10;
			arrIB_Configuration[iModuleNumber].arrPdOut[1].X4	:=	arrLumgates[iLoop].xRelay1; 		(* Relay 1 *)
			arrIB_Configuration[iModuleNumber].arrPdOut[1].X5	:=	arrLumgates[iLoop].xRelay2;			(* Relay 2 *)
		End_if;


	Else
	(* Supply the Lumgate outputs with a general failsafe value, in case of an bus error or a power up,
	 if the count of Lumgates is >>>not<<< equal to the count befor the error occurs *)
		arrLumgates[iLoop].iActualOutputLevel			:=	250;
		If arrLumgates[iLoop].iModuleNumberGeneral = 0 then
			arrLumgates[iLoop].iModuleNumberGeneral		:=	iLoop + 5;
		End_if;
		wTempValue										:= 	int_to_word(arrLumgates[iLoop].iActualOutputLevel) or word#1;
		arrLumgates[iLoop].xRelay1						:=	False; 		(* Relay 1 *)
		arrLumgates[iLoop].xRelay2						:=	False; 		(* Relay 2 *)


		iModuleNumber									:=	arrLumgates[iLoop].iModuleNumberGeneral;
		If iModuleNumber > 0 then
			arrIB_Configuration[iModuleNumber].arrPdOut[2]		:= 	wTempValue.B0;
			arrIB_Configuration[iModuleNumber].arrPdOut[1].X0	:= 	wTempValue.X8;
			arrIB_Configuration[iModuleNumber].arrPdOut[1].X1	:= 	wTempValue.X9;
			arrIB_Configuration[iModuleNumber].arrPdOut[1].X2	:= 	wTempValue.X10;
			arrIB_Configuration[iModuleNumber].arrPdOut[1].X4	:=	arrLumgates[iLoop].xRelay1; 		(* Relay 1 *)
			arrIB_Configuration[iModuleNumber].arrPdOut[1].X5	:=	arrLumgates[iLoop].xRelay2;			(* Relay 2 *)
		End_if;
	End_if;
End_For;

(* InterbusStartup *)
IBStartup(xActivate	:= 	xActivateStartup,	 (* FB_InterbusStartup *)
xInitNewConfig		:= 	xInitNewConfig,
xOS					:= 	False,
tWatchdog			:= 	t#50s,
arrIBC_SysConf		:= 	arrIB_SysConfig,
arrIBS_ConfEntry	:= 	arrIB_Configuration);


xActiveStartup		:=	IBStartup.xActive;
xReadyStartup		:=	IBStartup.xReady;
xDoneStartup		:=	IBStartup.xDone;
xErrorStartup		:=	IBStartup.xError;
wDiagCodeStartup	:=	IBStartup.wDiagCode;
dwDiagCodeStartup	:=	IBStartup.dwAddDiagCode;
iTotalDevices		:=	IBStartup.iIBS_Modules;
iActDevice			:=	IBStartup.iIdxCntMod;
strDiagCodeStartup	:=	IBStartup.strDiagCode;
arrIB_SysConfig		:=	IBStartup.arrIBC_SysConf;
arrIB_Configuration	:=	IBStartup.arrIBS_ConfEntry;


If IBStartup.iState = 80 then
	iIBS_State	:=	1;						(* Visu Rescan messages, Search for Interbus devices *)
Elsif IBStartup.iState = 150 or IBStartup.iState = 160 then
	iIBS_State	:=	2;						(* Visu Rescan messages, Collect device information *)
Elsif IBStartup.iState = 170 or IBStartup.iState = 180 then
	iIBS_State	:=	3;						(* Visu Rescan messages, Get process data *)
Else
	iIBS_State	:=	0;						(* Visu Rescan messages, hide *)
End_if;


If TON_ReadDevices.Q and iTotalDevices = iBS_Devices then
	xIBS_DeviceCountOK	:=	True;
Else
	xIBS_DeviceCountOK	:=	False;
End_if;


ProcessData(xActivate:= xActivateProcessData, (* FB_PD_Assign *)
arrInput_PD			:=	arrInputPD,
arrIBS_ConfEntry	:=	arrIB_Configuration);


xActiveProcessData		:=	ProcessData.xActive;
xErrorProcessData		:=	ProcessData.xError;
wDiagCodeProcessData	:=	ProcessData.wDiagCode;
dwAddDiagCodeProcessData:=	ProcessData.dwAddDiagCode;
strDiagCodeProcessData	:=	ProcessData.strDiagCode;
arrOutgoingPD			:=	ProcessData.arrOutput_PD;
arrIB_Configuration		:=	ProcessData.arrIBS_ConfEntry;


If xActivateOutputData or xIBS_DeviceCountOK Then
	arrOutputPD	:= arrOutgoingPD;
Else
	For iLoop := 1 to 512 do
		If iLoop > 9 and not xStepup then
			arrOutputPD[iLoop]	:=	byte#16#C9;	(* Failsafe value 20.1 % during the Startup *)
			xStepup				:=	True;
		Elsif iLoop = 10 then
			arrOutputPD[iLoop]	:=	byte#16#C9;
			xStepup				:=	True;
		Elsif xStepup and iLoop > 9 then
			arrOutputPD[iLoop]	:=	byte#16#00;
			xStepup				:=	False;
		Else
			arrOutputPD[iLoop]	:=	byte#0;
		End_if;
	End_For;	
End_If;


(* Delay the output data ( 8s + 5 * switch scene delay ) *)
tDealy	:=	dint_to_time(dint#8000 + (int_to_dint(iDelay) * dint#5000));
TON_Startup(IN:=xActivateOutputData,PT:=tDealy);


TON_ReadDevices(IN:=((iTotalDevices = iActDevice) and (IBStartup.iState = 0) and xDoneStartup),PT:=t#2s);

R_TRIG_Activate(CLK := xActivate);
F_TRIG_Activate(CLK := xActivate);


(*Activation of FB*)
IF R_TRIG_Activate.Q THEN


	xActive 		:= TRUE;
	wCodeResp		:= WORD#16#0000;
	wParameterCount	:= WORD#16#0000;
	wResult			:= WORD#16#0000;
	iIndexDst		:= 0;
	iIndexEnd		:= 0;
	iIndexSrc		:= 0;
	xError			:= FALSE;
	xErrorReqCode	:= FALSE;
	xReady			:= FALSE;
	xErrorResult	:= FALSE;
	wCode			:= WORD#16#0000;
	wDiagCode		:= WORD#16#0000;
	wAddDiagCode	:= WORD#16#0000;
	iDataLength		:= 0;




(*Deactivation of FB*)
ELSIF F_TRIG_Activate.Q THEN


	xActive 		:= FALSE;
	wCodeResp		:= WORD#16#0000;
	wParameterCount	:= WORD#16#0000;
	wResult			:= WORD#16#0000;
	iIndexDst		:= 0;
	iIndexEnd		:= 0;
	iIndexSrc		:= 0;
	xError			:= FALSE;
	xErrorReqCode	:= FALSE;
	xReady			:= FALSE;
	xErrorResult	:= FALSE;
	wCode			:= WORD#16#0000;
	wDiagCode		:= WORD#16#0000;
	wAddDiagCode	:= WORD#16#0000;
	iDataLength		:= 0;


END_IF;

R_TRIG_Activate(CLK := xActivate);
F_TRIG_Activate(CLK := xActivate);


IF R_TRIG_Activate.Q THEN


	xActive 				:= TRUE;
	xErrBusStatus			:= FALSE;
	xErrConfig				:= FALSE;
	xErrIBC					:= FALSE;
	xErrResp				:= FALSE;
	xErrState				:= FALSE;
	xError					:= FALSE;
	xStateWatchdogEn		:= FALSE;
	xDone					:= FALSE;
	xReady					:= FALSE;
	xRecPdDescrList			:= FALSE;
	xRecComp_PD_RefList		:= FALSE;
	xActivateProc_IBC_Resp	:= TRUE;		(*Activate function block for processing of response from interbus controller*)
	xActivateEvalPdDescr	:= TRUE;		(*Activate function block for evaluation of process data description list*)


	dwAddDiagCode			:= DWORD#16#00000000;
	wDiagCode				:= WORD#16#0000;
	strDiagCode				:= '';
	iState					:= 0;
	iMaxModules				:= 256;
	iIdxCntMod 				:= 1;
	iPntrPDBeginIn			:= 1;
	iPntrPDBeginOut			:= 1;




	xActivateProc_IBC_Resp	:= TRUE;
	xActivateEvalPdDescr	:= TRUE;
	xActivateEvalCompRefList:= TRUE;
	xIBC_Req				:= FALSE;
	xIBC_NDR				:= FALSE;
	xIBC_Err				:= FALSE;
	xErrIBC					:= FALSE;
	xIBC_Resp_NDR			:= FALSE;
	xIBC_Resp_Active		:= FALSE;
	xIBC_Resp_Err			:= FALSE;
	xErrResp				:= FALSE;
	xErrBusStatus			:= FALSE;
	xErrBusRun				:= FALSE;
	xMoreFollows			:= FALSE;
	xErrConfig				:= FALSE;
	xStateWatchdogEn		:= FALSE;
	xErrParam				:= FALSE;
	xStateWatchdogEn		:= FALSE;
	xErrStateMachineWD		:= FALSE;
	xRecPdDescrList			:= FALSE;
	xPdEvalActive			:= FALSE;
	bBusSegNumber			:= BYTE#16#00;
	bPosNumber				:= BYTE#16#00;
	bPDDataTypeMask			:= BYTE#16#C0;
	bPDDataLengthMask		:= BYTE#16#3F;
	bModule_ID				:= BYTE#16#00;
	wIBC_RespCode			:= WORD#16#0000;
	wIBC_RespDiagCode		:= WORD#16#0000;
	wIBC_RespAddDiagCode	:= WORD#16#0000;
	wDiagnosticStatus		:= WORD#16#0000;
	wUsedAttributes			:= WORD#16#0000;
	dwIBC_AddErr			:= DWORD#16#0000;
	iState					:= 0;
	iIBC_RespDataLength		:= 0;
	iIBS_Modules			:= 0;
	iPDLength				:= 0;
	iPDDataType				:= 0;
	iStateLast				:= 0;
	iIndex					:= 1;
	iIndexSysParam			:= 1;
	iIdxCntMod				:= 1;
	iPntrPDBeginIn			:= 1;
	iPntrPDBeginOut			:= 1;
	udiCountErrState		:= UDINT#0;




	FOR iIndexArrInit := 1 TO 512 BY 1 DO


		arrIBC_RecData[iIndexArrInit]	:= WORD#16#0000;


	END_FOR;


	FOR iIndexArrInit := 1 TO 8 BY 1 DO


		arrIBC_SndData[iIndexArrInit]	:= WORD#16#0000;


	END_FOR;


	udtDiagnosticStatus.xActive	:= FALSE;
	udtDiagnosticStatus.xBD		:= FALSE;
	udtDiagnosticStatus.xBit0	:= FALSE;
	udtDiagnosticStatus.xBit4	:= FALSE;
	udtDiagnosticStatus.xBit9	:= FALSE;
	udtDiagnosticStatus.xBus	:= FALSE;
	udtDiagnosticStatus.xCtrl	:= FALSE;
	udtDiagnosticStatus.xForce	:= FALSE;
	udtDiagnosticStatus.xPF		:= FALSE;
	udtDiagnosticStatus.xReady	:= FALSE;
	udtDiagnosticStatus.xRun	:= FALSE;


ELSIF F_TRIG_Activate.Q THEN


	xActive					:= FALSE;
	xIBC_Req				:= FALSE;
	xReady					:= FALSE;
	iState					:= 0;
	xErrBusStatus			:= FALSE;
	xErrConfig				:= FALSE;
	xErrIBC					:= FALSE;
	xErrResp				:= FALSE;
	xErrState				:= FALSE;
	xErrBusRun				:= FALSE;
	xError					:= FALSE;
	xStateWatchdogEn		:= FALSE;
	xDone					:= FALSE;
	xReady					:= FALSE;
	xRecComp_PD_RefList		:= FALSE;
	dwAddDiagCode			:= DWORD#16#00000000;
	wDiagCode				:= WORD#16#0000;
	strDiagCode				:= '';
	iIdxCntMod 				:= 1;
	iPntrPDBeginIn			:= 1;
	iPntrPDBeginOut			:= 1;
	xRecPdDescrList			:= FALSE;
	xActivateProc_IBC_Resp	:= TRUE;		(*Activate function block for processing of response from interbus controller*)
	xActivateEvalPdDescr	:= TRUE;		(*Activate function block for evaluation of process data description list*)
	xActivateEvalCompRefList:= FALSE;


	xIBC_Req				:= FALSE;
	xIBC_NDR				:= FALSE;
	xIBC_Err				:= FALSE;
	xErrIBC					:= FALSE;
	xActivateProc_IBC_Resp	:= FALSE;
	xIBC_Resp_NDR			:= FALSE;
	xIBC_Resp_Active		:= FALSE;
	xIBC_Resp_Err			:= FALSE;
	xErrResp				:= FALSE;
	xErrBusStatus			:= FALSE;
	xMoreFollows			:= FALSE;
	xErrConfig				:= FALSE;
	xStateWatchdogEn		:= FALSE;
	xErrParam				:= FALSE;
	xStateWatchdogEn		:= FALSE;
	xErrStateMachineWD		:= FALSE;
	xRecPdDescrList			:= FALSE;
	xPdEvalActive			:= FALSE;
	xActivateEvalPdDescr	:= FALSE;
	bBusSegNumber			:= BYTE#16#00;
	bPosNumber				:= BYTE#16#00;
	bPDDataTypeMask			:= BYTE#16#00;
	bPDDataLengthMask		:= BYTE#16#00;
	bModule_ID				:= BYTE#16#00;
	wIBC_RespCode			:= WORD#16#0000;
	wIBC_RespDiagCode		:= WORD#16#0000;
	wIBC_RespAddDiagCode	:= WORD#16#0000;
	wDiagnosticStatus		:= WORD#16#0000;
	wUsedAttributes			:= WORD#16#0000;
	dwIBC_AddErr			:= DWORD#16#00000000;
	iState					:= 0;
	iIBC_RespDataLength		:= 0;
	iIndex					:= 0;
	iIBS_Modules			:= 0;
	iIndexSysParam			:= 0;
	iPDLength				:= 0;
	iPDDataType				:= 0;
	iMaxModules				:= 0;
	iStateLast				:= 0;
	iIdxCntMod				:= 0;
	iPntrPDBeginIn			:= 0;
	iPntrPDBeginOut			:= 0;
	udiCountErrState		:= UDINT#0;


	FOR iIndexArrInit := 1 TO 512 BY 1 DO


		arrIBC_RecData[iIndexArrInit]	:= WORD#16#0000;


	END_FOR;


	FOR iIndexArrInit := 1 TO 8 BY 1 DO


		arrIBC_SndData[iIndexArrInit]	:= WORD#16#0000;


	END_FOR;


	udtDiagnosticStatus.xActive	:= FALSE;
	udtDiagnosticStatus.xBD		:= FALSE;
	udtDiagnosticStatus.xBit0	:= FALSE;
	udtDiagnosticStatus.xBit4	:= FALSE;
	udtDiagnosticStatus.xBit9	:= FALSE;
	udtDiagnosticStatus.xBus	:= FALSE;
	udtDiagnosticStatus.xCtrl	:= FALSE;
	udtDiagnosticStatus.xForce	:= FALSE;
	udtDiagnosticStatus.xPF		:= FALSE;
	udtDiagnosticStatus.xReady	:= FALSE;
	udtDiagnosticStatus.xRun	:= FALSE;


END_IF;

(*Set FB outputs + Error processing*)


IF xErrParam THEN


	xReady 				:= FALSE;
	xError				:= TRUE;
	wDiagCode			:= WORD#16#0C10;
	dwAddDiagCode		:= DWORD#16#00000000;
	strDiagCode			:= 'Invalid input parameters';


ELSIF xErrIBC OR xIBC_Err THEN


	xReady				:= FALSE;
	xError				:= TRUE;
	wDiagCode			:= WORD#16#0C20;			(*Assign diagnostic code for interbus control FB error*)
	dwAddDiagCode		:= dwIBC_AddErr;			(*Return additional error information from IB control function block*)
	strDiagCode			:= 'Error processing FB IB Control';


ELSIF xErrResp OR xIBC_Resp_Err THEN


	xReady 				:= FALSE;
	xError				:= TRUE;
	wDiagCode			:= WORD#16#0C30;
	dwAddDiagCode		:= WORD_TO_DWORD(wIBC_RespDiagCode);
	strDiagCode			:= 'Error processing response from IB master';


ELSIF xErrConfig THEN


	xReady				:= FALSE;
	xError				:= TRUE;
	wDiagCode			:= WORD#16#0C40;
	dwAddDiagCode		:= INT_TO_DWORD(iIBC_RespDataLength);
	strDiagCode			:= 'Invalid response data length from IB master';


ELSIF xErrBusStatus THEN


	xReady				:= FALSE;
	xError				:= TRUE;
	wDiagCode			:= WORD#16#0C50;
	dwAddDiagCode		:= DWORD#16#00000000;
	dwAddDiagCode.X0	:= udtDiagnosticStatus.xReady;
	dwAddDiagCode.X1	:= udtDiagnosticStatus.xActive;
	dwAddDiagCode.X2	:= udtDiagnosticStatus.xRun;
	strDiagCode			:= 'Wrong bus status';


ELSIF xDone THEN


	xReady				:= TRUE;
	wDiagCode			:= WORD#16#1000;
	dwAddDiagCode		:= INT_TO_DWORD(iState);
	strDiagCode			:= 'Bus started';


ELSIF (iState >= 10) AND (iState <= 30) THEN


	xReady				:= FALSE;
	wDiagCode			:= WORD#16#8100;
	dwAddDiagCode		:= INT_TO_DWORD(iState);
	strDiagCode			:= 'Initialization';


ELSIF (iState > 30) AND (iState < 999) THEN


	xReady				:= FALSE;
	wDiagCode			:= WORD#16#8200;
	dwAddDiagCode		:= INT_TO_DWORD(iState);
	strDiagCode			:= 'Busy';


ELSIF xActive THEN


	xReady				:= TRUE;
	wDiagCode			:= WORD#16#8000;
	dwAddDiagCode		:= DWORD#16#00000000;
	strDiagCode			:= 'Ready';


END_IF;

R_TRIG_Activate(CLK := xActivate);
F_TRIG_Activate(CLK := xActivate);


(*Activation of FB*)
IF R_TRIG_Activate.Q THEN


	xActive 		:= TRUE;
	iIndexModules	:= 0;
	iErrMemcopy		:= 0;
	iPntrInput_PD	:= 0;
	iPntrOutput_PD	:= 0;
	dwModuleSelMask	:= DWORD#16#00000000;
	dwSelCurModule	:= DWORD#16#00000000;
	wMEMCPY			:= WORD#16#0000;
(*Deactivation of FB*)
ELSIF F_TRIG_Activate.Q THEN


	xActive 		:= FALSE;
	iIndexModules	:= 0;
	iErrMemcopy		:= 0;
	iPntrInput_PD	:= 0;
	iPntrOutput_PD	:= 0;
	dwModuleSelMask	:= DWORD#16#00000000;
	dwSelCurModule	:= DWORD#16#00000000;
	wMEMCPY			:= WORD#16#0000;


	For i:= 1 To 512
		Do
			arrOutput_PD[i]:= arrOutput_PD[i]; (* jvo 17.05.2016, hold PD *)
	End_For;


END_IF;


(*Process FB only if activated*)
IF xActive THEN


	(*Perform process data assignment if module has process data*)
	FOR iIndexModules := 1 TO 256 BY 1 DO
		(*Check if module has process data
		IF (arrIBS_ConfEntry[iIndexModules].iPdLenCompl > 0) THEN


			IF (iIndexModules <= 32) THEN


				dwSelCurModule 	:= SHL_DWORD(DWORD#16#00000001, (iIndexModules - 1));
				dwModuleSelMask	:= dwModuleSelMask1;


			ELSE


				dwSelCurModule 	:= SHL_DWORD(DWORD#16#00000001, (iIndexModules - 33));
				dwModuleSelMask	:= dwModuleSelMask2;


			END_IF;


			IF DWORD_TO_BOOL(dwModuleSelMask AND dwSelCurModule) THEN


				(*Check if module has input process data*)
				IF (arrIBS_ConfEntry[iIndexModules].iPdLenIn > 0) THEN


					iPntrInput_PD	:= arrIBS_ConfEntry[iIndexModules].iPntrPdBeginIn;	(*Select pointer to beginning of input PD of module*)
					wMEMCPY 		:= MEMCPY(iErrMemcopy, arrIBS_ConfEntry[iIndexModules].iReservedBytesIn,
									arrInput_PD[iPntrInput_PD], arrIBS_ConfEntry[iIndexModules].arrPdIn[1]);	(*Copy input PD into module structure*)


				END_IF;


				(*Check if module has output process data*)
				IF (arrIBS_ConfEntry[iIndexModules].iPdLenOut > 0) THEN


					iPntrOutput_PD	:= arrIBS_ConfEntry[iIndexModules].iPntrPdBeginOut;	(*Select pointer to beginning of output PD of module*)
					wMEMCPY 		:= MEMCPY(iErrMemcopy, arrIBS_ConfEntry[iIndexModules].iReservedBytesOut, 
									arrIBS_ConfEntry[iIndexModules].arrPdOut[1], arrOutput_PD[iPntrOutput_PD]);	(*Copy output PD into output PD array*)


				END_IF;


		(*	END_IF;


		END_IF;*)


	END_FOR;


END_IF;


(*Error processing*)
IF (iErrMemcopy <> 0) AND xActive THEN


	xError 			:= TRUE;
	wDiagCode		:= WORD#16#C010;
	dwAddDiagCode	:= INT_TO_DWORD(iErrMemcopy);
	strDiagCode		:= 'Memcopy error';


ELSIF xActive THEN


	wDiagCode		:= WORD#16#8200;	(*Set default diagnostic value*)
	dwAddDiagCode	:= DWORD#16#0000;	(*Set default additional diagnostic information*)


END_IF;

R_TRIG_Activate(CLK := xActivate);
F_TRIG_Activate(CLK := xActivate);


(*Trigger activation of function block*)
IF R_TRIG_Activate.Q THEN


	xActive 		:= TRUE;
	xError			:= FALSE;
	xErrorDataType	:= FALSE;
	xReady			:= FALSE;
	iIndex			:= 0;
	iPointerEntry	:= 0;
	wAddrDir		:= WORD#16#0000;
	wEntryCount		:= WORD#16#0000;
	wDataCons		:= WORD#16#0000;
	wPD_Address		:= WORD#16#0000;
	wDiagCode		:= WORD#16#0000;


(*Trigger deactivation of function block*)
ELSIF F_TRIG_Activate.Q THEN


	xActive 		:= FALSE;
	xError			:= FALSE;
	xErrorDataType	:= FALSE;
	xReady			:= FALSE;
	iIndex			:= 0;
	iPointerEntry	:= 0;
	wAddrDir		:= WORD#16#0000;
	wEntryCount		:= WORD#16#0000;
	wDataCons		:= WORD#16#0000;
	wPD_Address		:= WORD#16#0000;
	wDiagCode		:= WORD#16#0000;


END_IF;

(*Perform function block operations if function block active*)
IF xActive THEN


	(*Wait for trigger to process evaluation of process data description*)
	R_TRIG_Exec(CLK := xExecute);


	IF R_TRIG_Exec.Q THEN


		wAddrDir		:= arrIBC_SysConf[1];	(*Read out process data direction: 1000 - Input PD; 2000 - Output PD*)
		wEntryCount		:= arrIBC_SysConf[2];	(*Number of following entries*)
		iPointerEntry	:= 0;
		iIndex			:= 0;


		(*Process all process data entries of compact PD reference list*)
		FOR iIndex := 1 TO UINT_TO_INT(WORD_TO_UINT(wEntryCount)) BY 1 DO


			iPointerEntry	:= iIndex + 2;	(*Entry pointer = Index + 2 entries offset*)


			(*If entry = FFFF -> process data of module does not match requested data direction*)
			IF NOT (arrIBC_SysConf[iPointerEntry] = WORD#16#FFFF) THEN


				wDataCons		:= WORD#16#C000 AND arrIBC_SysConf[iPointerEntry];	(*Read out data consistency of process data of module*)
				wPD_Address		:= WORD#16#3FFF AND arrIBC_SysConf[iPointerEntry];	(*Read out starting address of process data*)


				(*Process input process data*)
				IF (wAddrDir = WORD#16#1000) THEN


					arrIBS_ConfEntry[iIndex].xInputPD	 		:= TRUE;										(*Module possesses input PD*)
					arrIBS_ConfEntry[iIndex].iPntrPdBeginIn		:= UINT_TO_INT(WORD_TO_UINT(wPD_Address)) + 1;	(*Pointer to begin of input PD*)
					arrIBS_ConfEntry[iIndex].iReservedBytesIn	:= arrIBS_ConfEntry[iIndex].iPdLenCompl / 8;	(*Reserved bytes for input PD*)
					arrIBS_ConfEntry[iIndex].iPdLenIn			:= arrIBS_ConfEntry[iIndex].iPdLenCompl;		(*Set length of input PD*)

					(*If module possesses input PD -> 1 Word is reserved at min on eClr device*)
					IF (arrIBS_ConfEntry[iIndex].iReservedBytesIn < 2) THEN


						arrIBS_ConfEntry[iIndex].iReservedBytesIn := 2;


					END_IF;


					(*Reserved process data is rounded up to full word on eClr device*)
					IF (arrIBS_ConfEntry[iIndex].iReservedBytesIn MOD 2 <> 0) THEN


						arrIBS_ConfEntry[iIndex].iReservedBytesIn := arrIBS_ConfEntry[iIndex].iReservedBytesIn + 1 ;


					END_IF;


				ELSIF (wAddrDir = WORD#16#2000) THEN


					arrIBS_ConfEntry[iIndex].xOutputPD 			:= TRUE;										(*Module possesses output PD*)
					arrIBS_ConfEntry[iIndex].iPntrPdBeginOut	:= UINT_TO_INT(WORD_TO_UINT(wPD_Address)) + 1;	(*Pointer to begin of output PD*)
					arrIBS_ConfEntry[iIndex].iReservedBytesOut	:= arrIBS_ConfEntry[iIndex].iPdLenCompl / 8;	(*Reserved bytes for output PD*)
					arrIBS_ConfEntry[iIndex].iPdLenOut			:= arrIBS_ConfEntry[iIndex].iPdLenCompl;		(*Set length of output PD*)

					(*If module possesses output PD -> 1 Word is reserved at min on eClr system*)
					IF (arrIBS_ConfEntry[iIndex].iReservedBytesOut < 2) THEN


						arrIBS_ConfEntry[iIndex].iReservedBytesOut := 2;


					END_IF;


					(*Reserved process data is rounded up to full word on eClr device*)
					IF (arrIBS_ConfEntry[iIndex].iReservedBytesOut MOD 2 <> 0) THEN


						arrIBS_ConfEntry[iIndex].iReservedBytesOut := arrIBS_ConfEntry[iIndex].iReservedBytesOut + 1 ;


					END_IF;


				(*Report error in case of unknown process data direction*)
				ELSE


					xErrorDataType	:= TRUE;


				END_IF;


			END_IF;


		END_FOR;


	END_IF;


END_IF;

(*Process output parameters of FB*)


IF NOT xActive THEN


	xReady 			:= FALSE;
	xError 			:= FALSE;
	wDiagCode		:= WORD#16#0000;
	dwAddDiagCode 	:= DWORD#16#00000000;


ELSIF xActivate AND xErrorDataType THEN


	xReady			:= FALSE;
	xError			:= TRUE;
	wDiagCode		:= WORD#16#C010;
	dwAddDiagCode	:= DWORD#16#0000FFFF AND WORD_TO_DWORD(wAddrDir);


ELSIF xActive AND NOT xError AND NOT R_TRIG_Exec.Q THEN


	xReady 			:= TRUE;
	wDiagCode		:= WORD#16#8000;
	dwAddDiagCode	:= DWORD#16#00000000;


ELSIF xActive AND NOT xError AND R_TRIG_Exec.Q THEN


	xReady			:= FALSE;
	wDiagCode		:= WORD#16#8200;
	dwAddDiagCode	:= DWORD#16#00000000;


END_IF;

R_TRIG_Activate(CLK := xActivate);
F_TRIG_Activate(CLK := xActivate);


(*Trigger activation of function block*)
IF R_TRIG_Activate.Q THEN


	xActive 			:= TRUE;
	xError				:= FALSE;
	xMoreFollows		:= FALSE;
	bBitPos				:= BYTE#16#00;
	bDataDir			:= BYTE#16#00;
	bDataType			:= BYTE#16#00;
	bLength				:= BYTE#16#00;
	wBytePos			:= WORD#16#0000;
	wDeviceNo			:= WORD#16#0000;
	wPDD_Count			:= WORD#16#0000;
	wPDD_Index			:= WORD#16#0000;
	wStart_PDD_Index	:= WORD#16#0000;
	iCntPdd				:= 0;
	iExtLength			:= 0;
	iIndex				:= 0;
	iPD_Length			:= 0;


(*Trigger deactivation of function block*)
ELSIF F_TRIG_Activate.Q THEN


	xActive 			:= FALSE;
	xError				:= FALSE;
	xMoreFollows		:= FALSE;
	bBitPos				:= BYTE#16#00;
	bDataDir			:= BYTE#16#00;
	bDataType			:= BYTE#16#00;
	bLength				:= BYTE#16#00;
	wBytePos			:= WORD#16#0000;
	wDeviceNo			:= WORD#16#0000;
	wPDD_Count			:= WORD#16#0000;
	wPDD_Index			:= WORD#16#0000;
	wStart_PDD_Index	:= WORD#16#0000;
	iCntPdd				:= 0;
	iExtLength			:= 0;
	iIndex				:= 0;
	iPD_Length			:= 0;


END_IF;


(*Perform function block operations if function block active*)
IF xActive THEN


	(*Wait for trigger to process evaluation of process data description*)
	R_TRIG_Exec(CLK := xExecute);


	xMoreFollows	:= FALSE;


	IF R_TRIG_Exec.Q THEN


		xMoreFollows 		:= arrIBC_SysConf[1].X0;
		wDeviceNo			:= arrIBC_SysConf[2];
		wStart_PDD_Index	:= arrIBC_SysConf[3];
		wPDD_Count			:= arrIBC_SysConf[4];
		iIndex				:= 5;

		FOR iCntPdd := 1 TO UINT_TO_INT(WORD_TO_UINT(wPDD_Count)) BY 1 DO


			wPDD_Index	:= arrIBC_SysConf[iIndex];
			iIndex		:= iIndex + 1;
			bDataDir	:= arrIBC_SysConf[iIndex].B1;
			bDataType	:= arrIBC_SysConf[iIndex].B0;
			iIndex		:= iIndex + 1;
			wBytePos	:= arrIBC_SysConf[iIndex];


			If wBytePos <> Word#0 Then
				xTest:= True;
			End_If;


			iIndex		:= iIndex + 1;
			bBitPos		:= arrIBC_SysConf[iIndex].B1;
			bLength		:= arrIBC_SysConf[iIndex].B0;
			iIndex		:= iIndex + 1;
			iExtLength	:= UINT_TO_INT(BYTE_TO_UINT(arrIBC_SysConf[iIndex].B1));
			iPD_Length 	:= UINT_TO_INT(BYTE_TO_UINT(bLength));


			(*Data direction = 0C: Input process data*)
			IF (bDataDir = BYTE#16#0C) THEN


				arrIBS_ConfEntry[iIdxCntMod].iPntrPdBeginIn	:= iPntrPDBeginIn + UINT_TO_INT(WORD_TO_UINT(wBytePos));
				arrIBS_ConfEntry[iIdxCntMod].xInputPD		:= TRUE;

				IF ((bDataType = BYTE#16#0A) AND (iPD_Length > 0)) THEN


					If (arrIBS_ConfEntry[iIdxCntMod].iPntrPdBeginIn Mod 2) = 0 & iPD_Length <> 1 Then
						arrIBS_ConfEntry[iIdxCntMod].iPntrPdBeginIn:= arrIBS_ConfEntry[iIdxCntMod].iPntrPdBeginIn + 1;
					End_If;


					arrIBS_ConfEntry[iIdxCntMod].iPdLenIn			:= arrIBS_ConfEntry[iIdxCntMod].iPdLenIn + iPD_Length * 8;
					iPntrPDBeginIn									:= arrIBS_ConfEntry[iIdxCntMod].iPntrPdBeginIn + iPD_Length;
					arrIBS_ConfEntry[iIdxCntMod].iReservedBytesIn	:= arrIBS_ConfEntry[iIdxCntMod].iReservedBytesIn + iPD_Length;


				ELSIF ((bDataType = BYTE#16#0F) AND (iPD_Length > 0)) THEN


					If (arrIBS_ConfEntry[iIdxCntMod].iPntrPdBeginIn Mod 2) = 0 Then
						arrIBS_ConfEntry[iIdxCntMod].iPntrPdBeginIn:= arrIBS_ConfEntry[iIdxCntMod].iPntrPdBeginIn + 1;
					End_If;


					arrIBS_ConfEntry[iIdxCntMod].iPdLenIn			:= arrIBS_ConfEntry[iIdxCntMod].iPdLenIn + iPD_Length;
					iPntrPDBeginIn									:= arrIBS_ConfEntry[iIdxCntMod].iPntrPdBeginIn + 1;
					arrIBS_ConfEntry[iIdxCntMod].iReservedBytesIn	:= arrIBS_ConfEntry[iIdxCntMod].iReservedBytesIn + 1;


				ELSE


					xErrorDataType	:= TRUE;
					EXIT;


				END_IF;


			(*Data direction = 0D: Output process data*)
			ELSIF (bDataDir = BYTE#16#0D) THEN


				arrIBS_ConfEntry[iIdxCntMod].iPntrPdBeginOut 	:= iPntrPdBeginOut + UINT_TO_INT(WORD_TO_UINT(wBytePos));
				arrIBS_ConfEntry[iIdxCntMod].xOutputPD			:= TRUE;


				IF ((bDataType = BYTE#16#0A) AND (iPD_Length > 0)) THEN


					If (arrIBS_ConfEntry[iIdxCntMod].iPntrPdBeginOut Mod 2) = 0 & iPD_Length <> 1 Then
						arrIBS_ConfEntry[iIdxCntMod].iPntrPdBeginOut:= arrIBS_ConfEntry[iIdxCntMod].iPntrPdBeginOut + 1;
					End_If;


					arrIBS_ConfEntry[iIdxCntMod].iPdLenOut			:= arrIBS_ConfEntry[iIdxCntMod].iPdLenOut + iPD_Length * 8;
					iPntrPDBeginOut									:= arrIBS_ConfEntry[iIdxCntMod].iPntrPdBeginOut + iPD_Length;
					arrIBS_ConfEntry[iIdxCntMod].iReservedBytesOut	:= arrIBS_ConfEntry[iIdxCntMod].iReservedBytesOut + iPD_Length;


				ELSIF ((bDataType = BYTE#16#0F) AND (iPD_Length > 0)) THEN


					If (arrIBS_ConfEntry[iIdxCntMod].iPntrPdBeginOut Mod 2) = 0 Then
						arrIBS_ConfEntry[iIdxCntMod].iPntrPdBeginOut:= arrIBS_ConfEntry[iIdxCntMod].iPntrPdBeginOut + 1;
					End_If;


					arrIBS_ConfEntry[iIdxCntMod].iPdLenOut			:= arrIBS_ConfEntry[iIdxCntMod].iPdLenOut + iPD_Length;
					iPntrPDBeginOut									:= arrIBS_ConfEntry[iIdxCntMod].iPntrPdBeginOut + 1;
					arrIBS_ConfEntry[iIdxCntMod].iReservedBytesOut	:= arrIBS_ConfEntry[iIdxCntMod].iReservedBytesOut + 1;


				ELSE


					xErrorDataType	:= TRUE;
					EXIT;


				END_IF;

			END_IF;


			IF (iExtLength > 0) THEN


				iIndex	:= iIndex + iExtLength;


			ELSE


				iIndex	:= iIndex + 1;


			END_IF;


		END_FOR;


	END_IF;


END_IF;

(*Process output parameters of FB*)


IF NOT xActive THEN


	xReady 			:= FALSE;
	xError 			:= FALSE;
	wDiagCode		:= WORD#16#0000;
	dwAddDiagCode 	:= DWORD#16#00000000;


ELSIF xActivate AND xErrorDataType THEN


	xReady			:= FALSE;
	xError			:= TRUE;
	wDiagCode		:= WORD#16#C010;
	dwAddDiagCode	:= DWORD#16#0000FFFF AND BYTE_TO_DWORD(bDataType);


ELSIF xActive AND NOT xError AND NOT R_TRIG_Exec.Q THEN


	xReady 			:= TRUE;
	wDiagCode		:= WORD#16#8000;
	dwAddDiagCode	:= DWORD#16#00000000;


ELSIF xActive AND NOT xError AND R_TRIG_Exec.Q THEN


	xReady			:= FALSE;
	wDiagCode		:= WORD#16#8200;
	dwAddDiagCode	:= DWORD#16#00000000;


END_IF;

(* Web_Communication *)
If xMenue1 then		(* Select symbolic top menue "System" *)
	xMenue1		:=	False;
	xShowMenue1	:=	True;
	xShowMenue2	:=	False;
	xShowMenue3	:=	False;
Elsif xMenue2 then	(* Select symbolic top menue "Project" *)
	xMenue2		:=	False;
	xShowMenue2	:=	True;
	xShowMenue1	:=	False;
	xShowMenue3	:=	False;
Elsif xMenue3 then	(* Select symbolic top menue "Home" *)
	xMenue3		:=	False;
	xShowMenue2	:=	False;
	xShowMenue1	:=	False;
	xShowMenue3	:=	True;
Elsif not xShowMenue1 and not xShowMenue2 and not xShowMenue3 then
	xShowMenue1	:=	False;
	xShowMenue2	:=	False;
	xShowMenue3	:=	True;
End_if;
iSelectedRBT	:=	iSelectRBT + 1;
(* Calculate amount Lumgates per RBT *)
	udtLumgateMain.arrAmountLumgates[1]	:=	0;
	udtLumgateMain.arrAmountLumgates[2]	:=	0;
	udtLumgateMain.arrAmountLumgates[3]	:=	0;
	udtLumgateMain.arrAmountLumgates[4]	:=	0;
	For iLoop := 1 to 240 do
		If arrLumgates[iLoop].iModuleLevel = 0 and arrLumgates[iLoop].xAvailable then
			udtLumgateMain.arrAmountLumgates[1]	:=	udtLumgateMain.arrAmountLumgates[1] + 1;
		End_If;
		If arrLumgates[iLoop].iModuleLevel = 1 and arrLumgates[iLoop].xAvailable then
			udtLumgateMain.arrAmountLumgates[2]	:=	udtLumgateMain.arrAmountLumgates[2] + 1;
		End_If;
		If arrLumgates[iLoop].iModuleLevel = 2 and arrLumgates[iLoop].xAvailable then
			udtLumgateMain.arrAmountLumgates[3]	:=	udtLumgateMain.arrAmountLumgates[3] + 1;
		End_If;
		If arrLumgates[iLoop].iModuleLevel = 3 and arrLumgates[iLoop].xAvailable then
			udtLumgateMain.arrAmountLumgates[4]	:=	udtLumgateMain.arrAmountLumgates[4] + 1;
		End_If;
	End_For;
(* Select data to display Lumgates in a branch (Select a Lumgate) *)
For iLoop := 0 to 9 do	(* Voltage output *)
	iTemp						:=	iLumgateSeite + iLoop;
	arrLumgateWeb[iLoop]		:=	iTemp;
	For iLoop2 := 1 to 240 do
		If arrLumgates[iLoop2].iModuleLevel = iSelectRBT and arrLumgates[iLoop2].iModuleNumber = iTemp then
			arr5LumgateValues[iLoop]	:=	arrLumgates[iTemp].iActualOutputLevel;
		End_if;
	End_for;
End_for;
(* Select data to display a single Lumgates *)
iSelectLumgate	:=	iSelectLumgateDevice + iSelectLumgateSeite - 1;
If iSelectRBT >= 0 and iSelectRBT < 4 and iSelectLumgate > 0 and iSelectLumgate < 240 then
	For iLoop := 1 to 240 do
		If arrLumgates[iLoop].iModuleLevel = iSelectRBT and arrLumgates[iLoop].iModuleNumber = iSelectLumgate then
			iTempSelectedLumgate	:=	iLoop;
			Exit;
		End_if;
	End_for;
	If iTempSelectedLumgate > 0 and iTempSelectedLumgate < 241 then
		udtLumgate.arrDimmingScene 				:=	arrLumgates[iTempSelectedLumgate].arrDimmingScene;


		If arrLumgates[iTempSelectedLumgate].xRelay1 <> xR1Old
		or arrLumgates[iTempSelectedLumgate].xRelay2 <> xR2Old then
			xR1Old	:=	arrLumgates[iTempSelectedLumgate].xRelay1;
			xR2Old	:=	arrLumgates[iTempSelectedLumgate].xRelay2;
			If arrLumgates[iTempSelectedLumgate].xRelay1 and arrLumgates[iTempSelectedLumgate].xRelay2 then
				udtLumgate.arrPowerConsumption   	:= arrLumgates[iTempSelectedLumgate].arrPowerConsumption_11;
			Elsif arrLumgates[iTempSelectedLumgate].xRelay1 and not arrLumgates[iTempSelectedLumgate].xRelay2 then
				udtLumgate.arrPowerConsumption   	:= arrLumgates[iTempSelectedLumgate].arrPowerConsumption_10;
			Elsif not arrLumgates[iTempSelectedLumgate].xRelay1 and arrLumgates[iTempSelectedLumgate].xRelay2 then
				udtLumgate.arrPowerConsumption   	:= arrLumgates[iTempSelectedLumgate].arrPowerConsumption_01;
			Elsif not arrLumgates[iTempSelectedLumgate].xRelay1 and not arrLumgates[iTempSelectedLumgate].xRelay2 then
				udtLumgate.arrPowerConsumption  	:= arrLumgates[iTempSelectedLumgate].arrPowerConsumption;
			End_if;
			udtCurrent.xRelay1	:=	arrLumgates[iTempSelectedLumgate].xRelay1;
			udtCurrent.xRelay2	:=	arrLumgates[iTempSelectedLumgate].xRelay2;
		Else
			If udtCurrent.xRelay1 and udtCurrent.xRelay2 then
				udtLumgate.arrPowerConsumption   	:= arrLumgates[iTempSelectedLumgate].arrPowerConsumption_11;
			Elsif udtCurrent.xRelay1 and not udtCurrent.xRelay2 then
				udtLumgate.arrPowerConsumption   	:= arrLumgates[iTempSelectedLumgate].arrPowerConsumption_10;
			Elsif not udtCurrent.xRelay1 and udtCurrent.xRelay2 then
				udtLumgate.arrPowerConsumption   	:= arrLumgates[iTempSelectedLumgate].arrPowerConsumption_01;
			Elsif not udtCurrent.xRelay1 and not udtCurrent.xRelay2 then
				udtLumgate.arrPowerConsumption  	:= arrLumgates[iTempSelectedLumgate].arrPowerConsumption;
			End_if;
		End_if;
		If arrLumgates[iTempSelectedLumgate].xRelay1 = udtCurrent.xRelay1 and arrLumgates[iTempSelectedLumgate].xRelay2 = udtCurrent.xRelay2 then
			udtCurrent.xRelaysEqual	:=	True;
		Else
			udtCurrent.xRelaysEqual	:=	False;
		End_if;

		udtLumgate.arrSpecialScene				:=	arrLumgates[iTempSelectedLumgate].arrSpecialScene;
		udtLumgate.iMode						:=	arrLumgates[iTempSelectedLumgate].iMode;
		udtLumgate.iModuleLevel					:=	arrLumgates[iTempSelectedLumgate].iModuleLevel;
		udtLumgate.iModuleNumber				:=	arrLumgates[iTempSelectedLumgate].iModuleNumber;
		udtLumgate.iModuleNumberGeneral			:=	arrLumgates[iTempSelectedLumgate].iModuleNumberGeneral;
		udtLumgate.rActualOutputLevel			:=	int_to_real(arrLumgates[iTempSelectedLumgate].iActualOutputLevel) / 10.0;
		udtLumgate.rMeasurement					:=	int_to_real(arrLumgates[iTempSelectedLumgate].iMeasurement);
		udtLumgate.rOutputLevelFailsave			:=	int_to_real(arrLumgates[iTempSelectedLumgate].iOutputLevelFailsave) / 10.0;
		udtLumgate.rOutputLevelManual			:=	int_to_real(arrLumgates[iTempSelectedLumgate].iOutputLevelManual) / 10.0;
		udtLumgate.rOutputLevelScenario			:=	int_to_real(arrLumgates[iTempSelectedLumgate].iOutputLevelScenario) / 10.0;
		udtLumgate.rOutputLevelScenarioWithClo	:=	int_to_real(arrLumgates[iTempSelectedLumgate].iOutputLevelScenarioWithClo) / 10.0;
		udtLumgate.rOutputLevelTeach			:=	int_to_real(arrLumgates[iTempSelectedLumgate].iOutputLevelTeach) / 10.0;
		udtLumgate.rTargetOutputLevel			:=	int_to_real(arrLumgates[iTempSelectedLumgate].iTargetOutputLevel) / 10.0;
		udtLumgate.udtCloLed.rAmountDecrease 	:=	int_to_real(arrLumgates[iTempSelectedLumgate].udtCloLed.iAmountDecrease) / 10.0;
		udtLumgate.udtCloMaintenance.rAmountDecrease :=	int_to_real(arrLumgates[iTempSelectedLumgate].udtCloMaintenance.iAmountDecrease ) / 10.0;
		udtLumgate.udtCurrentCheck				:=	arrLumgates[iTempSelectedLumgate].udtCurrentCheck;
		udtLumgate.udtHourCounter20_50			:=	arrLumgates[iTempSelectedLumgate].udtHourCounter20_50;
		udtLumgate.udtHourCounter50_75			:=	arrLumgates[iTempSelectedLumgate].udtHourCounter50_75;
		udtLumgate.udtHourCounter75_100			:=	arrLumgates[iTempSelectedLumgate].udtHourCounter75_100;
		udtLumgate.udtHourCounterGeneral		:=	arrLumgates[iTempSelectedLumgate].udtHourCounterGeneral;
		udtLumgate.udtHourCounterMaintenance	:=	arrLumgates[iTempSelectedLumgate].udtHourCounterMaintenance;
		udtLumgate.udtInternal					:=	arrLumgates[iTempSelectedLumgate].udtInternal;
		udtLumgate.rSourceOutputLevel			:=	int_to_real(arrLumgates[iTempSelectedLumgate].iSourceOutputLevel) / 10.0;
		udtLumgate.xAvailable					:=	arrLumgates[iTempSelectedLumgate].xAvailable;
		udtLumgate.xInput1						:=	arrLumgates[iTempSelectedLumgate].xInput1;
		udtLumgate.xInput2						:=	arrLumgates[iTempSelectedLumgate].xInput2;
		udtLumgate.xRelay1						:=	arrLumgates[iTempSelectedLumgate].xRelay1;
		udtLumgate.xRelay2						:=	arrLumgates[iTempSelectedLumgate].xRelay2;
		udtLumgate.xSafeFinish					:=	arrLumgates[iTempSelectedLumgate].xSafeFinish;
		udtLumgate.xSafeSet						:=	arrLumgates[iTempSelectedLumgate].xSafeSet;
		udtLumgate.xTeachConsumption			:=	arrLumgates[iTempSelectedLumgate].xTeachConsumption;
		udtLumgate.xTeachFailsave				:=	arrLumgates[iTempSelectedLumgate].xTeachFailsave;
	End_if;
End_if;


(* Display count of Lumgates at selected branch *)
If iSelectRBT >= 0 and iSelectRBT < 4 then
	iSelect				:=	iSelectRBT + 1;
	iTotalLumgatesRBT	:=	udtLumgateMain.arrAmountLumgates[iSelect];
End_if;


(* Hide Lumgates in the selected area wich are not available *)
For iLoop := 0 to 9 do
	If iTotalLumgatesRBT >= arrLumgateWeb[iLoop] then
		arr5Lumgate[iLoop]	:=	True;
	Else
		arr5Lumgate[iLoop]	:=	False;
	End_if;
End_for;


(* Old *)
For iLoop := 1 to 4 do
	If udtLumgateMain.arrSelectNumber[iLoop] < 1 or udtLumgateMain.arrSelectNumber[iLoop] > udtLumgateMain.arrAmountLumgates[iLoop] then
		udtLumgateMain.arrColorSelectNumber[iLoop]	:=	True;
	Else
		udtLumgateMain.arrColorSelectNumber[iLoop]	:=	False;
	End_If;


	If udtLumgateMain.arrButtonSelectNumber[iLoop] then
		udtLumgateMain.arrButtonSelectNumber[iLoop]	:=	False;
		iTempLevel	:=	iLoop - 1;
		For iLoop2 := 1 to 240 do
			If arrLumgates[iLoop2].iModuleLevel = iTempLevel and arrLumgates[iLoop2].iModuleNumber = udtLumgateMain.arrSelectNumber[iLoop] then
				iTempSelectedLumgate	:=	iLoop2;
				Exit;
			End_If;
		End_For;
	End_If;
End_For;


iTempSelectedLumgate				:=	Limit_Int(1,iTempSelectedLumgate,240);


