		iActualPosition										:=	165;
	601..699:
		udtLumgateCurrentConsumption.arrActualOutput[14]	:=	True; (* 6,5 *)
		iActualPosition										:=	195;
	701..799:
		udtLumgateCurrentConsumption.arrActualOutput[16]	:=	True; (* 7,5 *)
		iActualPosition										:=	225;
	801..899:
		udtLumgateCurrentConsumption.arrActualOutput[18]	:=	True; (* 8,5 *)
		iActualPosition										:=	255;
	901..999:
		udtLumgateCurrentConsumption.arrActualOutput[20]	:=	True; (* 9,5 *)
		iActualPosition										:=	285;
	1001..1070:
		udtLumgateCurrentConsumption.arrActualOutput[22]	:=	True; (* 10,7 *)
		iActualPosition										:=	315;
End_Case;


(* Old *)
udtLumgateCurrentConsumption.strVoltage			:=	Int_To_String(iTempValue,'%01d');
(* Old *)
Case iTempValue of
	0..9:
		udtLumgateCurrentConsumption.strVoltage	:=	Concat('0,0',udtLumgateCurrentConsumption.strVoltage);

	10..99:
		udtLumgateCurrentConsumption.strVoltage	:=	Concat('0,',udtLumgateCurrentConsumption.strVoltage);


	100..999:
		udtLumgateCurrentConsumption.strVoltage	:=	Insert(udtLumgateCurrentConsumption.strVoltage,',',1);


	1000..1070:
		udtLumgateCurrentConsumption.strVoltage	:=	Insert(udtLumgateCurrentConsumption.strVoltage,',',2);
End_Case;


If iTempValue > 0 then
	udtLumgateCurrentConsumption.iCurrent			:=	arrLumgates[iTempSelectedLumgate].iMeasurement;
Else
	udtLumgateCurrentConsumption.iCurrent			:=	0;
End_If;

(* Edit Lumgate data *)
If xEnableLumgateEdit then		(*Enable edit mode *)
	xLumgateEditable	:=	True;
	If xCancelLumgate or (iTempSelectedLumgate <> iTempSelectedLumgateOld) then	(* Exit edit mode, restore old date *)
		xLumgateEditable	:=	False;
		xEnableLumgateEdit	:=	False;
		xSafeLumgate		:=	False;
		xCancelLumgate		:=	False;
	Else
		(* Limit input values *)
		rLevelFailsafe											:=	limit_real(0.0,rLevelFailsafe,107.0);
		rManuelOutput											:=	limit_real(0.0,rManuelOutput,107.0);
		arrLumgates[iTempSelectedLumgate].iOutputLevelManual	:=	real_to_int(rManuelOutput * 10.0);
		arrLumgates[iTempSelectedLumgate].xManualRelay1			:=	udtLumgate.xManualRelay1;
		arrLumgates[iTempSelectedLumgate].xManualRelay2			:=	udtLumgate.xManualRelay2;


		arrLumgates[iTempSelectedLumgate].xAutomaticManual		:=	xSetManualLumgate;	(* Change mode directly *)


		(* Detect fail mode changes, save the mode directly *)
		If ((arrLumgates[iTempSelectedLumgate].xFailMode <> xFailMode)
			or (arrLumgates[iTempSelectedLumgate].xFailsaveRelay1 <> udtLumgate.xFailsaveRelay1)
			or (arrLumgates[iTempSelectedLumgate].xFailsaveRelay2 <> udtLumgate.xFailsaveRelay2)) then
			arrLumgates[iTempSelectedLumgate].xFailMode				:=	xFailMode;
			arrLumgates[iTempSelectedLumgate].xTeachFailsave		:=	True;
			arrLumgates[iTempSelectedLumgate].xFailsaveRelay1		:=	udtLumgate.xFailsaveRelay1;
			arrLumgates[iTempSelectedLumgate].xFailsaveRelay2		:=	udtLumgate.xFailsaveRelay2;
		End_if;


		If xSafeLumgate then	(* Exit edit mode, save changed values *)
			xLumgateEditable	:=	False;
			xEnableLumgateEdit	:=	False;
			xSafeLumgate		:=	False;
			xCancelLumgate		:=	False;

			arrLumgates[iTempSelectedLumgate].xRelay1_NC	:=	udtLumgate.xRelay1_NC;
			arrLumgates[iTempSelectedLumgate].xRelay2_NC	:=	udtLumgate.xRelay2_NC;


			If rLevelFailsafe <> int_to_real(arrLumgates[iTempSelectedLumgate].iOutputLevelFailsave / 10) then
				arrLumgates[iTempSelectedLumgate].xTeachFailsave		:=	True;
				arrLumgates[iTempSelectedLumgate].iOutputLevelFailsave	:=	real_to_int(rLevelFailsafe * 10.0);
				arrLumgates[iTempSelectedLumgate].xFailsaveRelay1		:=	udtLumgate.xFailsaveRelay1;
				arrLumgates[iTempSelectedLumgate].xFailsaveRelay2		:=	udtLumgate.xFailsaveRelay2;
			End_if;
			arrLumgates[iTempSelectedLumgate].strDescription		:=	strLumgateDescription;
		End_if;
	End_if;
Else
	iTempSelectedLumgateOld	:=	iTempSelectedLumgate;
	rManuelOutput			:=	int_to_real(arrLumgates[iTempSelectedLumgate].iOutputLevelManual / 10);
	xFailMode				:=	arrLumgates[iTempSelectedLumgate].xFailMode;
	strLumgateDescription	:=	arrLumgates[iTempSelectedLumgate].strDescription;
	xSetManualLumgate		:=	arrLumgates[iTempSelectedLumgate].xAutomaticManual;
	rLevelFailsafe			:=	int_to_real(arrLumgates[iTempSelectedLumgate].iOutputLevelFailsave / 10);


	udtLumgate.xFailsaveRelay1				:=	arrLumgates[iTempSelectedLumgate].xFailsaveRelay1;
	udtLumgate.xFailsaveRelay2				:=	arrLumgates[iTempSelectedLumgate].xFailsaveRelay2;
	udtLumgate.xRelay1_NC					:=	arrLumgates[iTempSelectedLumgate].xRelay1_NC;
	udtLumgate.xRelay2_NC					:=	arrLumgates[iTempSelectedLumgate].xRelay2_NC;
	udtLumgate.xManualRelay1				:=	arrLumgates[iTempSelectedLumgate].xManualRelay1;
	udtLumgate.xManualRelay2				:=	arrLumgates[iTempSelectedLumgate].xManualRelay1;
	udtLumgate.strDescription				:=	arrLumgates[iTempSelectedLumgate].strDescription;
	udtLumgate.xAutomaticManual				:=	arrLumgates[iTempSelectedLumgate].xAutomaticManual;
End_if;


If arrLumgates[iTempSelectedLumgate].xAutomaticManual then
	strLumgateState	:=	'Manuel mode';
Elsif arrLumgates[iTempSelectedLumgate].xTeachConsumption then
	strLumgateState	:=	'Teach current';
Elsif arrLumgates[iTempSelectedLumgate].xError then
	strLumgateState	:=	'Error';
Elsif not arrLumgates[iTempSelectedLumgate].xAutomaticManual then
	strLumgateState	:=	'Automatic mode';
End_if;

(* Current Consuption - Diagram *)
For iLoop := 1 to 22 do
	iLoop2	:=	iLoop + 1;


	(* Scale diagram *)
	If udtCurrent.xRelay1 and udtCurrent.xRelay2 then
		udtLumgateCurrentConsumption.arrCurrentValue[iLoop]   := arrLumgates[iTempSelectedLumgate].arrPowerConsumption_11[iLoop2];
	Elsif udtCurrent.xRelay1 and not udtCurrent.xRelay2 then
		udtLumgateCurrentConsumption.arrCurrentValue[iLoop]   := arrLumgates[iTempSelectedLumgate].arrPowerConsumption_10[iLoop2];
	Elsif not udtCurrent.xRelay1 and udtCurrent.xRelay2 then
		udtLumgateCurrentConsumption.arrCurrentValue[iLoop]   := arrLumgates[iTempSelectedLumgate].arrPowerConsumption_01[iLoop2];
	Elsif not udtCurrent.xRelay1 and not udtCurrent.xRelay2 then
		udtLumgateCurrentConsumption.arrCurrentValue[iLoop]   := arrLumgates[iTempSelectedLumgate].arrPowerConsumption[iLoop2];
	End_if;

	If udtLumgateCurrentConsumption.arrCurrentValue[5] < udtLumgateCurrentConsumption.arrCurrentValue[1] then
		iScaleValue	:=	real_to_int(((arrLumgates[iTempSelectedLumgate].udtCurrentCheck.rPercentageError + 120.0) / 100.0) * int_to_real(udtLumgateCurrentConsumption.arrCurrentValue[1]));
	Elsif udtLumgateCurrentConsumption.arrCurrentValue[10] < udtLumgateCurrentConsumption.arrCurrentValue[5] then
		iScaleValue	:=	real_to_int(((arrLumgates[iTempSelectedLumgate].udtCurrentCheck.rPercentageError + 120.0) / 100.0) * int_to_real(udtLumgateCurrentConsumption.arrCurrentValue[5]));
	Elsif udtLumgateCurrentConsumption.arrCurrentValue[15] < udtLumgateCurrentConsumption.arrCurrentValue[10] then
		iScaleValue	:=	real_to_int(((arrLumgates[iTempSelectedLumgate].udtCurrentCheck.rPercentageError + 120.0) / 100.0) * int_to_real(udtLumgateCurrentConsumption.arrCurrentValue[10]));
	Elsif udtLumgateCurrentConsumption.arrCurrentValue[22] < udtLumgateCurrentConsumption.arrCurrentValue[15] then
		iScaleValue	:=	real_to_int(((arrLumgates[iTempSelectedLumgate].udtCurrentCheck.rPercentageError + 120.0) / 100.0) * int_to_real(udtLumgateCurrentConsumption.arrCurrentValue[15]));
	Else
		iScaleValue	:=	real_to_int(((arrLumgates[iTempSelectedLumgate].udtCurrentCheck.rPercentageError + 100.0) / 100.0) * int_to_real(udtLumgateCurrentConsumption.arrCurrentValue[22]));
	End_if;
	If iScaleValue < 100 then		(* max current < 100 mA *)
		(* Measured current dots *)
		arrCurrentDiagram[iLoop]			:=	udtLumgateCurrentConsumption.arrCurrentValue[iLoop] * -1;
		(* current - warning deviation *)
		arrCurrentDiagramWarningMin[iLoop]	:=	real_to_int(((100.0 - arrLumgates[iTempSelectedLumgate].udtCurrentCheck.rPercentageWarning) / 100.0) * int_to_real(udtLumgateCurrentConsumption.arrCurrentValue[iLoop])) * -1;
		(* current + warning deviation *)
		arrCurrentDiagramWarningMax[iLoop]	:=	real_to_int(((arrLumgates[iTempSelectedLumgate].udtCurrentCheck.rPercentageWarning + 100.0) / 100.0) * int_to_real(udtLumgateCurrentConsumption.arrCurrentValue[iLoop])) * -1;
		(* current - alarm deviation *)
		arrCurrentDiagramAlarmMin[iLoop]	:=	real_to_int(((100.0 - arrLumgates[iTempSelectedLumgate].udtCurrentCheck.rPercentageError) / 100.0) * int_to_real(udtLumgateCurrentConsumption.arrCurrentValue[iLoop])) * -1;
		(* current + alarm deviation *)
		arrCurrentDiagramAlarmMax[iLoop]	:=	real_to_int(((arrLumgates[iTempSelectedLumgate].udtCurrentCheck.rPercentageError + 100.0) / 100.0) * int_to_real(udtLumgateCurrentConsumption.arrCurrentValue[iLoop])) * -1;
		(* actual y position *)
		iCurrentActual						:=	udtLumgateCurrentConsumption.iCurrent * -1;
		(* y scale *)
		arrScale[0]							:=	100;
		arrScale[1]							:=	100 * 4	/ 5;
		arrScale[2]							:=	100 * 3	/ 5;
		arrScale[3]							:=	100 * 2	/ 5;
		arrScale[4]							:=	100 * 1	/ 5;
	Elsif iScaleValue < 200 then	(* max current < 200 mA *)
		arrCurrentDiagram[iLoop]			:=	udtLumgateCurrentConsumption.arrCurrentValue[iLoop] / -2;
		arrCurrentDiagramWarningMin[iLoop]	:=	real_to_int(((100.0 - arrLumgates[iTempSelectedLumgate].udtCurrentCheck.rPercentageWarning) / 100.0) * int_to_real(udtLumgateCurrentConsumption.arrCurrentValue[iLoop])) / -2;
		arrCurrentDiagramWarningMax[iLoop]	:=	real_to_int(((arrLumgates[iTempSelectedLumgate].udtCurrentCheck.rPercentageWarning + 100.0) / 100.0) * int_to_real(udtLumgateCurrentConsumption.arrCurrentValue[iLoop])) / -2;
		arrCurrentDiagramAlarmMin[iLoop]	:=	real_to_int(((100.0 - arrLumgates[iTempSelectedLumgate].udtCurrentCheck.rPercentageError) / 100.0) * int_to_real(udtLumgateCurrentConsumption.arrCurrentValue[iLoop])) / -2;
		arrCurrentDiagramAlarmMax[iLoop]	:=	real_to_int(((arrLumgates[iTempSelectedLumgate].udtCurrentCheck.rPercentageError + 100.0) / 100.0) * int_to_real(udtLumgateCurrentConsumption.arrCurrentValue[iLoop])) / -2;
		iCurrentActual						:=	udtLumgateCurrentConsumption.iCurrent / -2;
		arrScale[0]							:=	200;
		arrScale[1]							:=	200 * 4	/ 5;
		arrScale[2]							:=	200 * 3	/ 5;
		arrScale[3]							:=	200 * 2	/ 5;
		arrScale[4]							:=	200 * 1	/ 5;
	Elsif iScaleValue < 300 then	(* max current < 300 mA *)
		arrCurrentDiagram[iLoop]			:=	udtLumgateCurrentConsumption.arrCurrentValue[iLoop] / -3;
		arrCurrentDiagramWarningMin[iLoop]	:=	real_to_int(((100.0 - arrLumgates[iTempSelectedLumgate].udtCurrentCheck.rPercentageWarning) / 100.0) * int_to_real(udtLumgateCurrentConsumption.arrCurrentValue[iLoop])) / -3;
		arrCurrentDiagramWarningMax[iLoop]	:=	real_to_int(((arrLumgates[iTempSelectedLumgate].udtCurrentCheck.rPercentageWarning + 100.0) / 100.0) * int_to_real(udtLumgateCurrentConsumption.arrCurrentValue[iLoop])) / -3;
		arrCurrentDiagramAlarmMin[iLoop]	:=	real_to_int(((100.0 - arrLumgates[iTempSelectedLumgate].udtCurrentCheck.rPercentageError) / 100.0) * int_to_real(udtLumgateCurrentConsumption.arrCurrentValue[iLoop])) / -3;
		arrCurrentDiagramAlarmMax[iLoop]	:=	real_to_int(((arrLumgates[iTempSelectedLumgate].udtCurrentCheck.rPercentageError + 100.0) / 100.0) * int_to_real(udtLumgateCurrentConsumption.arrCurrentValue[iLoop])) / -3;
		iCurrentActual						:=	udtLumgateCurrentConsumption.iCurrent / -3;
		arrScale[0]							:=	300;
		arrScale[1]							:=	300 * 4	/ 5;
		arrScale[2]							:=	300 * 3	/ 5;
		arrScale[3]							:=	300 * 2	/ 5;
		arrScale[4]							:=	300 * 1	/ 5;
	Elsif iScaleValue < 500 then	(* max current < 500 mA *)
		arrCurrentDiagram[iLoop]			:=	udtLumgateCurrentConsumption.arrCurrentValue[iLoop] / -5;
		arrCurrentDiagramWarningMin[iLoop]	:=	real_to_int(((100.0 - arrLumgates[iTempSelectedLumgate].udtCurrentCheck.rPercentageWarning) / 100.0) * int_to_real(udtLumgateCurrentConsumption.arrCurrentValue[iLoop])) / -5;
		arrCurrentDiagramWarningMax[iLoop]	:=	real_to_int(((arrLumgates[iTempSelectedLumgate].udtCurrentCheck.rPercentageWarning + 100.0) / 100.0) * int_to_real(udtLumgateCurrentConsumption.arrCurrentValue[iLoop])) / -5;
		arrCurrentDiagramAlarmMin[iLoop]	:=	real_to_int(((100.0 - arrLumgates[iTempSelectedLumgate].udtCurrentCheck.rPercentageError) / 100.0) * int_to_real(udtLumgateCurrentConsumption.arrCurrentValue[iLoop])) / -5;
		arrCurrentDiagramAlarmMax[iLoop]	:=	real_to_int(((arrLumgates[iTempSelectedLumgate].udtCurrentCheck.rPercentageError + 100.0) / 100.0) * int_to_real(udtLumgateCurrentConsumption.arrCurrentValue[iLoop])) / -5;
		iCurrentActual						:=	udtLumgateCurrentConsumption.iCurrent / -5;
		arrScale[0]							:=	500;
		arrScale[1]							:=	500 * 4	/ 5;
		arrScale[2]							:=	500 * 3	/ 5;
		arrScale[3]							:=	500 * 2	/ 5;
		arrScale[4]							:=	500 * 1	/ 5;
	Elsif iScaleValue < 1000 then	(* max current < 1 A *)
		arrCurrentDiagram[iLoop]			:=	udtLumgateCurrentConsumption.arrCurrentValue[iLoop] / -10;
		arrCurrentDiagramWarningMin[iLoop]	:=	real_to_int(((100.0 - arrLumgates[iTempSelectedLumgate].udtCurrentCheck.rPercentageWarning) / 100.0) * int_to_real(udtLumgateCurrentConsumption.arrCurrentValue[iLoop])) / -10;
		arrCurrentDiagramWarningMax[iLoop]	:=	real_to_int(((arrLumgates[iTempSelectedLumgate].udtCurrentCheck.rPercentageWarning + 100.0) / 100.0) * int_to_real(udtLumgateCurrentConsumption.arrCurrentValue[iLoop])) / -10;
		arrCurrentDiagramAlarmMin[iLoop]	:=	real_to_int(((100.0 - arrLumgates[iTempSelectedLumgate].udtCurrentCheck.rPercentageError) / 100.0) * int_to_real(udtLumgateCurrentConsumption.arrCurrentValue[iLoop])) / -10;
		arrCurrentDiagramAlarmMax[iLoop]	:=	real_to_int(((arrLumgates[iTempSelectedLumgate].udtCurrentCheck.rPercentageError + 100.0) / 100.0) * int_to_real(udtLumgateCurrentConsumption.arrCurrentValue[iLoop])) / -10;
		iCurrentActual						:=	udtLumgateCurrentConsumption.iCurrent / -10;
		arrScale[0]							:=	1000;
		arrScale[1]							:=	1000 * 4	/ 5;
		arrScale[2]							:=	1000 * 3	/ 5;
		arrScale[3]							:=	1000 * 2	/ 5;
		arrScale[4]							:=	1000 * 1	/ 5;
	Elsif iScaleValue < 3000 then	(* max current < 3 A *)
		arrCurrentDiagram[iLoop]			:=	udtLumgateCurrentConsumption.arrCurrentValue[iLoop] / -30;
		arrCurrentDiagramWarningMin[iLoop]	:=	real_to_int(((100.0 - arrLumgates[iTempSelectedLumgate].udtCurrentCheck.rPercentageWarning) / 100.0) * int_to_real(udtLumgateCurrentConsumption.arrCurrentValue[iLoop])) / -30;
		arrCurrentDiagramWarningMax[iLoop]	:=	real_to_int(((arrLumgates[iTempSelectedLumgate].udtCurrentCheck.rPercentageWarning + 100.0) / 100.0) * int_to_real(udtLumgateCurrentConsumption.arrCurrentValue[iLoop])) / -30;
		arrCurrentDiagramAlarmMin[iLoop]	:=	real_to_int(((100.0 - arrLumgates[iTempSelectedLumgate].udtCurrentCheck.rPercentageError) / 100.0) * int_to_real(udtLumgateCurrentConsumption.arrCurrentValue[iLoop])) / -30;
		arrCurrentDiagramAlarmMax[iLoop]	:=	real_to_int(((arrLumgates[iTempSelectedLumgate].udtCurrentCheck.rPercentageError + 100.0) / 100.0) * int_to_real(udtLumgateCurrentConsumption.arrCurrentValue[iLoop])) / -30;
		iCurrentActual						:=	udtLumgateCurrentConsumption.iCurrent / -30;
		arrScale[0]							:=	3000;
		arrScale[1]							:=	3000 * 4	/ 5;
		arrScale[2]							:=	3000 * 3	/ 5;
		arrScale[3]							:=	3000 * 2	/ 5;
		arrScale[4]							:=	3000 * 1	/ 5;
	Elsif iScaleValue < 5000 then	(* max current < 5 A *)
		arrCurrentDiagram[iLoop]			:=	udtLumgateCurrentConsumption.arrCurrentValue[iLoop] / -50;
		arrCurrentDiagramWarningMin[iLoop]	:=	real_to_int(((100.0 - arrLumgates[iTempSelectedLumgate].udtCurrentCheck.rPercentageWarning) / 100.0) * int_to_real(udtLumgateCurrentConsumption.arrCurrentValue[iLoop])) / -50;
		arrCurrentDiagramWarningMax[iLoop]	:=	real_to_int(((arrLumgates[iTempSelectedLumgate].udtCurrentCheck.rPercentageWarning + 100.0) / 100.0) * int_to_real(udtLumgateCurrentConsumption.arrCurrentValue[iLoop])) / -50;
		arrCurrentDiagramAlarmMin[iLoop]	:=	real_to_int(((100.0 - arrLumgates[iTempSelectedLumgate].udtCurrentCheck.rPercentageError) / 100.0) * int_to_real(udtLumgateCurrentConsumption.arrCurrentValue[iLoop])) / -50;
		arrCurrentDiagramAlarmMax[iLoop]	:=	real_to_int(((arrLumgates[iTempSelectedLumgate].udtCurrentCheck.rPercentageError + 100.0) / 100.0) * int_to_real(udtLumgateCurrentConsumption.arrCurrentValue[iLoop])) / -50;
		iCurrentActual						:=	udtLumgateCurrentConsumption.iCurrent / -50;
		arrScale[0]							:=	5000;
		arrScale[1]							:=	5000 * 4	/ 5;
		arrScale[2]							:=	5000 * 3	/ 5;
		arrScale[3]							:=	5000 * 2	/ 5;
		arrScale[4]							:=	5000 * 1	/ 5;
	Elsif iScaleValue < 10000 then	(* max current < 10 A *)
		arrCurrentDiagram[iLoop]			:=	udtLumgateCurrentConsumption.arrCurrentValue[iLoop] / -100;
		arrCurrentDiagramWarningMin[iLoop]	:=	real_to_int(((100.0 - arrLumgates[iTempSelectedLumgate].udtCurrentCheck.rPercentageWarning) / 100.0) * int_to_real(udtLumgateCurrentConsumption.arrCurrentValue[iLoop])) / -100;
		arrCurrentDiagramWarningMax[iLoop]	:=	real_to_int(((arrLumgates[iTempSelectedLumgate].udtCurrentCheck.rPercentageWarning + 100.0) / 100.0) * int_to_real(udtLumgateCurrentConsumption.arrCurrentValue[iLoop])) / -100;
		arrCurrentDiagramAlarmMin[iLoop]	:=	real_to_int(((100.0 - arrLumgates[iTempSelectedLumgate].udtCurrentCheck.rPercentageError) / 100.0) * int_to_real(udtLumgateCurrentConsumption.arrCurrentValue[iLoop])) / -100;
		arrCurrentDiagramAlarmMax[iLoop]	:=	real_to_int(((arrLumgates[iTempSelectedLumgate].udtCurrentCheck.rPercentageError + 100.0) / 100.0) * int_to_real(udtLumgateCurrentConsumption.arrCurrentValue[iLoop])) / -100;
		iCurrentActual						:=	udtLumgateCurrentConsumption.iCurrent / -100;
		arrScale[0]							:=	10000;
		arrScale[1]							:=	uint_to_int(uint#10000 * uint#4	/ uint#5);
		arrScale[2]							:=	10000 * 3	/ 5;
		arrScale[3]							:=	10000 * 2	/ 5;
		arrScale[4]							:=	10000 * 1	/ 5;
	End_if;
End_For;


(* Execute current consuption for a single Lumgate *)
If udtCurrent.xTeachCurrent then	(* Request curret teaching *)
	If udtCurrent.xTeachConfirmation then		(* Confirm starting current teaching *)
		udtCurrent.xTeachCurrent			:=	False;
		udtCurrent.xTeachConfirmation		:=	False;
		udtCurrent.xTeachNotConfirmation	:=	False;
		arrLumgates[iTempSelectedLumgate].xTeachConsumption	:=	True;
		strDiag												:=	'Starting current measurement';
	Elsif udtCurrent.xTeachNotConfirmation then	(* Cancel starting current teaching *)
		udtCurrent.xTeachCurrent			:=	False;
		udtCurrent.xTeachConfirmation		:=	False;
		udtCurrent.xTeachNotConfirmation	:=	False;
	End_if;
End_if;


(* Execute current consuption for all Lumgates *)
If udtCurrent.xTeachAll then
	If udtCurrent.xTeachConfirmation then
		udtCurrent.xTeachAll				:=	False;
		udtCurrent.xTeachConfirmation		:=	False;
		udtCurrent.xTeachNotConfirmation	:=	False;
		For iLoop := 1 to 240 do
			If arrLumgates[iLoop].xAvailable then
				arrLumgates[iLoop].xTeachConsumption	:=	True;
			End_if;
		End_for;
		strDiag										:=	'Starting current measurement';
	Elsif udtCurrent.xTeachNotConfirmation then
		udtCurrent.xTeachAll				:=	False;
		udtCurrent.xTeachConfirmation		:=	False;
		udtCurrent.xTeachNotConfirmation	:=	False;
	End_if;
End_if;


(* Current consuption - Edit fields *)
If udtCurrent.xEnableCurrentEdit then	(*Enable edit mode *)
	udtCurrent.xEditableCurrent		:=	True;
	If udtCurrent.xCancelCurrent or (iTempSelectedLumgate <> iTempSelectedLumgateOld) then(* Exit edit mode, restore old date *)
		udtCurrent.xEditableCurrent		:=	False;
		udtCurrent.xEnableCurrentEdit	:=	False;
		udtCurrent.xSafeCurrent			:=	False;
		udtCurrent.xCancelCurrent 		:=	False;
	Else
		(* Limit input values *)
		udtCurrent.rDeviationError		:=	limit_real(0.0,udtCurrent.rDeviationError,100.0);
		udtCurrent.rDeviationWarning	:=	limit_real(0.0,udtCurrent.rDeviationWarning,100.0);


		If udtCurrent.xSafeCurrent then	(* Exit edit mode, save changed values *)
			udtCurrent.xEditableCurrent		:=	False;
			udtCurrent.xEnableCurrentEdit	:=	False;
			udtCurrent.xSafeCurrent			:=	False;
			udtCurrent.xCancelCurrent 		:=	False;


			arrLumgates[iTempSelectedLumgate].udtCurrentCheck.xActiveCurrentCheckWarning	:=	udtCurrent.xActivateWarning;
			arrLumgates[iTempSelectedLumgate].udtCurrentCheck.rPercentageWarning			:=	udtCurrent.rDeviationWarning;
			arrLumgates[iTempSelectedLumgate].udtCurrentCheck.xActiveCurrentCheckError		:=	udtCurrent.xActivateError;
			arrLumgates[iTempSelectedLumgate].udtCurrentCheck.rPercentageError				:=	udtCurrent.rDeviationError;
		End_if;


	End_if;
Else	(* Updating values... *)
	iTempSelectedLumgateOld						:=	iTempSelectedLumgate;
	udtCurrent.xActivateWarning					:=	arrLumgates[iTempSelectedLumgate].udtCurrentCheck.xActiveCurrentCheckWarning;
	udtCurrent.xActivateError					:=	arrLumgates[iTempSelectedLumgate].udtCurrentCheck.xActiveCurrentCheckError;


	udtLumgateCurrentConsumption.xColorError	:=	arrLumgates[iTempSelectedLumgate].udtCurrentCheck.xError;
	udtLumgateCurrentConsumption.xColorWarning	:=	arrLumgates[iTempSelectedLumgate].udtCurrentCheck.xWarning;
	udtCurrent.rDeviationError					:=	arrLumgates[iTempSelectedLumgate].udtCurrentCheck.rPercentageError;
	udtCurrent.rDeviationWarning				:=	arrLumgates[iTempSelectedLumgate].udtCurrentCheck.rPercentageWarning;
End_if;


udtLumgateCurrentConsumption.strPercentageError		:=	Real_To_String(arrLumgates[iTempSelectedLumgate].udtCurrentCheck.rPercentageError,'%01.2f');
udtLumgateCurrentConsumption.strPercentageWarning	:=	Real_To_String(arrLumgates[iTempSelectedLumgate].udtCurrentCheck.rPercentageWarning,'%01.2f');
udtLumgateCurrentConsumption.iLimitMaxError			:=	arrLumgates[iTempSelectedLumgate].udtCurrentCheck.iLimitMaxError;
udtLumgateCurrentConsumption.iLimitMinError			:=	arrLumgates[iTempSelectedLumgate].udtCurrentCheck.iLimitMinError;
udtLumgateCurrentConsumption.iLimitMaxWarning		:=	arrLumgates[iTempSelectedLumgate].udtCurrentCheck.iLimitMaxWarning;
udtLumgateCurrentConsumption.iLimitMinWarning		:=	arrLumgates[iTempSelectedLumgate].udtCurrentCheck.iLimitMinWarning;


If arrLumgates[iTempSelectedLumgate].udtCurrentCheck.xActiveCurrentCheckError and arrLumgates[iTempSelectedLumgate].udtInternal.xPowerConsumptionCorrect then
	udtLumgateCurrentConsumption.strStateError		:=	'On';
Elsif not arrLumgates[iTempSelectedLumgate].udtCurrentCheck.xActiveCurrentCheckError then
	udtLumgateCurrentConsumption.strStateError		:=	'Off';
Else
	udtLumgateCurrentConsumption.strStateError		:=	'n.a.';
End_If;


If arrLumgates[iTempSelectedLumgate].udtCurrentCheck.xActiveCurrentCheckWarning and arrLumgates[iTempSelectedLumgate].udtInternal.xPowerConsumptionCorrect then
	udtLumgateCurrentConsumption.strStateWarning	:=	'On';
Elsif not arrLumgates[iTempSelectedLumgate].udtCurrentCheck.xActiveCurrentCheckWarning then
	udtLumgateCurrentConsumption.strStateWarning	:=	'Off';
Else
	udtLumgateCurrentConsumption.strStateWarning	:=	'n.a.';
End_If;

(* Edit CLO data *)
If udtCLO.xEnableCLO_Edit then	(*Enable edit mode *)
	udtCLO.xEditableCLO			:=	True;


	If udtCLO.xCancelCLO or (iTempSelectedLumgate <> iTempSelectedLumgateOld) then(* Exit edit mode, restore old date *)
		udtCLO.xEditableCLO		:=	False;
		udtCLO.xEnableCLO_Edit	:=	False;
		udtCLO.xSafeCLO			:=	False;
		udtCLO.xCancelCLO		:=	False;
	Else
		(* Limit input values *)
		udtCLO.rDecreaseCLO				:=	limit_real(0.0,udtCLO.rDecreaseCLO,20.0);
		udtCLO.rDecreaseMaintenance		:=	limit_real(0.0,udtCLO.rDecreaseMaintenance,20.0);


		arrLumgates[iTempSelectedLumgate].udtHourCounterGeneral.xReset		:=	udtCLO.xResetOperatingHours;
		arrLumgates[iTempSelectedLumgate].udtHourCounterMaintenance.xReset	:=	udtCLO.xResetMaintenanceHours;
		arrLumgates[iTempSelectedLumgate].udtHourCounter20_50.xReset		:=	udtCLO.xResetOperatingHours;
		arrLumgates[iTempSelectedLumgate].udtHourCounter50_75.xReset		:=	udtCLO.xResetOperatingHours;
		arrLumgates[iTempSelectedLumgate].udtHourCounter75_100.xReset		:=	udtCLO.xResetOperatingHours;


		If udtCLO.xSafeCLO then	(* Exit edit mode, save changed values *)
			udtCLO.xEditableCLO		:=	False;
			udtCLO.xEnableCLO_Edit	:=	False;
			udtCLO.xSafeCLO			:=	False;
			udtCLO.xCancelCLO		:=	False;


			arrLumgates[iTempSelectedLumgate].udtCloLed.xActive					:=	udtCLO.xActivateCLO;
			If udtCLO.xActivateCLO then
				arrLumgates[iTempSelectedLumgate].udtCloLed.rPercentage			:=	udtCLO.rDecreaseCLO;
				arrLumgates[iTempSelectedLumgate].udtCloLed.udiHours			:=	udtCLO.udiHoursCLO;
			Else
				arrLumgates[iTempSelectedLumgate].udtCloLed.rPercentage			:=	0.0;
				arrLumgates[iTempSelectedLumgate].udtCloLed.udiHours			:=	udint#0;
			End_if;


			arrLumgates[iTempSelectedLumgate].udtCloMaintenance.xActive			:=	udtCLO.xActivateMaintenance;
			If udtCLO.xActivateMaintenance then
				arrLumgates[iTempSelectedLumgate].udtCloMaintenance.rPercentage	:=	udtCLO.rDecreaseMaintenance;
				arrLumgates[iTempSelectedLumgate].udtCloMaintenance.udiHours	:=	udtCLO.udiHoursMaintenance;
			Else
				arrLumgates[iTempSelectedLumgate].udtCloMaintenance.rPercentage	:=	0.0;
				arrLumgates[iTempSelectedLumgate].udtCloMaintenance.udiHours	:=	udint#0;
			End_if;
		End_if;


	End_if;
Else
	iTempSelectedLumgateOld		:=	iTempSelectedLumgate;
	udtCLO.xActivateCLO			:=	arrLumgates[iTempSelectedLumgate].udtCloLed.xActive;
	udtCLO.rDecreaseCLO			:=	arrLumgates[iTempSelectedLumgate].udtCloLed.rPercentage;
	udtCLO.udiHoursCLO			:=	arrLumgates[iTempSelectedLumgate].udtCloLed.udiHours;


	udtCLO.xActivateMaintenance	:=	arrLumgates[iTempSelectedLumgate].udtCloMaintenance.xActive;
	udtCLO.rDecreaseMaintenance	:=	arrLumgates[iTempSelectedLumgate].udtCloMaintenance.rPercentage;
	udtCLO.udiHoursMaintenance	:=	arrLumgates[iTempSelectedLumgate].udtCloMaintenance.udiHours;


End_if;


If arrLumgates[iTempSelectedLumgate].udtCloLed.xActive then
	strCloState			:=	'On';
Else
	strCloState			:=	'Off';
End_If;


If arrLumgates[iTempSelectedLumgate].udtCloMaintenance.xActive then
	strMaintenanceState	:=	'On';
Else
	strMaintenanceState	:=	'Off';
End_If;

(* Web interface - Scenes *)
If udtScenes.xEnableSceneEdit then	(*Enable edit mode *)
	udtScenes.xEditableScenes	:=	True;


	If	udtScenes.xCancelScenes or (iTempSelectedLumgate <> iTempSelectedLumgateOld) then(* Exit edit mode, restore old date *)
		udtScenes.xEnableSceneEdit	:=	False;
		udtScenes.xEditableScenes	:=	False;
		udtScenes.xCancelScenes		:=	False;
		udtScenes.xSafeScenes		:=	False;
	Else
		(* Limit input values *)
		udtScenes.iScene	:=	limit_int(1,udtScenes.iScene,50);
		iTemp				:=	udtScenes.iScene;
		(* en-/ disabel scenes directly *)
		If udtScenes.xSet then
			udtScenes.xSet	:=	False;
			If arrLumgates[iTempSelectedLumgate].arrDimmingScene[iTemp].xUsed then
				arrLumgates[iTempSelectedLumgate].arrDimmingScene[iTemp].xUsed	:=	False;
			Else
				arrLumgates[iTempSelectedLumgate].arrDimmingScene[iTemp].xUsed	:=	True;
			End_if;
		End_if;


		udtScenes.iSpecialScene	:=	limit_int(1,udtScenes.iSpecialScene,15);
		iTemp					:=	udtScenes.iSpecialScene;
		(* en-/ disabel special scenes directly *)
		If udtScenes.xSetSpecial then
			udtScenes.xSetSpecial	:=	False;
			If arrLumgates[iTempSelectedLumgate].arrSpecialScene[iTemp].xUsed  then
				arrLumgates[iTempSelectedLumgate].arrSpecialScene[iTemp].xUsed	:=	False;
			Else
				arrLumgates[iTempSelectedLumgate].arrSpecialScene[iTemp].xUsed	:=	True;
			End_if;
		End_if;


		If udtScenes.xSafeScenes then(* Exit edit mode, save changed values *)
			udtScenes.xEnableSceneEdit	:=	False;
			udtScenes.xEditableScenes	:=	False;
			udtScenes.xCancelScenes		:=	False;
			udtScenes.xSafeScenes		:=	False;


			For iLoop := 1 to 50 do
				arrLumgates[iTempSelectedLumgate].arrDimmingScene[iLoop].iValue :=	limit(0,udtScenes.arrSceneValues[iLoop],107) * 10;

				(*	V2.01 changes: Editable scene relay state EPO-IS-268-1668 *)
				arrLumgates[iTempSelectedLumgate].arrDimmingScene[iLoop].xRelay1	:=	udtScenes.arrSceneRelays1[iLoop];
				arrLumgates[iTempSelectedLumgate].arrDimmingScene[iLoop].xRelay2	:=	udtScenes.arrSceneRelays2[iLoop];
			End_for;
			For iLoop := 1 to 15 do
				arrLumgates[iTempSelectedLumgate].arrSpecialScene[iLoop].iValue :=	limit(0,udtScenes.arrSpecialSceneValues[iLoop],107) * 10;


				(*	V2.01 changes: Editable scene relay state EPO-IS-268-1668 *)
				arrLumgates[iTempSelectedLumgate].arrSpecialScene[iLoop].xRelay1	:=	udtScenes.arrSpecialSceneRelays1[iLoop];
				arrLumgates[iTempSelectedLumgate].arrSpecialScene[iLoop].xRelay2	:=	udtScenes.arrSpecialSceneRelays2[iLoop];
			End_for;
		End_if;




	End_if;
Else
	iTempSelectedLumgateOld		:=	iTempSelectedLumgate;
	For iLoop := 1 to 50 do
		udtScenes.arrSceneValues[iLoop]		:=	arrLumgates[iTempSelectedLumgate].arrDimmingScene[iLoop].iValue / 10;


		(*	V2.01 changes: Editable scene relay state EPO-IS-268-1668 *)
		udtScenes.arrSceneRelays1[iLoop]	:=	arrLumgates[iTempSelectedLumgate].arrDimmingScene[iLoop].xRelay1;
		udtScenes.arrSceneRelays2[iLoop]	:=	arrLumgates[iTempSelectedLumgate].arrDimmingScene[iLoop].xRelay2;
	End_for;
	For iLoop := 1 to 15 do
		udtScenes.arrSpecialSceneValues[iLoop] :=	arrLumgates[iTempSelectedLumgate].arrSpecialScene[iLoop].iValue / 10;


		(*	V2.01 changes: Editable scene relay state EPO-IS-268-1668 *)
		udtScenes.arrSpecialSceneRelays1[iLoop]	:=	arrLumgates[iTempSelectedLumgate].arrSpecialScene[iLoop].xRelay1;
		udtScenes.arrSpecialSceneRelays2[iLoop]	:=	arrLumgates[iTempSelectedLumgate].arrSpecialScene[iLoop].xRelay2;
	End_for;
End_if;


(* Web interface - Scene Hour Counter *)
If udtScenesHours.xEnableSceneEdit then	(*Enable edit mode *)
	udtScenesHours.xEditableScenes	:=	True;


	(* Limit input values *)
	udtScenesHours.iScene	:=	limit_int(1,udtScenesHours.iScene,50);
	iTemp					:=	udtScenesHours.iScene;


	(* Reset a counter directly *)
	If udtScenesHours.xSet then
		udtScenesHours.xSet	:=	False;
		arrSceneHourCounter[iTemp].iMinutes	:=	0;
		arrSceneHourCounter[iTemp].iSeconds	:=	0;
		arrSceneHourCounter[iTemp].udiHours	:=	udint#0;
	End_if;


	(* no function... *)
	If udtScenesHours.xSafeScenes then
		udtScenesHours.xEnableSceneEdit	:=	False;
		udtScenesHours.xEditableScenes	:=	False;
		udtScenesHours.xCancelScenes	:=	False;
		udtScenesHours.xSafeScenes		:=	False;
	End_if;
	(* no function... *)
	If	udtScenesHours.xCancelScenes then
		udtScenesHours.xEnableSceneEdit	:=	False;
		udtScenesHours.xEditableScenes	:=	False;
		udtScenesHours.xCancelScenes	:=	False;
		udtScenesHours.xSafeScenes		:=	False;
	End_if;
Else
	udtScenesHours.xSet				:=	False;
	udtScenesHours.xEditableScenes	:=	False;
	udtScenesHours.xCancelScenes	:=	False;
	udtScenesHours.xSafeScenes		:=	False;
End_if;
udtScenesHours.arrSceneHours :=	arrSceneHourCounter;

R_TRIG_Config1(CLK	:=	udtThreshold.xActivateConfig1);
R_TRIG_Config2(CLK	:=	udtThreshold.xActivateConfig2);
R_TRIG_Config3(CLK	:=	udtThreshold.xActivateConfig3);
R_TRIG_Config4(CLK	:=	udtThreshold.xActivateConfig4);


(* Display message "Table is not active", if an inactive table is selected *)
If udtThreshold.iViewConfig = 1 and udtThreshold.xActivateConfig1 then
	udtThreshold.xTableNotActive	:=	False;
Elsif udtThreshold.iViewConfig = 2 and udtThreshold.xActivateConfig2 then
	udtThreshold.xTableNotActive	:=	False;
Elsif udtThreshold.iViewConfig = 3 and udtThreshold.xActivateConfig3 then
	udtThreshold.xTableNotActive	:=	False;
Elsif udtThreshold.iViewConfig = 4 and udtThreshold.xActivateConfig4 then
	udtThreshold.xTableNotActive	:=	False;
Else
	udtThreshold.xTableNotActive	:=	True;
End_if;


udtThreshold.iViewConfig	:=	limit_int(1,udtThreshold.iViewConfig,4);


If udtThreshold.xEnableThresholdEdit then	(*Enable edit mode *)
	udtThreshold.xEditableThreshold	:=	True;


	If	udtThreshold.xCancelThreshold or (iSelectSensor <> iSelectSensorOld) then(* Exit edit mode, restore old date *)
		udtThreshold.xEditableThreshold		:=	False;
		udtThreshold.xCancelThreshold		:=	False;
		udtThreshold.xEnableThresholdEdit	:=	False;
		udtThreshold.xSafeThreshold			:=	False;
	Else
		(* move threshold slider to edit scene options *)
		If udtThreshold.xIncrease and iSelectedScenario < 25 then
			udtThreshold.xIncrease	:=	False;
			iSelectedScenario		:=	iSelectedScenario + 1;
			arrSelectedScenario		:=	arrScenarioConfiguration[iSelectSensor][iSelectedScenario];
		End_if;
		If udtThreshold.xDecrease and iSelectedScenario > 1 then
			udtThreshold.xDecrease	:=	False;
			iSelectedScenario		:=	iSelectedScenario - 1;
			arrSelectedScenario		:=	arrScenarioConfiguration[iSelectSensor][iSelectedScenario];
		End_if;


		(* Select scene options *)
		If iSelectedScenario > 0 and iSelectedScenario < 26 then
			arrScenarioConfiguration[iSelectSensor][iSelectedScenario]	:=	arrSelectedScenario;
		End_if;


		(* Limit input values *)
		udtThreshold.iThreshold		:=	limit_int(1,udtThreshold.iThreshold,25);
		iTemp						:=	udtThreshold.iThreshold;
		For iLoop := 1 to 24 do
			arrSelectedSensor[iLoop]	:=	limit_real(1.0,arrSelectedSensor[iLoop],32768.0);
		End_for;


		(* Interlock to activate only one threshold configuration *)
		If R_TRIG_Config1.Q then
			udtThreshold.xActivateConfig1	:=	True;
			udtThreshold.xActivateConfig2	:=	False;
			udtThreshold.xActivateConfig3	:=	False;
			udtThreshold.xActivateConfig4	:=	False;
		Elsif R_TRIG_Config2.Q then
			udtThreshold.xActivateConfig1	:=	False;
			udtThreshold.xActivateConfig2	:=	True;
			udtThreshold.xActivateConfig3	:=	False;
			udtThreshold.xActivateConfig4	:=	False;
		Elsif R_TRIG_Config3.Q then
			udtThreshold.xActivateConfig1	:=	False;
			udtThreshold.xActivateConfig2	:=	False;
			udtThreshold.xActivateConfig3	:=	True;
			udtThreshold.xActivateConfig4	:=	False;
		Elsif R_TRIG_Config4.Q then
			udtThreshold.xActivateConfig1	:=	False;
			udtThreshold.xActivateConfig2	:=	False;
			udtThreshold.xActivateConfig3	:=	False;
			udtThreshold.xActivateConfig4	:=	True;
		End_if;

		(* en-/ disable a threshold directly *)
		If udtThreshold.xSet then
			udtThreshold.xSet	:=	False;
			If arrLevelConfiguration[iSelectSensor].arrScenarioActive[iTemp] then
				arrLevelConfiguration[iSelectSensor].arrScenarioActive[iTemp]	:=	False;
			Else
				arrLevelConfiguration[iSelectSensor].arrScenarioActive[iTemp]	:=	True;
			End_if;
		End_if;


		If udtThreshold.xSafeThreshold then	(* Exit edit mode, save changed values *)
			udtThreshold.xEditableThreshold		:=	False;
			udtThreshold.xCancelThreshold		:=	False;
			udtThreshold.xEnableThresholdEdit	:=	False;
			udtThreshold.xSafeThreshold			:=	False;


			arrLevelConfiguration[iSelectSensor].xActiveConfig1	:=	udtThreshold.xActivateConfig1;
			arrLevelConfiguration[iSelectSensor].xActiveConfig2	:=	udtThreshold.xActivateConfig2;
			arrLevelConfiguration[iSelectSensor].xActiveConfig3	:=	udtThreshold.xActivateConfig3;
			arrLevelConfiguration[iSelectSensor].xActiveConfig4	:=	udtThreshold.xActivateConfig4;


			arrLevelConfiguration[iSelectSensor].strName1	:=	udtThreshold.strName1;
			arrLevelConfiguration[iSelectSensor].strName2	:=	udtThreshold.strName2;
			arrLevelConfiguration[iSelectSensor].strName3	:=	udtThreshold.strName3;
			arrLevelConfiguration[iSelectSensor].strName4	:=	udtThreshold.strName4;

			udtScenarioMode.arrManualScene[iSelectSensor]	:=	udtThreshold.iManualScene;


			If udtThreshold.iViewConfig = 1 then
				For iLoop := 1 to 24 do
					arrLevelConfiguration[iSelectSensor].arrScenarioConfig1[iLoop]	:=	real_to_int(arrSelectedSensor[iLoop]);
				End_for;
			Elsif udtThreshold.iViewConfig = 2 then
				For iLoop := 1 to 24 do
					arrLevelConfiguration[iSelectSensor].arrScenarioConfig2[iLoop]	:=	real_to_int(arrSelectedSensor[iLoop]);
				End_for;
			Elsif udtThreshold.iViewConfig = 3 then
				For iLoop := 1 to 24 do
					arrLevelConfiguration[iSelectSensor].arrScenarioConfig3[iLoop]	:=	real_to_int(arrSelectedSensor[iLoop]);
				End_for;
			Elsif udtThreshold.iViewConfig = 4 then
				For iLoop := 1 to 24 do
					arrLevelConfiguration[iSelectSensor].arrScenarioConfig4[iLoop]	:=	real_to_int(arrSelectedSensor[iLoop]);
				End_for;
			End_if;
		End_if;
	End_if;
Else
	iSelectSensorOld	:=	iSelectSensor;
	udtThreshold.iManualScene	:=	udtScenarioMode.arrManualScene[iSelectSensor];


	If iSelectSensor > 0 and iSelectSensor < 5 then
		arrActiveScenario	:=	arrLevelConfiguration[iSelectSensor].arrScenarioActive; 
		iSelectedScene		:=	arrLumgates[iTempSelectedLumgate].iActualScene;
		(* iSelectedScene		:=	udtScenarioMode.arrActualScene[iSelectSensor]; *)
		iSelectedScenario	:=	udtScenarioMode.arrScenario[iSelectSensor];
		iChosedScene		:=	udtScenarioMode.arrScenarioOption[iSelectSensor];
	End_if;


	(* Select sensor *)
	If iSelectSensor > 0 and iSelectSensor < 5 then
		rAnalogValue		:=	arrAnalogValues[iSelectSensor].rValue;
		wAnalogDiag			:=	arrAnalogValues[iSelectSensor].wDiagCode;
		arrAnalogDiag[1]	:=	arrAnalogValues[1].wDiagCode;
		arrAnalogDiag[2]	:=	arrAnalogValues[2].wDiagCode;
		arrAnalogDiag[3]	:=	arrAnalogValues[3].wDiagCode;
		arrAnalogDiag[4]	:=	arrAnalogValues[4].wDiagCode;


		(* Select Threshold configuration (speed level) *)
		arrActiveScenario				:=	arrLevelConfiguration[iSelectSensor].arrScenarioActive; 
		If udtThreshold.iViewConfig = 1 then
			For iLoop := 1 to 24 do
				arrSelectedSensor[iLoop]	:=	int_to_real(arrLevelConfiguration[iSelectSensor].arrScenarioConfig1[iLoop]);
			End_for;
		Elsif udtThreshold.iViewConfig = 2 then
			For iLoop := 1 to 24 do
				arrSelectedSensor[iLoop]	:=	int_to_real(arrLevelConfiguration[iSelectSensor].arrScenarioConfig2[iLoop]);
			End_for;
		Elsif udtThreshold.iViewConfig = 3 then
			For iLoop := 1 to 24 do
				arrSelectedSensor[iLoop]	:=	int_to_real(arrLevelConfiguration[iSelectSensor].arrScenarioConfig3[iLoop]);
			End_for;
		Elsif udtThreshold.iViewConfig = 4 then
			For iLoop := 1 to 24 do
				arrSelectedSensor[iLoop]	:=	int_to_real(arrLevelConfiguration[iSelectSensor].arrScenarioConfig4[iLoop]);
			End_for;
		End_if;
		arrSelectedConfiguration		:=	arrScenarioConfiguration[iSelectSensor];
		If iSelectedScenario > 0 and iSelectedScenario < 26 then
			arrSelectedScenario			:=	arrScenarioConfiguration[iSelectSensor][iSelectedScenario];
		Else
			For iLoop := 1 to 5 do
				arrSelectedScenario[iLoop]	:=	0;
			End_for;
		End_if;
	End_if;


	udtThreshold.xActivateConfig1	:=	arrLevelConfiguration[iSelectSensor].xActiveConfig1;
	udtThreshold.xActivateConfig2	:=	arrLevelConfiguration[iSelectSensor].xActiveConfig2;
	udtThreshold.xActivateConfig3	:=	arrLevelConfiguration[iSelectSensor].xActiveConfig3;
	udtThreshold.xActivateConfig4	:=	arrLevelConfiguration[iSelectSensor].xActiveConfig4;


	udtThreshold.strName1			:=	arrLevelConfiguration[iSelectSensor].strName1;
	udtThreshold.strName2			:=	arrLevelConfiguration[iSelectSensor].strName2;
	udtThreshold.strName3			:=	arrLevelConfiguration[iSelectSensor].strName3;
	udtThreshold.strName4			:=	arrLevelConfiguration[iSelectSensor].strName4;
End_if;


If iSelectSensor > 0 and iSelectSensor < 5 then
	arrActiveScenario	:=	arrLevelConfiguration[iSelectSensor].arrScenarioActive; 
End_if;


iDelay	:=	arrLevelConfiguration[1].iDelay;

(* Web interface - select source *)
If udtSource.xEnableSourceEdit then	(*Enable edit mode *)
	udtSource.xEditableSource	:=	True;


	(* Limit input values *)
	udtSource.iLimit1	:=	limit(1,udtSource.iLimit1,4);
	udtSource.iLimit2	:=	limit(1,udtSource.iLimit2,4);
	udtSource.iLimit3	:=	limit(1,udtSource.iLimit3,4);
	udtSource.iLimit4	:=	limit(1,udtSource.iLimit4,4);


	If udtSource.xSaveSource then		(* Exit edit mode, save changed values *)
		udtSource.xEditableSource		:=	False;
		udtSource.xSaveSource			:=	False;
		udtSource.xCancelSource			:=	False;
		udtSource.xEnableSourceEdit		:=	False;


		arrLevelConfiguration[1].iInputValue	:=	udtSource.iSelect1;
		arrLevelConfiguration[2].iInputValue	:=	udtSource.iSelect2;
		arrLevelConfiguration[3].iInputValue	:=	udtSource.iSelect3;
		arrLevelConfiguration[4].iInputValue	:=	udtSource.iSelect4;


		udtL20Config.iBackup_1_2_3_4_Limit		:=	udtSource.iLimit1;
		udtL20Config.iBackup_2_3_4_1_Limit		:=	udtSource.iLimit2;
		udtL20Config.iBackup_3_4_1_2_Limit		:=	udtSource.iLimit3;
		udtL20Config.iBackup_4_1_2_3_Limit		:=	udtSource.iLimit4;

		udtL20Config.rMaxCd1					:=	udint_to_real(udtSource.udiMaxValue1);
		udtL20Config.rMaxCd2					:=	udint_to_real(udtSource.udiMaxValue2);
		udtL20Config.rMaxCd3					:=	udint_to_real(udtSource.udiMaxValue3);
		udtL20Config.rMaxCd4					:=	udint_to_real(udtSource.udiMaxValue4);


		strUnit							:=	udtSource.strUnit;
	End_if;


	If	udtSource.xCancelSource then	(* Exit edit mode, restore old date *)
		udtSource.xEditableSource		:=	False;
		udtSource.xSaveSource			:=	False;
		udtSource.xCancelSource			:=	False;
		udtSource.xEnableSourceEdit		:=	False;
	End_if;
Else
	udtSource.iSelect1		:=	arrLevelConfiguration[1].iInputValue;
	udtSource.iSelect2		:=	arrLevelConfiguration[2].iInputValue;
	udtSource.iSelect3		:=	arrLevelConfiguration[3].iInputValue;
	udtSource.iSelect4		:=	arrLevelConfiguration[4].iInputValue;
	udtSource.iLimit1		:=	udtL20Config.iBackup_1_2_3_4_Limit;
	udtSource.iLimit2		:=	udtL20Config.iBackup_2_3_4_1_Limit;
	udtSource.iLimit3		:=	udtL20Config.iBackup_3_4_1_2_Limit;
	udtSource.iLimit4		:=	udtL20Config.iBackup_4_1_2_3_Limit;
	udtSource.udiMaxValue1	:=	real_to_udint(udtL20Config.rMaxCd1);
	udtSource.udiMaxValue2	:=	real_to_udint(udtL20Config.rMaxCd2);
	udtSource.udiMaxValue3	:=	real_to_udint(udtL20Config.rMaxCd3);
	udtSource.udiMaxValue4	:=	real_to_udint(udtL20Config.rMaxCd4);
	udtSource.strUnit		:=	strUnit;
End_if;

(* Web interface - system settings *)
If udtProject.xEnableProjectEdit then	(*Enable edit mode *)
	udtProject.xEditableProject	:=	True;


	(* Limit input values *)
	udtProject.diAverageDelay	:=	limit_dint(dint#1,udtProject.diAverageDelay,dint#1000);
	udtProject.iHysteric		:=	limit(1,udtProject.iHysteric,10);
	udtProject.iSwitchDelay		:=	limit(1,udtProject.iSwitchDelay,3600);
	udtProject.iDimmingSpeed	:=	limit(1,udtProject.iDimmingSpeed,10); (* In Visu wrong variable... *)


	If udtProject.xSafeAllFailStates then	(* execute failsafe teaching *)
		udtProject.xSafeAllFailStates	:=	False;
		strDiag	:=	'Start failsafe teaching';


		For iLoop := 1 to 240 do
			arrLumgates[iLoop].xTeachFailsave	:=	True;
		End_for;
	End_if;


	If udtProject.xSaveProject then		(* Exit edit mode, save changed values *)
		udtProject.xEditableProject		:=	False;
		udtProject.xSaveProject			:=	False;
		udtProject.xCancelProject		:=	False;
		udtProject.xEnableProjectEdit	:=	False;


		arrLevelConfiguration[1].iDelay			:=	udtProject.iSwitchDelay;
		arrLevelConfiguration[2].iDelay			:=	udtProject.iSwitchDelay;
		arrLevelConfiguration[3].iDelay			:=	udtProject.iSwitchDelay;
		arrLevelConfiguration[4].iDelay			:=	udtProject.iSwitchDelay;
		arrLevelConfiguration[1].iHysteric		:=	udtProject.iHysteric;
		arrLevelConfiguration[2].iHysteric		:=	udtProject.iHysteric;
		arrLevelConfiguration[3].iHysteric		:=	udtProject.iHysteric;
		arrLevelConfiguration[4].iHysteric		:=	udtProject.iHysteric;
		arrLevelConfiguration[1].diAverageTime	:=	udtProject.diAverageDelay;
		arrLevelConfiguration[2].diAverageTime	:=	udtProject.diAverageDelay;
		arrLevelConfiguration[3].diAverageTime	:=	udtProject.diAverageDelay;
		arrLevelConfiguration[4].diAverageTime	:=	udtProject.diAverageDelay;
	End_if;


	If	udtProject.xCancelProject then	(* Exit edit mode, restore old date *)
		udtProject.xEditableProject		:=	False;
		udtProject.xSaveProject			:=	False;
		udtProject.xCancelProject		:=	False;
		udtProject.xEnableProjectEdit	:=	False;
	End_if;
Else
	udtProject.iSwitchDelay		:=	arrLevelConfiguration[1].iDelay;
	udtProject.iHysteric		:=	arrLevelConfiguration[1].iHysteric;
	udtProject.diAverageDelay	:=	arrLevelConfiguration[1].diAverageTime;
End_if;


F_TRIG_Failsafe(CLK:=arrLumgates[1].xTeachFailsave);
If F_TRIG_Failsafe.Q then
	strDiag	:=	'Failsafe teaching done';
End_if;

TON_Timeout(PT:=t#30s);
TON_SceneDelay(IN:=((arrExternalDataIn[1] <> bOldByte1) or (arrExternalDataIn[2] <> bOldByte2)
or (arrExternalDataIn[3] <> bOldByte3) or (arrExternalDataIn[4] <> bOldByte4)
or (arrExternalDataIn[5] <> bOldByte5)),PT:=t#0.1s);


(* TLS_Communication *)
If xActivate then
	xActive	:=	True;
	(* Life bit from external system *)
	xLifeBit			:=	arrExternalDataIn[0].X0;
	TON_Timeout.IN		:=	(xLifeBit = xOldLifeBit);


	If xLifeBit <> xOldLifeBit then
		xOldLifeBit	:=	xLifeBit;
		strDiag	:=	'New data received from upper system';
		xDataValid	:=	True;
	Elsif TON_Timeout.Q then
		xDataValid	:=	False;
		xError	:=	True;
		strDiag	:=	'Error: No incoming data from upper system';
	End_if;


	If xError then
		If xConfirm then
			If xDataValid then
				strDiag	:=	'TCS communication error confirmed';
				xError	:=	False;
			Else
				strDiag	:=	'TCS communication error is still active';
			End_if;
		End_if;
	End_if;
	(* Activate dimming scenarios *)
	iTempLocation				:=	0;
	xErrorTooMuchScenarioActive	:= 	False;




	If TON_SceneDelay.Q then


		udtScenarioMode.arrExternalInputScene[1]	:=	0;
		udtScenarioMode.arrExternalInputScene[2]	:=	0;
		udtScenarioMode.arrExternalInputScene[3]	:=	0;
		udtScenarioMode.arrExternalInputScene[4]	:=	0;


		bOldByte1	:=	arrExternalDataIn[1];
		bOldByte2	:=	arrExternalDataIn[2];
		bOldByte3	:=	arrExternalDataIn[3];
		bOldByte4	:=	arrExternalDataIn[4];
		bOldByte5	:=	arrExternalDataIn[5];
		For iLoop := 0 to 5 do
			iLoop2	:= 	iLoop + 1;
			If arrExternalDataIn[iLoop2].X0 then
				iTempLocation	:=	iTempLocation + 1;
				If iTempLocation < 5 then
					udtScenarioMode.arrExternalInputScene[iTempLocation]	:= 	iLoop * 8 + 1;
				End_If;
			End_If;
			If arrExternalDataIn[iLoop2].X1 Then
				iTempLocation	:= 	iTempLocation + 1;
				If iTempLocation < 5 Then
					udtScenarioMode.arrExternalInputScene[iTempLocation]	:= 	iLoop * 8 + 2;
				End_If;
			End_If;
			If arrExternalDataIn[iLoop2].X2 Then
				iTempLocation	:= 	iTempLocation + 1;
				If iTempLocation < 5 Then
					udtScenarioMode.arrExternalInputScene[iTempLocation]	:= 	iLoop * 8 + 3;
				End_If;
			End_If;
			If arrExternalDataIn[iLoop2].X3 Then
				iTempLocation	:= 	iTempLocation + 1;
				If iTempLocation < 5 Then
					udtScenarioMode.arrExternalInputScene[iTempLocation]	:= 	iLoop * 8 + 4;
				End_If;
			End_If;
			If arrExternalDataIn[iLoop2].X4 Then
				iTempLocation	:= 	iTempLocation + 1;
				If iTempLocation < 5 Then
					udtScenarioMode.arrExternalInputScene[iTempLocation]	:= 	iLoop * 8 + 5;
				End_If;
			End_If;
			If arrExternalDataIn[iLoop2].X5 Then
				iTempLocation	:= 	iTempLocation + 1;
				If iTempLocation < 5 Then
					udtScenarioMode.arrExternalInputScene[iTempLocation]	:= 	iLoop * 8 + 6;
				End_If;
			End_If;
			If arrExternalDataIn[iLoop2].X6 Then
				iTempLocation	:= 	iTempLocation + 1;
				If iTempLocation < 5 Then
					udtScenarioMode.arrExternalInputScene[iTempLocation]	:= 	iLoop * 8 + 7;
				End_If;
			End_If;
			If arrExternalDataIn[iLoop2].X7 Then
				iTempLocation	:= 	iTempLocation + 1;
				If iTempLocation < 5 Then
					udtScenarioMode.arrExternalInputScene[iTempLocation]	:= 	iLoop * 8 + 8;
				End_If;
			End_If;
		End_For;


		If arrExternalDataIn[7].X0 Then
			iTempLocation	:= 	iTempLocation + 1;
			If iTempLocation < 5 Then
				udtScenarioMode.arrExternalInputScene[iTempLocation]	:= 	49;
			End_If;
		End_If;
		If arrExternalDataIn[7].X1 Then
			iTempLocation	:= 	iTempLocation + 1;
			If iTempLocation < 5 Then
				udtScenarioMode.arrExternalInputScene[iTempLocation]	:= 	50;
			End_If;
		End_If;


		If iTempLocation > 4 then
			xErrorTooMuchScenarioActive	:= 	True;
			strDiag	:=	'Error: too much scenes active';
		End_If;
	End_if;


	(* Activate special scenarios *)
	wSpecialScenario.B0	:=	arrExternalDataIn[8];
	wSpecialScenario.B1	:=	arrExternalDataIn[9];


	(* Select scenario hour counter (1-50) *)
	bSelectScenarioHourCounter	:=	arrExternalDataIn[10];


	(* Select data from one Lumgate *)
	bSelectDataLumgate	:=	arrExternalDataIn[11];


	(* 2: System is Modbus connected, own inputs are used, different threshold columns can be used *)
	(* 3: System is Profinet connected, own inputs are used, different threshold columns can be used *)
	If udtNetworkConfiguration.iMode = 2 or udtNetworkConfiguration.iMode = 3 then


		(* Threshold table 1 *)
		udtLevelConfigurationL20_1.xActive	:= arrExternalDataIn[15].X0;


		bTemp		:= Byte#0;
		bTemp.X0	:= arrExternalDataIn[15].X1;
		bTemp.X1	:= arrExternalDataIn[15].X2;
		bTemp.X2	:= arrExternalDataIn[15].X3;


		iTemp		:= Byte_To_Int(bTemp);


		udtLevelConfigurationL20_1.xActiveConfig1		:= False;
		udtLevelConfigurationL20_1.xActiveConfig2		:= False;
		udtLevelConfigurationL20_1.xActiveConfig3		:= False;
		udtLevelConfigurationL20_1.xActiveConfig4		:= False;


		Case iTemp Of
		1:	udtLevelConfigurationL20_1.xActiveConfig1	:= True;
		2:	udtLevelConfigurationL20_1.xActiveConfig2	:= True;
		3:	udtLevelConfigurationL20_1.xActiveConfig3	:= True;
		4:	udtLevelConfigurationL20_1.xActiveConfig4	:= True;
		Else
			udtLevelConfigurationL20_1.xActiveConfig1	:= True;
		End_Case;


		(* Threshold table 2 *)
		udtLevelConfigurationL20_2.xActive	:= arrExternalDataIn[16].X0;


		bTemp		:= Byte#0;
		bTemp.X0	:= arrExternalDataIn[16].X1;
		bTemp.X1	:= arrExternalDataIn[16].X2;
		bTemp.X2	:= arrExternalDataIn[16].X3;


		iTemp		:= Byte_To_Int(bTemp);


		udtLevelConfigurationL20_2.xActiveConfig1		:= False;
		udtLevelConfigurationL20_2.xActiveConfig2		:= False;
		udtLevelConfigurationL20_2.xActiveConfig3		:= False;
		udtLevelConfigurationL20_2.xActiveConfig4		:= False;


		Case iTemp Of
		1:	udtLevelConfigurationL20_2.xActiveConfig1	:= True;
		2:	udtLevelConfigurationL20_2.xActiveConfig2	:= True;
		3:	udtLevelConfigurationL20_2.xActiveConfig3	:= True;
		4:	udtLevelConfigurationL20_2.xActiveConfig4	:= True;
		Else
			udtLevelConfigurationL20_2.xActiveConfig1	:= True;
		End_Case;


		(* Threshold table 3 *)
		udtLevelConfigurationL20_3.xActive	:= arrExternalDataIn[17].X0;


		bTemp		:= Byte#0;
		bTemp.X0	:= arrExternalDataIn[17].X1;
		bTemp.X1	:= arrExternalDataIn[17].X2;
		bTemp.X2	:= arrExternalDataIn[17].X3;


		iTemp		:= Byte_To_Int(bTemp);


		udtLevelConfigurationL20_3.xActiveConfig1		:= False;
		udtLevelConfigurationL20_3.xActiveConfig2		:= False;
		udtLevelConfigurationL20_3.xActiveConfig3		:= False;
		udtLevelConfigurationL20_3.xActiveConfig4		:= False;


		Case iTemp Of
		1:	udtLevelConfigurationL20_3.xActiveConfig1	:= True;
		2:	udtLevelConfigurationL20_3.xActiveConfig2	:= True;
		3:	udtLevelConfigurationL20_3.xActiveConfig3	:= True;
		4:	udtLevelConfigurationL20_3.xActiveConfig4	:= True;
		Else
			udtLevelConfigurationL20_3.xActiveConfig1	:= True;
		End_Case;


		(* Threshold table 4 *)
		udtLevelConfigurationL20_4.xActive	:= arrExternalDataIn[18].X0;


		bTemp		:= Byte#0;
		bTemp.X0	:= arrExternalDataIn[18].X1;
		bTemp.X1	:= arrExternalDataIn[18].X2;
		bTemp.X2	:= arrExternalDataIn[18].X3;


		iTemp		:= Byte_To_Int(bTemp);


		udtLevelConfigurationL20_4.xActiveConfig1		:= False;
		udtLevelConfigurationL20_4.xActiveConfig2		:= False;
		udtLevelConfigurationL20_4.xActiveConfig3		:= False;
		udtLevelConfigurationL20_4.xActiveConfig4		:= False;


		Case iTemp Of
		1:	udtLevelConfigurationL20_4.xActiveConfig1	:= True;
		2:	udtLevelConfigurationL20_4.xActiveConfig2	:= True;
		3:	udtLevelConfigurationL20_4.xActiveConfig3	:= True;
		4:	udtLevelConfigurationL20_4.xActiveConfig4	:= True;
		Else
			udtLevelConfigurationL20_4.xActiveConfig1	:= True;
		End_Case;
	End_If;
Else
	xActive	:=	False;
	xError	:=	False;
End_if;

(* TLS_Communication *)
If xActivate then
	(* Boot project available, boot and ram are equal *)
	arrExternalDataOut[0].X0	:=	PLC_HAS_PRJ_BOOT > Byte#0 and (PLC_CRC_PRJ_BOOT = PLC_CRC_PRJ_RAM);


	(* CPU load warning *)
	arrExternalDataOut[0].X1	:=	COP_CPU_LOAD_WARNING;


	(* Breakpoint set *)
	arrExternalDataOut[0].X2	:=	PLCDEBUG_BPSET;


	(* Clock pulse 1 second *)
	arrExternalDataOut[0].X3	:=	CLOCK_PULSE_1S;


	(* Peripheral fault *)
	arrExternalDataOut[0].X4	:=	MASTER_DIAG_STATUS_REG_PF;


	(* Data transmission active *)
	arrExternalDataOut[0].X5	:=	MASTER_DIAG_STATUS_REG_RUN;


	(* Life bit (copy from input register byte 0 bit 0) *)
	arrExternalDataOut[0].X6	:=	xLifeBit;


	(* Clear analog values *)


	For iLoop 	:=	1 to 8 do
		arrExternalDataOut[iLoop]	:=	Byte#0;
	End_For;


	(* Analog input channel 1 to 4 *)
	For iLoop 	:=	1 to 4 do
		iLoop3	:=	iLoop * 2 - 1;
		iLoop2	:=	iLoop * 2;


	(*	If arrInputsL20[iLoop].xActive then	*)
			If iLoop = 1 and udtL20Config.rMaxCd1 > 0.0 then
				rTempValue					:=	Limit_Real(0.0,arrInputsL20[iLoop].rValue / udtL20Config.rMaxCd1 * 1000.0,1000.0);
			Elsif iLoop = 2 and udtL20Config.rMaxCd2 > 0.0 then
				rTempValue					:=	Limit_Real(0.0,arrInputsL20[iLoop].rValue / udtL20Config.rMaxCd2 * 1000.0,1000.0);
			Elsif iLoop = 3 and udtL20Config.rMaxCd3 > 0.0 then
				rTempValue					:=	Limit_Real(0.0,arrInputsL20[iLoop].rValue / udtL20Config.rMaxCd3 * 1000.0,1000.0);
			Elsif iLoop = 4 and udtL20Config.rMaxCd4 > 0.0 then
				rTempValue					:=	Limit_Real(0.0,arrInputsL20[iLoop].rValue / udtL20Config.rMaxCd4 * 1000.0,1000.0);
			End_if;


			wTempValue						:=	Real_To_Word(rTempValue);
			arrExternalDataOut[iLoop3]		:=	wTempValue.B0;
			arrExternalDataOut[iLoop2].X0	:=	wTempValue.X8;
			arrExternalDataOut[iLoop2].X1	:=	wTempValue.X9;
			arrExternalDataOut[iLoop2].X2	:=	arrInputsL20[iLoop].wDiagCode.X0;
			arrExternalDataOut[iLoop2].X3	:=	arrInputsL20[iLoop].wDiagCode.X1;
			arrExternalDataOut[iLoop2].X7	:=	arrInputsL20[iLoop].xActive;

	(*	End_If;	*)
	End_For;


	(* Active dimming scenarios *)
	arrExternalDataOut[10]	:=	Int_To_Byte(udtScenarioMode.arrActualScene[1]);
	arrExternalDataOut[11]	:=	Int_To_Byte(udtScenarioMode.arrActualScene[2]);
	arrExternalDataOut[12]	:=	Int_To_Byte(udtScenarioMode.arrActualScene[3]);
	arrExternalDataOut[13]	:=	Int_To_Byte(udtScenarioMode.arrActualScene[4]);


	(* Active special scenarios *)
	arrExternalDataOut[15]	:=	wSpecialScenario.B0;
	arrExternalDataOut[16]	:=	wSpecialScenario.B1; 


	(* Select scenario hour counter *)
	arrExternalDataOut[20]		:=	bSelectScenarioHourCounter;
	iSelectScenarioHourCounter	:=	Byte_To_Int(bSelectScenarioHourCounter);
	iSelectScenarioHourCounter	:=	Limit_Int(1,iSelectScenarioHourCounter,50);


	dwTempValueHours			:=	Udint_To_Dword(arrScenarioHourCounter[iSelectScenarioHourCounter].udiHours);


	arrExternalDataOut[21]		:=	dwTempValueHours.B0;
	arrExternalDataOut[22]		:=	dwTempValueHours.B1;
	arrExternalDataOut[23]		:=	dwTempValueHours.B2;
	arrExternalDataOut[24]		:=	dwTempValueHours.B3;


	arrExternalDataOut[25]		:=	Int_to_Byte(arrScenarioHourCounter[iSelectScenarioHourCounter].iMinutes);
	arrExternalDataOut[26]		:=	Int_to_Byte(arrScenarioHourCounter[iSelectScenarioHourCounter].iSeconds);


	(* Amount of Lumgates *)
	arrExternalDataOut[30]		:=	Int_To_Byte(udtLumgateMain.arrAmountLumgates[1]);
	arrExternalDataOut[31]		:=	Int_To_Byte(udtLumgateMain.arrAmountLumgates[2]);
	arrExternalDataOut[32]		:=	Int_To_Byte(udtLumgateMain.arrAmountLumgates[3]);
	arrExternalDataOut[33]		:=	Int_To_Byte(udtLumgateMain.arrAmountLumgates[4]);


	(* Lumgate available *)
	For iLoop 	:=	35 to 64 do
		arrExternalDataOut[iLoop]	:=	Byte#0;
	End_For;


	For iLoop 	:=	1 to 240 do
		iByteAddress		:=	iLoop / 8 + 35; (* 35 is first byte *)
		iBitAddress			:=	(iLoop Mod 8);


		If iBitAddress = 0 then (* Division by 8 possible *)
			iByteAddress	:=	iByteAddress - 1;
		End_If;


		Case iBitAddress of
			0:
				If arrLumgates[iLoop].xAvailable then
					arrExternalDataOut[iByteAddress].X7	:=	True;
				End_If;
			1:
				If arrLumgates[iLoop].xAvailable then
					arrExternalDataOut[iByteAddress].X0	:=	True;
				End_If;
			2:
				If arrLumgates[iLoop].xAvailable then
					arrExternalDataOut[iByteAddress].X1	:=	True;
				End_If;
			3:
				If arrLumgates[iLoop].xAvailable then
					arrExternalDataOut[iByteAddress].X2	:=	True;
				End_If;
			4:
				If arrLumgates[iLoop].xAvailable then
					arrExternalDataOut[iByteAddress].X3	:=	True;
				End_If;
			5:
				If arrLumgates[iLoop].xAvailable then
					arrExternalDataOut[iByteAddress].X4	:=	True;
				End_If;
			6:
				If arrLumgates[iLoop].xAvailable then
					arrExternalDataOut[iByteAddress].X5	:=	True;
				End_If;
			7:
				If arrLumgates[iLoop].xAvailable then
					arrExternalDataOut[iByteAddress].X6	:=	True;
				End_If;
		End_Case;
	End_For;


	(* Lumgate error *)
	For iLoop 	:=	65 to 94 do
		arrExternalDataOut[iLoop]	:=	Byte#0;
	End_For;


	For iLoop 	:=	1 to 240 do
		iByteAddress		:=	iLoop / 8 + 65; (* 65 is first byte *)
		iBitAddress			:=	(iLoop Mod 8);

		If iBitAddress = 0 then (* Division by 8 possible *)
			iByteAddress	:=	iByteAddress - 1;
		End_If;


		Case iBitAddress Of
			0:
				If arrLumgates[iLoop].xError then
					arrExternalDataOut[iByteAddress].X7	:=	True;
				End_If;
			1:
				If arrLumgates[iLoop].xError then
					arrExternalDataOut[iByteAddress].X0	:=	True;
				End_If;
			2:
				If arrLumgates[iLoop].xError then
					arrExternalDataOut[iByteAddress].X1	:=	True;
				End_If;
			3:
				If arrLumgates[iLoop].xError then
					arrExternalDataOut[iByteAddress].X2	:=	True;
				End_If;
			4:
				If arrLumgates[iLoop].xError then
					arrExternalDataOut[iByteAddress].X3	:=	True;
				End_If;
			5:
				If arrLumgates[iLoop].xError then
					arrExternalDataOut[iByteAddress].X4	:=	True;
				End_If;
			6:
				If arrLumgates[iLoop].xError then
					arrExternalDataOut[iByteAddress].X5	:=	True;
				End_If;
			7:
				If arrLumgates[iLoop].xError then
					arrExternalDataOut[iByteAddress].X6	:=	True;
				End_If;
		End_Case;
	End_For;


	(* Detailed Lumgate data *)
	uiSelectedLumgate			:=	Byte_To_UInt(bSelectDataLumgate);
	iSelectedLumgate			:=	UInt_To_Int(uiSelectedLumgate);
	iSelectedLumgate			:=	Limit_Int(1,iSelectedLumgate,240);
	arrExternalDataOut[100]		:=	Int_to_Byte(iSelectedLumgate);


	arrExternalDataOut[101].X0	:=	arrLumgates[iSelectedLumgate].xAvailable;
	arrExternalDataOut[101].X1	:=	arrLumgates[iSelectedLumgate].xAutomaticManual;
	arrExternalDataOut[101].X2	:=	arrLumgates[iSelectedLumgate].xRelay1;
	arrExternalDataOut[101].X3	:=	arrLumgates[iSelectedLumgate].xRelay2;
	arrExternalDataOut[101].X4	:=	arrLumgates[iSelectedLumgate].xInput1;
	arrExternalDataOut[101].X5	:=	arrLumgates[iSelectedLumgate].xInput2;
	arrExternalDataOut[101].X6	:=	arrLumgates[iSelectedLumgate].xFailMode;
	arrExternalDataOut[101].X7	:=	arrLumgates[iSelectedLumgate].xSafeSet;


	wTempValue				:=	Int_To_Word(arrLumgates[iSelectedLumgate].iActualOutputLevel);
	arrExternalDataOut[102]	:=	wTempValue.B0;
	arrExternalDataOut[103]	:=	wTempValue.B1;


	wTempValue				:=	Int_To_Word(arrLumgates[iSelectedLumgate].iMeasurement);
	arrExternalDataOut[104]	:=	wTempValue.B0;
	arrExternalDataOut[105]	:=	wTempValue.B1;




	dwTempValueHours		:=	Udint_To_Dword(arrLumgates[iSelectedLumgate].udtHourCounterGeneral.udiHours);


	arrExternalDataOut[106]	:=	dwTempValueHours.B0;
	arrExternalDataOut[107]	:=	dwTempValueHours.B1;
	arrExternalDataOut[108]	:=	dwTempValueHours.B2;
	arrExternalDataOut[109]	:=	dwTempValueHours.B3;


	arrExternalDataOut[110]	:=	Int_to_Byte(arrLumgates[iSelectedLumgate].udtHourCounterGeneral.iMinutes);
	arrExternalDataOut[111]	:=	Int_to_Byte(arrLumgates[iSelectedLumgate].udtHourCounterGeneral.iSeconds);




	dwTempValueHours		:=	Udint_To_Dword(arrLumgates[iSelectedLumgate].udtHourCounterMaintenance.udiHours);


	arrExternalDataOut[112]	:=	dwTempValueHours.B0;
	arrExternalDataOut[113]	:=	dwTempValueHours.B1;
	arrExternalDataOut[114]	:=	dwTempValueHours.B2;
	arrExternalDataOut[115]	:=	dwTempValueHours.B3;


	arrExternalDataOut[116]	:=	Int_to_Byte(arrLumgates[iSelectedLumgate].udtHourCounterMaintenance.iMinutes);
	arrExternalDataOut[117]	:=	Int_to_Byte(arrLumgates[iSelectedLumgate].udtHourCounterMaintenance.iSeconds);




	dwTempValueHours		:=	Udint_To_Dword(arrLumgates[iSelectedLumgate].udtHourCounter20_50.udiHours);


	arrExternalDataOut[118]	:=	dwTempValueHours.B0;
	arrExternalDataOut[119]	:=	dwTempValueHours.B1;
	arrExternalDataOut[120]	:=	dwTempValueHours.B2;
	arrExternalDataOut[121]	:=	dwTempValueHours.B3;


	arrExternalDataOut[122]	:=	Int_to_Byte(arrLumgates[iSelectedLumgate].udtHourCounter20_50.iMinutes);
	arrExternalDataOut[123]	:=	Int_to_Byte(arrLumgates[iSelectedLumgate].udtHourCounter20_50.iSeconds);




	dwTempValueHours		:=	Udint_To_Dword(arrLumgates[iSelectedLumgate].udtHourCounter50_75.udiHours);


	arrExternalDataOut[124]	:=	dwTempValueHours.B0;
	arrExternalDataOut[125]	:=	dwTempValueHours.B1;
	arrExternalDataOut[126]	:=	dwTempValueHours.B2;
	arrExternalDataOut[127]	:=	dwTempValueHours.B3;


	arrExternalDataOut[128]	:=	Int_to_Byte(arrLumgates[iSelectedLumgate].udtHourCounter50_75.iMinutes);
	arrExternalDataOut[129]	:=	Int_to_Byte(arrLumgates[iSelectedLumgate].udtHourCounter50_75.iSeconds);




	dwTempValueHours		:=	Udint_To_Dword(arrLumgates[iSelectedLumgate].udtHourCounter75_100.udiHours);


	arrExternalDataOut[130]	:=	dwTempValueHours.B0;
	arrExternalDataOut[131]	:=	dwTempValueHours.B1;
	arrExternalDataOut[132]	:=	dwTempValueHours.B2;
	arrExternalDataOut[133]	:=	dwTempValueHours.B3;


	arrExternalDataOut[134]	:=	Int_to_Byte(arrLumgates[iSelectedLumgate].udtHourCounter75_100.iMinutes);
	arrExternalDataOut[135]	:=	Int_to_Byte(arrLumgates[iSelectedLumgate].udtHourCounter75_100.iSeconds);




	arrExternalDataOut[136].X0	:=	arrLumgates[iSelectedLumgate].udtCurrentCheck.xError;
	arrExternalDataOut[136].X1	:=	arrLumgates[iSelectedLumgate].udtCurrentCheck.xWarning;


	arrExternalDataOut[141]	:=	Int_to_Byte(iOperatingSeconds);
	arrExternalDataOut[142]	:=	Int_to_Byte(iOperatingMinutes);
	dwTemp					:=	udint_to_dword(udiOperatingHours);
	arrExternalDataOut[137]	:=	dwTemp.B0;
	arrExternalDataOut[138]	:=	dwTemp.B1;
	arrExternalDataOut[139]	:=	dwTemp.B2;
	arrExternalDataOut[140]	:=	dwTemp.B3;


	arrExternalDataOut[143].X0	:=	ONBOARD_INPUT_BIT0;
	arrExternalDataOut[143].X1	:=	ONBOARD_INPUT_BIT1;
	arrExternalDataOut[143].X2	:=	ONBOARD_INPUT_BIT2;
	arrExternalDataOut[143].X3	:=	ONBOARD_INPUT_BIT3;
	arrExternalDataOut[143].X4	:=	ONBOARD_INPUT_BIT4;
	arrExternalDataOut[143].X5	:=	ONBOARD_INPUT_BIT5;
	arrExternalDataOut[143].X6	:=	ONBOARD_INPUT_BIT6;
	arrExternalDataOut[143].X7	:=	ONBOARD_INPUT_BIT7;


	arrExternalDataOut[144].X0	:=	ONBOARD_INPUT_BIT8;
	arrExternalDataOut[144].X1	:=	ONBOARD_INPUT_BIT9;
	arrExternalDataOut[144].X2	:=	ONBOARD_INPUT_BIT10;
	arrExternalDataOut[144].X3	:=	ONBOARD_INPUT_BIT11;


	arrExternalDataOut[145].X0	:=	ONBOARD_OUTPUT_BIT0;
	arrExternalDataOut[145].X1	:=	ONBOARD_OUTPUT_BIT1;
	arrExternalDataOut[145].X2	:=	ONBOARD_OUTPUT_BIT2;
	arrExternalDataOut[145].X3	:=	ONBOARD_OUTPUT_BIT3;


	arrExternalDataOut[155]	:=	byte#2;
End_if;

If xActivate then
	If udtNetworkConfiguration.iMode = 5 or udtNetworkConfiguration.iMode = 3 then
		iExternalData	:=	1;	(* Profinet *)
	Elsif udtNetworkConfiguration.iMode = 4 or udtNetworkConfiguration.iMode = 2 then
		iExternalData	:=	2;	(* Modbus *)
	End_if;


	Case iExternalData of
		1:	(* Profinet *)
			For iLoop := 0 to 255 do
				arrExternalDataIn[iLoop]:= PND_S1S1_INPUTS[iLoop];
				PND_S1S1_OUTPUTS[iLoop]:= arrExternalDataOut[iLoop];
		End_For;


		2:	(* Modbus *)
			For iLoop := 300 to 311 do
				iLoop2 := iLoop - 300;
				arrExternalDataIn[iLoop2]:= MB_Data_External[iLoop].B0;
			End_For;

			For iLoop := 100 to 236 do
				iLoop2	:=	iLoop - 100;
				MB_Data_External[iLoop].B0:= arrExternalDataOut[iLoop2];
			End_For;
	End_case;
End_if;


iPort:= 502;
xAutoQuit:= True;


MB_TCP_Server_V2_00_1(
xActivate				:=	xActivate,
xAcknowledge			:=	xQuit,
xAutoAck				:=	xAutoQuit,
xUDP_Mode				:=	xUDP,
iTCP_Port				:=	iPort,
strPartner_IP			:=	udtNetworkConfiguration.strIPModbus,
tTimeout				:=	T#0s,
uiOffsetInputRegister	:=	uiRegOffsetInputs,
uiOffsetHoldingRegister	:=	uiRegOffset,
uiOffsetInputs			:=	uiRegOffsetInputs,
uiOffsetCoils			:=	uiRegOffsetCoils,
arrModbusData			:=	MB_Data_External);


xActive_MB_Server		:=	MB_TCP_Server_V2_00_1.xActive;
xConnection_MB_Server	:=	MB_TCP_Server_V2_00_1.xConnected;
xError_MB_Server		:=	MB_TCP_Server_V2_00_1.xError;
wDiagCode_MB_Server		:=	MB_TCP_Server_V2_00_1.wDiagCode;
wAddDiagCode_MB_Server	:=	MB_TCP_Server_V2_00_1.wAddDiagCode;
MB_Data_External		:=	MB_TCP_Server_V2_00_1.arrModbusData;

(* Shifted On/Off *)
(* to turn on the luminaiers in groups and not all together *)
Case iShiftOnOff of
	0:
		iNumber			:=	iNumber + 1;
		If iNumber > 8 then
			iNumber		:=	1;
		End_If;
		iShiftOnOff		:=	1;


	1:
		If TON_Delay_Shift.Q then
			iShiftOnOff	:=	0;
		End_If;
End_Case;


TON_Delay_Shift(IN		:=	iShiftOnOff = 1,
				PT		:=	t#200ms);	(* Delay between switching on *)


For iLoop := 1 to 8	do
	arrShiftedOnOff[iLoop]	:=	False;
End_For;
arrShiftedOnOff[iNumber]	:=	True;

(* Teach Cos Failsafe *)
For iLoop := 1 to 240 do
	iStep	:=	arrLumgates[iLoop].udtInternal.iStepTeach;


	Case iStep of
		0:	(* Teach consumption active for this Lumgate *)
			If arrLumgates[iLoop].xAvailable and arrLumgates[iLoop].iMode = 3 then 
				arrLumgates[iLoop].iOutputLevelTeach		:= 	0;
				iStep										:= 	2;
				arrLumgates[iLoop].udtInternal.iTeachRun	:=	1;


			(* Teach failsave active for this Lumgate *)
			Elsif arrLumgates[iLoop].xAvailable and arrLumgates[iLoop].iMode = 4 then 
				iStep									:= 	10;
			End_If;

		2:
			If arrLumgates[iLoop].iActualOutputLevel = arrLumgates[iLoop].iTargetOutputLevel then
				arrLumgates[iLoop].udtInternal.iCounterTeach		:= 	arrLumgates[iLoop].udtInternal.iCounterTeach + 1;
				If arrLumgates[iLoop].udtInternal.iTeachRun = 1 then
					arrLumgates[iLoop].xRelay1	:=	False;
					arrLumgates[iLoop].xRelay2	:=	False;
				Elsif arrLumgates[iLoop].udtInternal.iTeachRun = 2 then
					arrLumgates[iLoop].xRelay1	:=	False;
					arrLumgates[iLoop].xRelay2	:=	True;
				Elsif arrLumgates[iLoop].udtInternal.iTeachRun = 3 then
					arrLumgates[iLoop].xRelay1	:=	True;
					arrLumgates[iLoop].xRelay2	:=	False;
				Elsif arrLumgates[iLoop].udtInternal.iTeachRun = 4 then
					arrLumgates[iLoop].xRelay1	:=	True;
					arrLumgates[iLoop].xRelay2	:=	True;
				End_if;
				(* Wait during 200 cycles befor saving measured value *)
				If arrLumgates[iLoop].udtInternal.iCounterTeach >= 200 then 
					arrLumgates[iLoop].udtInternal.iCounterTeach	:= 	0;
					iStep											:= 	3;
				End_If;
			End_If;


		3:
			iTemp	:= 	arrLumgates[iLoop].iOutputLevelTeach;
			Case iTemp of
				0:
					If arrLumgates[iLoop].udtInternal.iTeachRun = 1 then
						strDiag	:=	'Starting current measurement run 1/4';
					Elsif arrLumgates[iLoop].udtInternal.iTeachRun = 2 then
						strDiag	:=	'Starting current measurement run 2/4';
					Elsif arrLumgates[iLoop].udtInternal.iTeachRun = 3 then
						strDiag	:=	'Starting current measurement run 3/4';
					Elsif arrLumgates[iLoop].udtInternal.iTeachRun = 4 then
						strDiag	:=	'Starting current measurement run 4/4';
					End_if;
					iLoop2	:=	1;


				1:
					iLoop2	:=	2;


				50:
					iLoop2	:=	3;
					strDiag	:=	'Current measurement run 12% done';


				100:
					iLoop2	:=	4;


				150:
					iLoop2	:=	5;


				200:
					iLoop2	:=	6;


				250:
					iLoop2	:=	7;
					strDiag	:=	'Current measurement run 28% done';


				300:
					iLoop2	:=	8;


				350:
					iLoop2	:=	9;


				400:
					iLoop2	:=	10;


				450:
					iLoop2	:=	11;
					strDiag	:=	'Current measurement run 44% done';


				500:
					iLoop2	:=	12;


				550:
					iLoop2	:=	13;


				600:
					iLoop2	:=	14;


				650:
					iLoop2	:=	15;
					strDiag	:=	'Current measurement run 60% done';


				700:
					iLoop2	:=	16;


				750:
					iLoop2	:=	17;


				800:
					iLoop2	:=	18;


				850:
					iLoop2	:=	19;
					strDiag	:=	'Current measurement run 76% done';


				900:
					iLoop2	:=	20;


				950:
					iLoop2	:=	21;


				1000:
					iLoop2	:=	22;


				1070:
					iLoop2	:=	23;
					strDiag	:=	'Current measurement run 92% done';
			End_Case;


			If iLoop2 > 1 then
				iLoop3	:=	iLoop2 - 1;
				If arrLumgates[iLoop].udtInternal.iTeachRun = 1 then
					If arrLumgates[iLoop].iMeasurement >= arrLumgates[iLoop].arrPowerConsumption[iLoop3] then
						arrLumgates[iLoop].arrPowerConsumption[iLoop2]	:= 	arrLumgates[iLoop].iMeasurement;	(* Save value *)
					Else
						arrLumgates[iLoop].arrPowerConsumption[iLoop2]	:= 	arrLumgates[iLoop].arrPowerConsumption[iLoop3];
					End_if;
				Elsif arrLumgates[iLoop].udtInternal.iTeachRun = 2 then
					If arrLumgates[iLoop].iMeasurement >= arrLumgates[iLoop].arrPowerConsumption_01[iLoop3] then
						arrLumgates[iLoop].arrPowerConsumption_01[iLoop2]	:=	arrLumgates[iLoop].iMeasurement;
					Else
						arrLumgates[iLoop].arrPowerConsumption_01[iLoop2]	:= 	arrLumgates[iLoop].arrPowerConsumption_01[iLoop3];
					End_if;
				Elsif arrLumgates[iLoop].udtInternal.iTeachRun = 3 then
					If arrLumgates[iLoop].iMeasurement >= arrLumgates[iLoop].arrPowerConsumption_10[iLoop3] then
						arrLumgates[iLoop].arrPowerConsumption_10[iLoop2]	:=	arrLumgates[iLoop].iMeasurement;
					Else
						arrLumgates[iLoop].arrPowerConsumption_10[iLoop2]	:= 	arrLumgates[iLoop].arrPowerConsumption_10[iLoop3];
					End_if;
				Elsif arrLumgates[iLoop].udtInternal.iTeachRun = 4 then
					If arrLumgates[iLoop].iMeasurement >= arrLumgates[iLoop].arrPowerConsumption_11[iLoop3] then
						arrLumgates[iLoop].arrPowerConsumption_11[iLoop2]	:=	arrLumgates[iLoop].iMeasurement;
					Else
						arrLumgates[iLoop].arrPowerConsumption_11[iLoop2]	:= 	arrLumgates[iLoop].arrPowerConsumption_11[iLoop3];
					End_if;
				End_if;
			Else
				If arrLumgates[iLoop].udtInternal.iTeachRun = 1 then
					arrLumgates[iLoop].arrPowerConsumption[iLoop2]		:= 	arrLumgates[iLoop].iMeasurement;	(* Save value *)
				Elsif arrLumgates[iLoop].udtInternal.iTeachRun = 2 then
					arrLumgates[iLoop].arrPowerConsumption_01[iLoop2]	:=	arrLumgates[iLoop].iMeasurement;
				Elsif arrLumgates[iLoop].udtInternal.iTeachRun = 3 then
					arrLumgates[iLoop].arrPowerConsumption_10[iLoop2]	:=	arrLumgates[iLoop].iMeasurement;
				Elsif arrLumgates[iLoop].udtInternal.iTeachRun = 4 then
					arrLumgates[iLoop].arrPowerConsumption_11[iLoop2]	:=	arrLumgates[iLoop].iMeasurement;
				End_if;
			End_If;


			If iLoop2 = 1 then
				arrLumgates[iLoop].iOutputLevelTeach	:=	1;
				iStep	:=	2;
			Elsif iLoop2 = 2 then
				arrLumgates[iLoop].iOutputLevelTeach	:=	50;
				iStep	:=	2;
			Elsif iLoop2 > 2 and iLoop2 < 22 then
				arrLumgates[iLoop].iOutputLevelTeach	:=	arrLumgates[iLoop].iOutputLevelTeach + 50;
				iStep	:=	2;
			Elsif iLoop2 = 22 then
				arrLumgates[iLoop].iOutputLevelTeach	:=	1070;
				iStep	:=	2;
			Else
				If arrLumgates[iLoop].udtInternal.iTeachRun < 4 then
					arrLumgates[iLoop].udtInternal.iTeachRun	:=	arrLumgates[iLoop].udtInternal.iTeachRun + 1;
					arrLumgates[iLoop].iOutputLevelTeach		:=	0;
					iStep	:=	2;
				Else
					iStep	:=	4;
				End_if;
			End_If;


		4:	
			strDiag													:=	'Current measurement done';
			arrLumgates[iLoop].xTeachConsumption					:=	False;
			arrLumgates[iLoop].udtInternal.xPowerConsumptionCorrect	:= 	True;


			(* There is no minimum difference measured *)
			If arrLumgates[iLoop].arrPowerConsumption[23] <= arrLumgates[iLoop].arrPowerConsumption[12] then 
				arrLumgates[iLoop].udtInternal.xPowerConsumptionCorrect	:= 	False;
			End_If;


			(* There is no minimum difference measured *)
			If arrLumgates[iLoop].arrPowerConsumption[12] <= arrLumgates[iLoop].arrPowerConsumption[2] then 
				arrLumgates[iLoop].udtInternal.xPowerConsumptionCorrect	:= 	False;
			End_If;
			iStep						:= 	0;


		10:
			If arrLumgates[iLoop].iActualOutputLevel = arrLumgates[iLoop].iTargetOutputLevel then
				iStep					:= 	11;
			End_If;


		11:
			arrLumgates[iLoop].xSafe	:= 	True;
			iStep						:= 	12;


		12:
			arrLumgates[iLoop].udtInternal.iCounterFailsave	:= 	arrLumgates[iLoop].udtInternal.iCounterFailsave + 1;
			If arrLumgates[iLoop].udtInternal.iCounterFailsave > 50 then
				arrLumgates[iLoop].xSafe						:= 	False;
				arrLumgates[iLoop].udtInternal.iCounterFailsave	:= 	0;
				arrLumgates[iLoop].xTeachFailsave				:= 	False;
				iStep	:= 	0;
			Elsif arrLumgates[iLoop].xSafeFinish then
				arrLumgates[iLoop].xSafe						:=	False;
				arrLumgates[iLoop].udtInternal.iCounterFailsave	:= 	0;
				arrLumgates[iLoop].xTeachFailsave				:= 	False;
				iStep	:= 	0;
			End_If;
	End_Case;


	arrLumgates[iLoop].udtInternal.iStepTeach			:= 	iStep;


	If arrLumgates[iLoop].iMode < 3 or arrLumgates[iLoop].iMode > 4 then (* Teach not active *)
		arrLumgates[iLoop].iOutputLevelTeach			:= 	0;
		arrLumgates[iLoop].udtInternal.iCounterTeach	:= 	0;
		arrLumgates[iLoop].udtInternal.iCounterFailsave	:= 	0;
		arrLumgates[iLoop].udtInternal.iStepTeach		:= 	0;
	Elsif arrLumgates[iLoop].iMode = 4 and arrLumgates[iLoop].udtInternal.iStepTeach < 10 and arrLumgates[iLoop].udtInternal.iStepTeach > 0 then
		arrLumgates[iLoop].iOutputLevelTeach			:= 	0;
		arrLumgates[iLoop].udtInternal.iCounterTeach	:= 	0;
		arrLumgates[iLoop].udtInternal.iCounterFailsave	:= 	0;
		arrLumgates[iLoop].udtInternal.iStepTeach		:= 	0;
	End_If;
End_For;

(* Current Check *)
Case iCurrentCheck of
	0:
		iNumberLumgateCurrentCheck	:= 	iNumberLumgateCurrentCheck + 1;
		If iNumberLumgateCurrentCheck > 240 then
			iNumberLumgateCurrentCheck	:= 	1;
		End_If;


		If iNumberLumgateCurrentCheck > 0 and iNumberLumgateCurrentCheck < 241 then
			If arrLumgates[iNumberLumgateCurrentCheck].xAvailable and
				(arrLumgates[iNumberLumgateCurrentCheck].udtCurrentCheck.xActiveCurrentCheckError or
				arrLumgates[iNumberLumgateCurrentCheck].udtCurrentCheck.xActiveCurrentCheckWarning) then
				iCurrentCheck			:= 	1;		(* Check current is possible *)
			End_If;


			If not arrLumgates[iNumberLumgateCurrentCheck].udtCurrentCheck.xActiveCurrentCheckError
				or not arrLumgates[iNumberLumgateCurrentCheck].udtInternal.xPowerConsumptionCorrect  then
					arrLumgates[iNumberLumgateCurrentCheck].udtCurrentCheck.xDirectError	:= 	False;
					arrLumgates[iNumberLumgateCurrentCheck].udtCurrentCheck.xError			:= 	False;
													(* Current error is disabled or invalid measurement*)
			End_If;


			If not arrLumgates[iNumberLumgateCurrentCheck].udtCurrentCheck.xActiveCurrentCheckWarning
				or not arrLumgates[iNumberLumgateCurrentCheck].udtInternal.xPowerConsumptionCorrect  then
					arrLumgates[iNumberLumgateCurrentCheck].udtCurrentCheck.xDirectWarning	:= 	False;
					arrLumgates[iNumberLumgateCurrentCheck].udtCurrentCheck.xWarning		:= 	False;
													(* Current warning is disabled or invalid measurement*)
			End_If;
		End_If;


	1:	
		If arrLumgates[iNumberLumgateCurrentCheck].udtInternal.xPowerConsumptionCorrect then
			iTempOutputLevel	:= 	arrLumgates[iNumberLumgateCurrentCheck].iActualOutputLevel;
			If iTempOutputLevel > 0 then

				Case iTempOutputLevel of
					1..50:
						iTempLevelMinimum	:=	2;
						iTempLevelMaximum	:=	3;
						iParameter1			:=	1;
						iParameter2			:=	50;


					51..100:
						iTempLevelMinimum	:=	3;
						iTempLevelMaximum	:=	4;
						iParameter1			:=	50;
						iParameter2			:=	100;


					101..150:
						iTempLevelMinimum	:=	4;
						iTempLevelMaximum	:=	5;
						iParameter1			:=	100;
						iParameter2			:=	150;


					151..200:
						iTempLevelMinimum	:=	5;
						iTempLevelMaximum	:=	6;
						iParameter1			:=	150;
						iParameter2			:=	200;


					201..250:
						iTempLevelMinimum	:=	6;
						iTempLevelMaximum	:=	7;
						iParameter1			:=	200;
						iParameter2			:=	250;


					251..300:
						iTempLevelMinimum	:=	7;
						iTempLevelMaximum	:=	8;
						iParameter1			:=	250;
						iParameter2			:=	300;


					301..350:
						iTempLevelMinimum	:=	8;
						iTempLevelMaximum	:=	9;
						iParameter1			:=	300;
						iParameter2			:=	350;


					351..400:
						iTempLevelMinimum	:=	9;
						iTempLevelMaximum	:=	10;
						iParameter1			:=	350;
						iParameter2			:=	400;


					401..450:
						iTempLevelMinimum	:=	10;
						iTempLevelMaximum	:=	11;
						iParameter1			:=	400;
						iParameter2			:=	450;


					451..500:
						iTempLevelMinimum	:=	11;
						iTempLevelMaximum	:=	12;
						iParameter1			:=	450;
						iParameter2			:=	500;


					501..550:
						iTempLevelMinimum	:=	12;
						iTempLevelMaximum	:=	13;
						iParameter1			:=	500;
						iParameter2			:=	550;


					551..600:
						iTempLevelMinimum	:=	13;
						iTempLevelMaximum	:=	14;
						iParameter1			:=	550;
						iParameter2			:=	600;


					601..650:
						iTempLevelMinimum	:=	14;
						iTempLevelMaximum	:=	15;
						iParameter1			:=	600;
						iParameter2			:=	650;


					651..700:
						iTempLevelMinimum	:=	15;
						iTempLevelMaximum	:=	16;
						iParameter1			:=	650;
						iParameter2			:=	700;


					701..750:
						iTempLevelMinimum	:=	16;
						iTempLevelMaximum	:=	17;
						iParameter1			:=	700;
						iParameter2			:=	750;


					751..800:
						iTempLevelMinimum	:=	17;
						iTempLevelMaximum	:=	18;
						iParameter1			:=	750;
						iParameter2			:=	800;


					801..850:
						iTempLevelMinimum	:=	18;
						iTempLevelMaximum	:=	19;
						iParameter1			:=	800;
						iParameter2			:=	850;


					851..900:
						iTempLevelMinimum	:=	19;
						iTempLevelMaximum	:=	20;
						iParameter1			:=	850;
						iParameter2			:=	900;


					901..950:
						iTempLevelMinimum	:=	20;
						iTempLevelMaximum	:=	21;
						iParameter1			:=	900;
						iParameter2			:=	950;


					951..1000:
						iTempLevelMinimum	:=	21;
						iTempLevelMaximum	:=	22;
						iParameter1			:=	950;
						iParameter2			:=	1000;


					1001..1070:	
						iTempLevelMinimum	:=	22;
						iTempLevelMaximum	:=	23;
						iParameter1			:=	1000;
						iParameter2			:=	1070;
				End_Case;

				If arrLumgates[iNumberLumgateCurrentCheck].xRelay1 and arrLumgates[iNumberLumgateCurrentCheck].xRelay2 then
					iParameter4	:=	arrLumgates[iNumberLumgateCurrentCheck].arrPowerConsumption_11[iTempLevelMinimum];
					iParameter5	:=	arrLumgates[iNumberLumgateCurrentCheck].arrPowerConsumption_11[iTempLevelMaximum];
				Elsif arrLumgates[iNumberLumgateCurrentCheck].xRelay1 and not arrLumgates[iNumberLumgateCurrentCheck].xRelay2 then
					iParameter4	:=	arrLumgates[iNumberLumgateCurrentCheck].arrPowerConsumption_10[iTempLevelMinimum];
					iParameter5	:=	arrLumgates[iNumberLumgateCurrentCheck].arrPowerConsumption_10[iTempLevelMaximum];
				Elsif not arrLumgates[iNumberLumgateCurrentCheck].xRelay1 and arrLumgates[iNumberLumgateCurrentCheck].xRelay2 then
					iParameter4	:=	arrLumgates[iNumberLumgateCurrentCheck].arrPowerConsumption_01[iTempLevelMinimum];
					iParameter5	:=	arrLumgates[iNumberLumgateCurrentCheck].arrPowerConsumption_01[iTempLevelMaximum];
				Elsif not arrLumgates[iNumberLumgateCurrentCheck].xRelay1 and not arrLumgates[iNumberLumgateCurrentCheck].xRelay2 then
					iParameter4	:=	arrLumgates[iNumberLumgateCurrentCheck].arrPowerConsumption[iTempLevelMinimum];
					iParameter5	:=	arrLumgates[iNumberLumgateCurrentCheck].arrPowerConsumption[iTempLevelMaximum];
				End_if;


				(* Calculate deviation *)
				rParameterA	:=	Int_To_Real(iParameter5 - iParameter4) / Int_To_Real(iParameter2 - iParameter1);
				rParameterB	:=	Int_To_Real(iParameter5) - rParameterA * Int_To_Real(iParameter2);


				arrLumgates[iNumberLumgateCurrentCheck].udtCurrentCheck.iExpectedCurrent	:=	Real_To_Int(rParameterA * Int_To_Real(iTempOutputLevel) + rParameterB);
				iCurrentCheck	:=	2;
			Else
				arrLumgates[iNumberLumgateCurrentCheck].udtCurrentCheck.xDirectError		:=	False;
				arrLumgates[iNumberLumgateCurrentCheck].udtCurrentCheck.xDirectWarning		:=	False;
				If arrLumgates[iNumberLumgateCurrentCheck].xRelay1 and arrLumgates[iNumberLumgateCurrentCheck].xRelay2 then
					arrLumgates[iNumberLumgateCurrentCheck].udtCurrentCheck.iExpectedCurrent	:=	arrLumgates[iNumberLumgateCurrentCheck].arrPowerConsumption_11[1];
				Elsif arrLumgates[iNumberLumgateCurrentCheck].xRelay1 and not arrLumgates[iNumberLumgateCurrentCheck].xRelay2 then
					arrLumgates[iNumberLumgateCurrentCheck].udtCurrentCheck.iExpectedCurrent	:=	arrLumgates[iNumberLumgateCurrentCheck].arrPowerConsumption_10[1];
				Elsif not arrLumgates[iNumberLumgateCurrentCheck].xRelay1 and arrLumgates[iNumberLumgateCurrentCheck].xRelay2 then
					arrLumgates[iNumberLumgateCurrentCheck].udtCurrentCheck.iExpectedCurrent	:=	arrLumgates[iNumberLumgateCurrentCheck].arrPowerConsumption_01[1];
				Elsif not arrLumgates[iNumberLumgateCurrentCheck].xRelay1 and not arrLumgates[iNumberLumgateCurrentCheck].xRelay2 then
					arrLumgates[iNumberLumgateCurrentCheck].udtCurrentCheck.iExpectedCurrent	:=	arrLumgates[iNumberLumgateCurrentCheck].arrPowerConsumption[1];
				End_if;
				iCurrentCheck	:=	0;
			End_If;
		Else
			iCurrentCheck		:=	0;
		End_If;


	2:
		If arrLumgates[iNumberLumgateCurrentCheck].udtCurrentCheck.xActiveCurrentCheckError then
			rTempExpected		:=	Int_To_Real(arrLumgates[iNumberLumgateCurrentCheck].udtCurrentCheck.iExpectedCurrent);
			rMarginError		:=	rTempExpected * arrLumgates[iNumberLumgateCurrentCheck].udtCurrentCheck.rPercentageError / 100.0;


			If rMarginError < 16.0 then (* limit deviation to 16 mA *)
				rMarginError	:=	16.0;
			End_If;


			rLimitMax	:=	rTempExpected + rMarginError;
			rLimitMin	:=	rTempExpected - rMarginError;


			iLimitMax	:=	Real_To_Int(rLimitMax);
			iLimitMin	:=	Real_To_Int(rLimitMin);


			arrLumgates[iNumberLumgateCurrentCheck].udtCurrentCheck.iLimitMaxError		:=	iLimitMax;
			arrLumgates[iNumberLumgateCurrentCheck].udtCurrentCheck.iLimitMinError		:=	iLimitMin;


			If arrLumgates[iNumberLumgateCurrentCheck].iMeasurement > iLimitMax or arrLumgates[iNumberLumgateCurrentCheck].iMeasurement < iLimitMin then
				(* current error detected *)
				arrLumgates[iNumberLumgateCurrentCheck].udtCurrentCheck.xDirectError	:=	True;
			Else
				arrLumgates[iNumberLumgateCurrentCheck].udtCurrentCheck.xDirectError	:=	False;
			End_If;
		End_If;


		If arrLumgates[iNumberLumgateCurrentCheck].udtCurrentCheck.xActiveCurrentCheckWarning then
			rTempExpected	:=	Int_To_Real(arrLumgates[iNumberLumgateCurrentCheck].udtCurrentCheck.iExpectedCurrent);
			rMarginError	:=	rTempExpected * arrLumgates[iNumberLumgateCurrentCheck].udtCurrentCheck.rPercentageWarning / 100.0;


			If rMarginError < 16.0 then	(* limit deviation to 16 mA *)
				rMarginError	:=	16.0;
			End_If;


			rLimitMax	:=	rTempExpected + rMarginError;
			rLimitMin	:=	rTempExpected - rMarginError;


			iLimitMax	:=	Real_To_Int(rLimitMax);
			iLimitMin	:=	Real_To_Int(rLimitMin);


			arrLumgates[iNumberLumgateCurrentCheck].udtCurrentCheck.iLimitMaxWarning	:=	iLimitMax;
			arrLumgates[iNumberLumgateCurrentCheck].udtCurrentCheck.iLimitMinWarning	:=	iLimitMin;


			If arrLumgates[iNumberLumgateCurrentCheck].iMeasurement > iLimitMax or arrLumgates[iNumberLumgateCurrentCheck].iMeasurement < iLimitMin then
				(* current warning detected *)
				arrLumgates[iNumberLumgateCurrentCheck].udtCurrentCheck.xDirectWarning	:=	True;
			Else
				arrLumgates[iNumberLumgateCurrentCheck].udtCurrentCheck.xDirectWarning	:=	False;
			End_If;
		End_If;
		iCurrentCheck	:=	0;
End_Case;


(* Try to execute diagnostic messages sequential *)
For iLoop := 1 to 240 do
	iStep:= arrLumgates[iLoop].udtInternal.iStepCurrentCheck;


	Case iStep of
		0:
			If arrLumgates[iLoop].iActualOutputLevel = arrLumgates[iLoop].iTargetOutputLevel then
				arrLumgates[iLoop].udtInternal.iCounterCurrentCheck	:=	arrLumgates[iLoop].udtInternal.iCounterCurrentCheck + 1;


				(* Wait during 200 cycles to suppress short deviations *)
				If arrLumgates[iLoop].udtInternal.iCounterCurrentCheck >= 200 then 
					arrLumgates[iLoop].udtInternal.iCounterCurrentCheck	:=	0;
					iStep	:=	1;
				End_If;
			Else
				arrLumgates[iLoop].udtInternal.iCounterCurrentCheck	:=	0;
			End_If;


			If (not arrLumgates[iLoop].udtCurrentCheck.xActiveCurrentCheckError
				and not arrLumgates[iLoop].udtCurrentCheck.xActiveCurrentCheckWarning)
				or not arrLumgates[iLoop].udtInternal.xPowerConsumptionCorrect then
				(* reset 200 cycle counter *)
				arrLumgates[iLoop].udtInternal.iCounterCurrentCheck	:=	0;
			End_If;


			arrLumgates[iLoop].udtInternal.iStepCurrentCheck	:= iStep;


			If not arrLumgates[iLoop].udtCurrentCheck.xActiveCurrentCheckError then
				arrLumgates[iLoop].udtCurrentCheck.xError		:=	False;
			End_If;
			If not arrLumgates[iLoop].udtCurrentCheck.xActiveCurrentCheckWarning then
				arrLumgates[iLoop].udtCurrentCheck.xWarning		:=	False;
			End_If;
		1:
			If arrLumgates[iLoop].iActualOutputLevel <> arrLumgates[iLoop].iTargetOutputLevel then
				arrLumgates[iLoop].udtInternal.iCounterCurrentCheck	:=	0;
				iStep	:=	0;
			End_If;


			If (not arrLumgates[iLoop].udtCurrentCheck.xActiveCurrentCheckError
				and not arrLumgates[iLoop].udtCurrentCheck.xActiveCurrentCheckWarning)
				or not arrLumgates[iLoop].udtInternal.xPowerConsumptionCorrect then
				arrLumgates[iLoop].udtInternal.iCounterCurrentCheck	:=	0;
				iStep	:=	0;
			End_If;
			arrLumgates[iLoop].udtInternal.iStepCurrentCheck	:= iStep;
	End_Case;
End_For;


If arrLumgates[iNumberLumgateCurrentCheck].udtInternal.iStepCurrentCheck = 1 then
	(* error or warning occurs *)
	arrLumgates[iNumberLumgateCurrentCheck].udtCurrentCheck.xError		:=	arrLumgates[iNumberLumgateCurrentCheck].udtCurrentCheck.xDirectError;
	arrLumgates[iNumberLumgateCurrentCheck].udtCurrentCheck.xWarning	:=	arrLumgates[iNumberLumgateCurrentCheck].udtCurrentCheck.xDirectWarning;
	iStep	:=	0;
	(* creating diagnostig text *)
	If arrLumgates[iNumberLumgateCurrentCheck].udtCurrentCheck.xWarning and arrLumgates[iNumberLumgateCurrentCheck].udtInternal.xPowerConsumptionCorrect and not arrLumgates[iNumberLumgateCurrentCheck].xTeachConsumption	
	and (arrLumgates[iNumberLumgateCurrentCheck].iMeasurement > arrLumgates[iNumberLumgateCurrentCheck].udtCurrentCheck.iLimitMaxWarning or 
	arrLumgates[iNumberLumgateCurrentCheck].iMeasurement < arrLumgates[iNumberLumgateCurrentCheck].udtCurrentCheck.iLimitMinWarning) then
		xPreAlarm	:=	True;
		strDiag		:=	'Warning: RBT ';
		strDiag		:=	concat(strDiag, int_to_string(arrLumgates[iNumberLumgateCurrentCheck].iModuleLevel + 1,'%01d'));
		strDiag		:=	concat(strDiag, ', Lumgate ');
		strDiag		:=	concat(strDiag, int_to_string(arrLumgates[iNumberLumgateCurrentCheck].iModuleNumber,'%01d'));
		If arrLumgates[iNumberLumgateCurrentCheck].iMeasurement > arrLumgates[iNumberLumgateCurrentCheck].udtCurrentCheck.iLimitMaxWarning then
			strDiag	:=	concat(strDiag, ', Current overrange');
		Elsif arrLumgates[iNumberLumgateCurrentCheck].iMeasurement < arrLumgates[iNumberLumgateCurrentCheck].udtCurrentCheck.iLimitMinWarning then
			strDiag	:=	concat(strDiag, ', Current underrange');
		End_if;
	End_if;
	If arrLumgates[iNumberLumgateCurrentCheck].udtCurrentCheck.xError and arrLumgates[iNumberLumgateCurrentCheck].udtInternal.xPowerConsumptionCorrect and not arrLumgates[iNumberLumgateCurrentCheck].xTeachConsumption 
	and (arrLumgates[iNumberLumgateCurrentCheck].iMeasurement > arrLumgates[iNumberLumgateCurrentCheck].udtCurrentCheck.iLimitMaxError or 
	arrLumgates[iNumberLumgateCurrentCheck].iMeasurement < arrLumgates[iNumberLumgateCurrentCheck].udtCurrentCheck.iLimitMinError) then
		xAlarm	:=	True;
		strDiag	:=	'Error: RBT ';
		strDiag	:=	concat(strDiag, int_to_string(arrLumgates[iNumberLumgateCurrentCheck].iModuleLevel + 1,'%01d'));
		strDiag	:=	concat(strDiag, ', Lumgate ');
		strDiag	:=	concat(strDiag, int_to_string(arrLumgates[iNumberLumgateCurrentCheck].iModuleNumber,'%01d'));
		If arrLumgates[iNumberLumgateCurrentCheck].iMeasurement > arrLumgates[iNumberLumgateCurrentCheck].udtCurrentCheck.iLimitMaxError then
			strDiag	:=	concat(strDiag, ', Current overrange');
		Elsif arrLumgates[iNumberLumgateCurrentCheck].iMeasurement < arrLumgates[iNumberLumgateCurrentCheck].udtCurrentCheck.iLimitMinError then
			strDiag	:=	concat(strDiag, ', Current underrange');
		End_if;
	End_if;
	arrLumgates[iNumberLumgateCurrentCheck].udtInternal.iStepCurrentCheck	:= 0;
End_if;

(* Hour Counter *)
For iLoop := 1 to 240 do
	xCount1	:=	arrLumgates[iLoop].xRelay1;
	xCount2	:=	arrLumgates[iLoop].xRelay1 and arrLumgates[iLoop].iActualOutputLevel < 500;
	xCount3	:=	arrLumgates[iLoop].xRelay1 and arrLumgates[iLoop].iActualOutputLevel >= 500 and arrLumgates[iLoop].iActualOutputLevel < 750;
	xCount4	:=	arrLumgates[iLoop].xRelay1 and arrLumgates[iLoop].iActualOutputLevel >= 750;


	FB_HourCounterGeneral(xCount				:=	true,
	xPulseSecond								:=	xPulseSecond,
	udtCounterInfo								:=	arrLumgates[iLoop].udtHourCounterGeneral);


	arrLumgates[iLoop].udtHourCounterGeneral	:=	FB_HourCounterGeneral.udtCounterInfo;


	FB_HourCounterMaintenance(xCount			:=	true,
	xPulseSecond								:=	xPulseSecond,
	udtCounterInfo								:=	arrLumgates[iLoop].udtHourCounterMaintenance);


	arrLumgates[iLoop].udtHourCounterMaintenance:=	FB_HourCounterMaintenance.udtCounterInfo;


	FB_HourCounter20_50(xCount					:=	xCount2,
	xPulseSecond								:=	xPulseSecond,
	udtCounterInfo								:=	arrLumgates[iLoop].udtHourCounter20_50);


	arrLumgates[iLoop].udtHourCounter20_50		:=	FB_HourCounter20_50.udtCounterInfo;


	FB_HourCounter50_75(xCount					:=	xCount3,
	xPulseSecond								:=	xPulseSecond,
	udtCounterInfo								:=	arrLumgates[iLoop].udtHourCounter50_75);


	arrLumgates[iLoop].udtHourCounter50_75		:=	FB_HourCounter50_75.udtCounterInfo;


	FB_HourCounter75_100(xCount					:=	xCount4,
	xPulseSecond								:=	xPulseSecond,
	udtCounterInfo								:=	arrLumgates[iLoop].udtHourCounter75_100);


	arrLumgates[iLoop].udtHourCounter75_100		:=	FB_HourCounter75_100.udtCounterInfo;
End_For;

(* Mode *)
For iLoop := 1 to 240 do
	If arrLumgates[iLoop].xAvailable then
		(* Check if there is a special scenario active for this Lumgate *)
		xSpecialScenarioActive		:=	False;
		iTempOutputValueLGSpecial	:=	0;


		For iLoop2 := 1 to 15 do
			Case iLoop2 of
				1:
					xTempStatusScenario	:=	wSpecialScenario.X0;
				2:
					xTempStatusScenario	:=	wSpecialScenario.X1;
				3:
					xTempStatusScenario	:=	wSpecialScenario.X2;
				4:
					xTempStatusScenario	:=	wSpecialScenario.X3;
				5:
					xTempStatusScenario	:=	wSpecialScenario.X4;
				6:
					xTempStatusScenario	:=	wSpecialScenario.X5;
				7:
					xTempStatusScenario	:=	wSpecialScenario.X6;
				8:
					xTempStatusScenario	:=	wSpecialScenario.X7;
				9:
					xTempStatusScenario	:=	wSpecialScenario.X8;
				10:
					xTempStatusScenario	:=	wSpecialScenario.X9;
				11:
					xTempStatusScenario	:=	wSpecialScenario.X10;
				12:
					xTempStatusScenario	:=	wSpecialScenario.X11;
				13:
					xTempStatusScenario	:=	wSpecialScenario.X12;
				14:
					xTempStatusScenario	:=	wSpecialScenario.X13;
				15:
					xTempStatusScenario	:=	wSpecialScenario.X14;
			End_Case;


			If xTempStatusScenario and arrLumgates[iLoop].arrSpecialScene[iLoop2].xUsed then
				xSpecialScenarioActive	:=	True;
				If arrLumgates[iLoop].arrSpecialScene[iLoop2].iValue > iTempOutputValueLGSpecial then
					iTempOutputValueLGSpecial	:=	arrLumgates[iLoop].arrSpecialScene[iLoop2].iValue;


					(*	V2.01 changes: Editable scene relay state EPO-IS-268-1667 *)
					(*	V3.01 change: replace the relay variables *)
					xTempRelay1					:=	arrLumgates[iLoop].arrSpecialScene[iLoop2].xRelay1;
					xTempRelay2					:=	arrLumgates[iLoop].arrSpecialScene[iLoop2].xRelay2;
				End_If;
			End_If;
		End_For;


		(* Check if there is a dimming scenario active for this Lumgate *)
		xDimmingScenarioActive		:=	False;
		iTempOutputValueLGDimming	:=	-1;


		For iLoop2 := 1 to 4 do		
			iTempScenarioLG	:=	udtScenarioMode.arrActualScene[iLoop2];
			If iTempScenarioLG > 0 and iTempScenarioLG < 51 then
				If arrLumgates[iLoop].arrDimmingScene[iTempScenarioLG].xUsed then
					xDimmingScenarioActive	:=	True;


					If arrLumgates[iLoop].arrDimmingScene[iTempScenarioLG].iValue > iTempOutputValueLGDimming then
						xDimmingScenarioActive			:=	True;
						iTempOutputValueLGDimming		:=	arrLumgates[iLoop].arrDimmingScene[iTempScenarioLG].iValue;


						If not xSpecialScenarioActive then
							(*	V2.01 changes: Editable scene relay state EPO-IS-268-1667 *)
							xTempRelay1						:=	arrLumgates[iLoop].arrDimmingScene[iTempScenarioLG].xRelay1;
							xTempRelay2						:=	arrLumgates[iLoop].arrDimmingScene[iTempScenarioLG].xRelay2;
						End_if;
						arrLumgates[iLoop].iActualScene	:=	iTempScenarioLG;
					End_If;
				End_If;
			End_If;
		End_For;


		(* 	Priority:
			1: Manual
			2: Automatic: Special scenario
			3: Automatic: Teach consumption
			4: Automatic: Teach failsave
			5: Automatic: Dimming scenario
		*)


		If arrLumgates[iLoop].xAutomaticManual then (* Lumgate in manual mode *)
			arrLumgates[iLoop].iMode	:=	1;
			iModuleLevel				:=	arrLumgates[iLoop].iModuleLevel + 1;
			If xCommissioning and ((iCommissioningBranch = 0) or (iCommissioningBranch = iModuleLevel)) then
				If arrLumgates[iLoop].xRelay1_NC then
					xTempRelay1					:=	True;
				Else
					xTempRelay1					:=	False;
				End_if;
				If arrLumgates[iLoop].xRelay2_NC then
					xTempRelay2					:=	True;
				Else
					xTempRelay2					:=	False;
				End_if;
			Else
				xTempRelay1					:=	arrLumgates[iLoop].xManualRelay1;
				xTempRelay2					:=	arrLumgates[iLoop].xManualRelay2;
			End_if;
		Elsif xSpecialScenarioActive then
			arrLumgates[iLoop].iMode	:=	2;
		Elsif arrLumgates[iLoop].xTeachConsumption then
			xTempRelay1					:=	arrLumgates[iLoop].xRelay1;
			xTempRelay2					:=	arrLumgates[iLoop].xRelay2;
			arrLumgates[iLoop].iMode	:=	3;
		Elsif arrLumgates[iLoop].xTeachFailsave then
			arrLumgates[iLoop].iMode	:=	4;
			xTempRelay1					:=	arrLumgates[iLoop].xFailsaveRelay1;
			xTempRelay2					:=	arrLumgates[iLoop].xFailsaveRelay2;
		Elsif xDimmingScenarioActive then
			arrLumgates[iLoop].iOutputLevelScenario:= iTempOutputValueLGDimming;
			arrLumgates[iLoop].iMode	:=	5;
		Else
			arrLumgates[iLoop].iMode	:=	6; (* Failsave *)
		End_If;


		(* Choose the right output for each mode *)
		Case arrLumgates[iLoop].iMode of
			1:
				arrLumgates[iLoop].iTargetOutputLevel	:=	arrLumgates[iLoop].iOutputLevelManual;

			2:
				arrLumgates[iLoop].iTargetOutputLevel	:=	iTempOutputValueLGSpecial;


			3:
				arrLumgates[iLoop].iTargetOutputLevel	:=	arrLumgates[iLoop].iOutputLevelTeach;


			4:
				arrLumgates[iLoop].iTargetOutputLevel	:=	arrLumgates[iLoop].iOutputLevelFailsave;


			5:
				arrLumgates[iLoop].iOutputLevelScenarioWithClo	:= 	arrLumgates[iLoop].iOutputLevelScenario -
															arrLumgates[iLoop].udtCloLed.iAmountDecrease -
															arrLumgates[iLoop].udtCloMaintenance.iAmountDecrease;


			If arrLumgates[iLoop].udtCloLed.xActive or arrLumgates[iLoop].udtCloMaintenance.xActive then
				arrLumgates[iLoop].iTargetOutputLevel	:=	arrLumgates[iLoop].iOutputLevelScenarioWithClo;
			Else
				arrLumgates[iLoop].iTargetOutputLevel	:=	arrLumgates[iLoop].iOutputLevelScenario;
			End_If;


			6:
				arrLumgates[iLoop].iTargetOutputLevel	:=	arrLumgates[iLoop].iOutputLevelFailsave;
				xTempRelay1								:=	arrLumgates[iLoop].xFailsaveRelay1;
				xTempRelay2								:=	arrLumgates[iLoop].xFailsaveRelay2;
		End_Case;


		(* Output to device *)
		iDimmingSpeed	:=	limit(1,iDimmingSpeed,10);
		If xShiftedOnOff then (* On/Off in groups *)
			iShiftPosition	:=	iLoop Mod 8;
			If iShiftPosition = 0 then
				iShiftPosition	:=	8;
			End_If;


			If arrLumgates[iLoop].iActualOutputLevel = 0 and arrLumgates[iLoop].iTargetOutputLevel <> 0 and arrShiftedOnOff[iShiftPosition]
				or arrLumgates[iLoop].iActualOutputLevel <> 0 then

				If arrLumgates[iLoop].xGraduallyOutputChanges then
					If arrLumgates[iLoop].iTargetOutputLevel > arrLumgates[iLoop].iActualOutputLevel then
						iTemp	:=	arrLumgates[iLoop].iTargetOutputLevel - arrLumgates[iLoop].iActualOutputLevel;


						If arrLumgates[iLoop].xRelay1_NC and not (arrLumgates[iLoop].iMode = 3) then
							arrLumgates[iLoop].xRelay1	:=	not xTempRelay1;
						Else
							arrLumgates[iLoop].xRelay1	:=	xTempRelay1;
						End_if;
						If arrLumgates[iLoop].xRelay2_NC and not (arrLumgates[iLoop].iMode = 3) then
							arrLumgates[iLoop].xRelay2	:=	not xTempRelay2;
						Else
							arrLumgates[iLoop].xRelay2	:=	xTempRelay2;
						End_if;


						If iTemp > iDimmingSpeed then
							arrLumgates[iLoop].iActualOutputLevel	:=	arrLumgates[iLoop].iActualOutputLevel + iDimmingSpeed;
						Else
							arrLumgates[iLoop].iActualOutputLevel	:=	arrLumgates[iLoop].iTargetOutputLevel;
						End_if;
					Elsif arrLumgates[iLoop].iTargetOutputLevel < arrLumgates[iLoop].iActualOutputLevel then
						iTemp	:=	arrLumgates[iLoop].iActualOutputLevel - arrLumgates[iLoop].iTargetOutputLevel;
						If iTemp > iDimmingSpeed then
							arrLumgates[iLoop].iActualOutputLevel	:=	arrLumgates[iLoop].iActualOutputLevel - iDimmingSpeed;
						Else

							If arrLumgates[iLoop].xRelay1_NC and not (arrLumgates[iLoop].iMode = 3) then
								arrLumgates[iLoop].xRelay1	:=	not xTempRelay1;
							Else
								arrLumgates[iLoop].xRelay1	:=	xTempRelay1;
							End_if;
							If arrLumgates[iLoop].xRelay2_NC and not (arrLumgates[iLoop].iMode = 3) then
								arrLumgates[iLoop].xRelay2	:=	not xTempRelay2;
							Else
								arrLumgates[iLoop].xRelay2	:=	xTempRelay2;
							End_if;


							arrLumgates[iLoop].iActualOutputLevel	:=	arrLumgates[iLoop].iTargetOutputLevel;
						End_if;
					Else
						If arrLumgates[iLoop].xRelay1_NC and not (arrLumgates[iLoop].iMode = 3) then
							arrLumgates[iLoop].xRelay1	:=	not xTempRelay1;
						Else
							arrLumgates[iLoop].xRelay1	:=	xTempRelay1;
						End_if;
						If arrLumgates[iLoop].xRelay2_NC and not (arrLumgates[iLoop].iMode = 3) then
							arrLumgates[iLoop].xRelay2	:=	not xTempRelay2;
						Else
							arrLumgates[iLoop].xRelay2	:=	xTempRelay2;
						End_if;
					End_If;


				Else (* Apply direct the correct value *)
					arrLumgates[iLoop].iActualOutputLevel	:=	arrLumgates[iLoop].iTargetOutputLevel;
					If arrLumgates[iLoop].xRelay1_NC and not (arrLumgates[iLoop].iMode = 3) then
						arrLumgates[iLoop].xRelay1	:=	not xTempRelay1;
					Else
						arrLumgates[iLoop].xRelay1	:=	xTempRelay1;
					End_if;
					If arrLumgates[iLoop].xRelay2_NC and not (arrLumgates[iLoop].iMode = 3) then
						arrLumgates[iLoop].xRelay2	:=	not xTempRelay2;
					Else
						arrLumgates[iLoop].xRelay2	:=	xTempRelay2;
					End_if;
				End_If;
			Else
				If arrLumgates[iLoop].xRelay1_NC and not (arrLumgates[iLoop].iMode = 3) then
					arrLumgates[iLoop].xRelay1	:=	not xTempRelay1;
				Else
					arrLumgates[iLoop].xRelay1	:=	xTempRelay1;
				End_if;
				If arrLumgates[iLoop].xRelay2_NC and not (arrLumgates[iLoop].iMode = 3) then
					arrLumgates[iLoop].xRelay2	:=	not xTempRelay2;
				Else
					arrLumgates[iLoop].xRelay2	:=	xTempRelay2;
				End_if;
			End_If;

		Else (* On/Off for all devices at the same time *)
			If arrLumgates[iLoop].xGraduallyOutputChanges then
				If arrLumgates[iLoop].iTargetOutputLevel > arrLumgates[iLoop].iActualOutputLevel then
					iTemp	:=	arrLumgates[iLoop].iTargetOutputLevel - arrLumgates[iLoop].iActualOutputLevel;
					If iTemp > iDimmingSpeed then
						arrLumgates[iLoop].iActualOutputLevel	:=	arrLumgates[iLoop].iActualOutputLevel + iDimmingSpeed;
					Else
						arrLumgates[iLoop].iActualOutputLevel	:=	arrLumgates[iLoop].iTargetOutputLevel;
					End_if;
				Elsif arrLumgates[iLoop].iTargetOutputLevel < arrLumgates[iLoop].iActualOutputLevel then
					iTemp	:=	arrLumgates[iLoop].iActualOutputLevel - arrLumgates[iLoop].iTargetOutputLevel;
					If iTemp > iDimmingSpeed then
						arrLumgates[iLoop].iActualOutputLevel	:=	arrLumgates[iLoop].iActualOutputLevel - iDimmingSpeed;
					Else
						arrLumgates[iLoop].iActualOutputLevel	:=	arrLumgates[iLoop].iTargetOutputLevel;
					End_if;
				End_If;


			Else (* Apply direct the correct value *)
				arrLumgates[iLoop].iActualOutputLevel	:=	arrLumgates[iLoop].iTargetOutputLevel;
			End_If;
		End_If;
	End_If;
End_For;

(* CLO *)
iCLO := iCLO + 1;
If iCLO > 240 then
	iCLO	:=	1;
End_If;


If arrLumgates[iCLO].udtCloLed.xActive and arrLumgates[iCLO].udtHourCounterGeneral.udiHours > Udint#0
	and arrLumgates[iCLO].udtCloLed.udiHours > Udint#0 then


	rTemp1	:=	Udint_To_Real(arrLumgates[iCLO].udtHourCounterGeneral.udiHours);
	rTemp2	:=	Udint_To_Real(arrLumgates[iCLO].udtCloLed.udiHours);

	rPercentageDecrease	:=	arrLumgates[iCLO].udtCloLed.rPercentage / (rTemp2 / rTemp1);


	rPercentageDecrease	:=	arrLumgates[iCLO].udtCloLed.rPercentage - rPercentageDecrease;


	If rPercentageDecrease > arrLumgates[iCLO].udtCloLed.rPercentage then
		rPercentageDecrease	:=	arrLumgates[iCLO].udtCloLed.rPercentage;
	End_If;


	If rPercentageDecrease < 0.0 Then
		rPercentageDecrease	:=	0.0;
	End_If;


	rTempDecrease	:=	Int_To_Real(arrLumgates[iCLO].iOutputLevelScenario) * rPercentageDecrease / 100.0;


	arrLumgates[iCLO].udtCloLed.iAmountDecrease	:=	Real_To_Int(rTempDecrease);


Elsif arrLumgates[iCLO].udtCloLed.xActive then	(* Max. decrese, in case of operating hours < 1 h *)


	rTempDecrease	:=	Int_To_Real(arrLumgates[iCLO].iOutputLevelScenario) * arrLumgates[iCLO].udtCloLed.rPercentage / 100.0;
	arrLumgates[iCLO].udtCloLed.iAmountDecrease	:=	Real_To_Int(rTempDecrease);
End_If;


If arrLumgates[iCLO].udtCloMaintenance.xActive and arrLumgates[iCLO].udtHourCounterMaintenance.udiHours > Udint#0 and arrLumgates[iCLO].udtCloMaintenance.udiHours > Udint#0 then
	rTemp1	:=	Udint_To_Real(arrLumgates[iCLO].udtHourCounterMaintenance.udiHours);
	rTemp2	:=	Udint_To_Real(arrLumgates[iCLO].udtCloMaintenance.udiHours);

	rPercentageDecrease	:=	arrLumgates[iCLO].udtCloMaintenance.rPercentage / (rTemp2 / rTemp1);
	rPercentageDecrease	:=	arrLumgates[iCLO].udtCloMaintenance.rPercentage - rPercentageDecrease;


	If rPercentageDecrease > arrLumgates[iCLO].udtCloMaintenance.rPercentage then
		rPercentageDecrease	:=	arrLumgates[iCLO].udtCloMaintenance.rPercentage;
	End_If;


	If rPercentageDecrease < 0.0 then
		rPercentageDecrease	:=	0.0;
	End_If;


	rTempDecrease	:=	Int_To_Real(arrLumgates[iCLO].iOutputLevelScenario) * rPercentageDecrease / 100.0;
	arrLumgates[iCLO].udtCloMaintenance.iAmountDecrease	:=	Real_To_Int(rTempDecrease);


Elsif arrLumgates[iCLO].udtCloMaintenance.xActive then	(* Max. decrese, in case of operating hours < 1 h *)


	rTempDecrease	:=	Int_To_Real(arrLumgates[iCLO].iOutputLevelScenario) * arrLumgates[iCLO].udtCloMaintenance.rPercentage / 100.0;
	arrLumgates[iCLO].udtCloMaintenance.iAmountDecrease	:=	Real_To_Int(rTempDecrease);
End_If;
arrLumgates[iCLO].iSourceOutputLevel	:=	arrLumgates[iCLO].iTargetOutputLevel + arrLumgates[iCLO].udtCloMaintenance.iAmountDecrease + arrLumgates[iCLO].udtCloLed.iAmountDecrease;

(* Trigger to send Lumgate configuration diagnostic text *)
R_TRIG_AmmountLG(CLK:=((arrAmountLumgates[1] > 0) or (arrAmountLumgates[2] > 0) or (arrAmountLumgates[3] > 0) or (arrAmountLumgates[4] > 0) or xUnexpectedLG_Count));


If xActivate then
	(* reset Errors *)
	xErrorRBT1	:=	False;
	xErrorRBT2	:=	False;
	xErrorRBT3	:=	False;
	xErrorRBT4	:=	False;
	For iLoop := 1 to 240 do	(* Check Current deviations *)
		If (arrLumgates[iLoop].udtCurrentCheck.xWarning or arrLumgates[iLoop].udtCurrentCheck.xError) and arrLumgates[iLoop].iModuleLevel = 0 and arrLumgates[iLoop].xAvailable and iRBT1 = 0 then
			xErrorRBT1	:=	True;
			iRBT1		:=	arrLumgates[iLoop].iModuleNumber;
		Elsif iRBT1 <> 0 then
			xErrorRBT1	:=	True;
		End_if;
		If (arrLumgates[iLoop].udtCurrentCheck.xWarning or arrLumgates[iLoop].udtCurrentCheck.xError) and arrLumgates[iLoop].iModuleLevel = 1 and arrLumgates[iLoop].xAvailable and iRBT2 = 0 then
			xErrorRBT2	:=	True;
			iRBT2		:=	arrLumgates[iLoop].iModuleNumber;
		Elsif iRBT2 <> 0 then
			xErrorRBT2	:=	True;
		End_if;
		If (arrLumgates[iLoop].udtCurrentCheck.xWarning or arrLumgates[iLoop].udtCurrentCheck.xError) and arrLumgates[iLoop].iModuleLevel = 2 and arrLumgates[iLoop].xAvailable and iRBT3 = 0 then
			xErrorRBT3	:=	True;
			iRBT3		:=	arrLumgates[iLoop].iModuleNumber;
		Elsif iRBT3 <> 0 then
			xErrorRBT3	:=	True;
		End_if;
		If (arrLumgates[iLoop].udtCurrentCheck.xWarning or arrLumgates[iLoop].udtCurrentCheck.xError) and arrLumgates[iLoop].iModuleLevel = 3 and arrLumgates[iLoop].xAvailable and iRBT4 = 0 then
			xErrorRBT4	:=	True;
			iRBT4		:=	arrLumgates[iLoop].iModuleNumber;
		Elsif iRBT4 <> 0 then
			xErrorRBT4	:=	True;
		End_if;
	End_for;


	If iRBT1 <> 0 then	(* Search for Device number *)
		If not arrLumgates[iRBT1].xError and not arrLumgates[iRBT1].udtCurrentCheck.xWarning and not arrLumgates[iRBT1].udtCurrentCheck.xError then
			iRBT1	:=	0;
		Elsif iRBT1 <> 0 and xNextDevice and iSelectRBT = 0 then
			xNextDevice	:=	False;
			iRBT1	:=	iRBT1 + 1;
			For iLoop := iRBT1 to 240 do
				If (arrLumgates[iLoop].udtCurrentCheck.xWarning or arrLumgates[iLoop].udtCurrentCheck.xError) and arrLumgates[iLoop].iModuleLevel = 0 then
					xErrorRBT1	:=	True;
					iRBT1		:=	arrLumgates[iLoop].iModuleNumber;
					Exit;
				End_if;
			End_for;
			If iLoop = 241 then
				iRBT1	:=	0;
			End_if;
		End_if;
	End_if;
	If iRBT2 <> 0 then	(* Search for Device number *)
		If not arrLumgates[iRBT2].xError and not arrLumgates[iRBT2].udtCurrentCheck.xWarning and not arrLumgates[iRBT2].udtCurrentCheck.xError then
			iRBT2	:=	0;
		Elsif iRBT2 <> 0 and xNextDevice and iSelectRBT = 1 then
			xNextDevice	:=	False;
			iRBT2	:=	iRBT2 + 1;
			For iLoop := iRBT2 to 240 do
				If (arrLumgates[iLoop].udtCurrentCheck.xWarning or arrLumgates[iLoop].udtCurrentCheck.xError) and arrLumgates[iLoop].iModuleLevel = 1 then
					xErrorRBT2	:=	True;
					iRBT2		:=	arrLumgates[iLoop].iModuleNumber;
					Exit;
				End_if;
			End_for;
			If iLoop = 241 then
				iRBT2	:=	0;
			End_if;
		End_if;
	End_if;
	If iRBT3 <> 0 then	(* Search for Device number *)
		If not arrLumgates[iRBT3].xError and not arrLumgates[iRBT3].udtCurrentCheck.xWarning and not arrLumgates[iRBT3].udtCurrentCheck.xError then
			iRBT3	:=	0;
		Elsif iRBT3 <> 0 and xNextDevice and iSelectRBT = 2 then
			xNextDevice	:=	False;
			iRBT3	:=	iRBT3 + 1;
			For iLoop := iRBT3 to 240 do
				If (arrLumgates[iLoop].udtCurrentCheck.xWarning or arrLumgates[iLoop].udtCurrentCheck.xError) and arrLumgates[iLoop].iModuleLevel = 2 then
					xErrorRBT3	:=	True;
					iRBT3		:=	arrLumgates[iLoop].iModuleNumber;
					Exit;
				End_if;
			End_for;
			If iLoop = 241 then
				iRBT3	:=	0;
			End_if;
		End_if;
	End_if;
	If iRBT4 <> 0 then	(* Search for Device number *)
		If not arrLumgates[iRBT4].xError and not arrLumgates[iRBT4].udtCurrentCheck.xWarning and not arrLumgates[iRBT4].udtCurrentCheck.xError then
			iRBT4	:=	0;
		Elsif iRBT4 <> 0 and xNextDevice and iSelectRBT = 3 then
			xNextDevice	:=	False;
			iRBT1	:=	iRBT4 + 1;
			For iLoop := iRBT4 to 240 do
				If (arrLumgates[iLoop].udtCurrentCheck.xWarning or arrLumgates[iLoop].udtCurrentCheck.xError) and arrLumgates[iLoop].iModuleLevel = 3 then
					xErrorRBT4	:=	True;
					iRBT4		:=	arrLumgates[iLoop].iModuleNumber;
					Exit;
				End_if;
			End_for;
			If iLoop = 241 then
				iRBT4	:=	0;
			End_if;
		End_if;
	End_if;


	(* Calculate amount Lumgates per RBT *)
	arrAmountLumgates[1]	:=	0;
	arrAmountLumgates[2]	:=	0;
	arrAmountLumgates[3]	:=	0;
	arrAmountLumgates[4]	:=	0;
	For iLoop := 1 to 240 do
		If arrLumgates[iLoop].iModuleLevel = 0 and arrLumgates[iLoop].xAvailable then
			arrAmountLumgates[1]	:=	arrAmountLumgates[1] + 1;
		End_If;
		If arrLumgates[iLoop].iModuleLevel = 1 and arrLumgates[iLoop].xAvailable then
			arrAmountLumgates[2]	:=	arrAmountLumgates[2] + 1;
		End_If;
		If arrLumgates[iLoop].iModuleLevel = 2 and arrLumgates[iLoop].xAvailable then
			arrAmountLumgates[3]	:=	arrAmountLumgates[3] + 1;
		End_If;
		If arrLumgates[iLoop].iModuleLevel = 3 and arrLumgates[iLoop].xAvailable then
			arrAmountLumgates[4]	:=	arrAmountLumgates[4] + 1;
		End_If;
	End_For;


	iTotalLumgates	:=	arrAmountLumgates[1] + arrAmountLumgates[2] + arrAmountLumgates[3] + arrAmountLumgates[4];


	(* Compare connected Lumgates with configured Lumgates *)
	If arrSegment[1] <> arrAmountLumgates[1] then
		xErrorRBT1			:=	True;
		xUnexpectedLG_Count	:=	True;
		If R_TRIG_AmmountLG.Q then
			strDiag			:=	'Error: Unexpected count of Lumgates in Segment 1';
		End_if;
	End_if;
	If arrSegment[2] <> arrAmountLumgates[2] then
		xErrorRBT2			:=	True;
		xUnexpectedLG_Count	:=	True;
		If R_TRIG_AmmountLG.Q then
			strDiag			:=	'Error: Unexpected count of Lumgates in Segment 2';
		End_if;
	End_if;
	If arrSegment[3] <> arrAmountLumgates[3] then
		xErrorRBT3			:=	True;
		xUnexpectedLG_Count	:=	True;
		If R_TRIG_AmmountLG.Q then
			strDiag			:=	'Error: Unexpected count of Lumgates in Segment 3';
		End_if;
	End_if;
	If arrSegment[4] <> arrAmountLumgates[4] then
		xErrorRBT4			:=	True;
		xUnexpectedLG_Count	:=	True;
		If R_TRIG_AmmountLG.Q then
			strDiag			:=	'Error: Unexpected count of Lumgates in Segment 4';
		End_if;
	End_if;


	(* Confirming in case of alarms *)
	If xUnexpectedLG_Count then
		If xConfirm then
			If arrSegment[1] = arrAmountLumgates[1] and arrSegment[2] = arrAmountLumgates[2]
			and arrSegment[3] = arrAmountLumgates[3] and arrSegment[4] = arrAmountLumgates[4] then
				xUnexpectedLG_Count	:=	False;
				strDiag	:=	'Lumgate Alarm confirmed';
				Else
				strDiag	:=	'The count of Lumgates is different to the configuration';
			End_if;
		End_if;
	End_if;


	For iLoop := 1 to 240 do
		If arrLumgates[iLoop].xAutomaticManual then
			xManualActive	:=	True;
			xPreAlarm		:=	True;
			strDiag	:=	'Warning: Lumgate ';
			strDiag	:=	concat(strDiag,int_to_string(arrLumgates[iLoop].iModuleNumber,'%02d'));
			strDiag	:=	concat(strDiag,' in segment ');
			strDiag	:=	concat(strDiag,int_to_string((arrLumgates[iLoop].iModuleLevel + 1) ,'%02d'));
			strDiag	:=	concat(strDiag,' in manual mode');
			Exit;
		End_if;
	End_for;
	If iLoop = 241 then
		xManualActive	:=	False;
	End_if;


	If xAlarm then
		For iLoop := 1 to 240 do
			If arrLumgates[iLoop].udtCurrentCheck.xWarning or arrLumgates[iLoop].udtCurrentCheck.xError then
				Exit;
			End_if;
		End_for;
		If iLoop = 241 then
			strDiag		:=	'Lumgate current returns to expected current range';
		End_if;
		If xConfirm then
			For iLoop := 1 to 240 do
				If arrLumgates[iLoop].udtCurrentCheck.xWarning or arrLumgates[iLoop].udtCurrentCheck.xError then
					Exit;
				End_if;
			End_for;
			If iLoop = 241 then
				xAlarm	:=	False;
				strDiag	:=	'Lumgate Alarm confirmed';
			End_if;
		End_if;
	End_if;
	If xPreAlarm then
		For iLoop := 1 to 240 do
			If arrLumgates[iLoop].udtCurrentCheck.xWarning or arrLumgates[iLoop].xAutomaticManual then
				Exit;
			End_if;
		End_for;
		If iLoop = 241 then
			xPreAlarm	:=	False;
			strDiag		:=	'Lumgate returns to expected state';
		End_if;
	End_if;
	If not xPreAlarm and not xAlarm then
		iRBT		:=	0;
		iLumgate	:=	0;
	Elsif iSelectRBT = 0 then
		iLumgate	:=	iRBT1;
	Elsif iSelectRBT = 1 then
		iLumgate	:=	iRBT2;
	Elsif iSelectRBT = 2 then
		iLumgate	:=	iRBT3;
	Elsif iSelectRBT = 3 then
		iLumgate	:=	iRBT4;
	End_if;
Else
	arrAmountLumgates[1]	:=	0;
	arrAmountLumgates[2]	:=	0;
	arrAmountLumgates[3]	:=	0;
	arrAmountLumgates[4]	:=	0;
End_if;

If not xActivate then
	iCalculation	:=	0;
	iTempCountValue	:=	0;
	iLevel			:=	0;
	xCountLevelUp	:=	False;
	xCountLevelDown	:=	False;
	xDelayReached	:=	False;
End_If;


iValueSelection		:= 	Limit_Int(1,iValueSelection,11);	(* Set to 1 *)
iCurrentValue		:= 	real_to_int(arrValue[iValueSelection].rValue);
xValid				:= 	arrValue[iValueSelection].xValid;


Case iCalculation of
	0:
		If xActivate then
			iCalculation	:=	5;
		End_If;


	5:
		iLevel	:=	1;
		For iLoop := 1 to 24 do
			iLoop2		:=	iLoop + 1;
			(* Jump to the next Scenario, if the actual analog value is higher then the next threshold *)
			If iCurrentValue > arrLevels[iLoop] and arrLevels[iLoop] > 0 and arrScenarioActive[iLoop2] then
				iLevel	:= 	iLoop2;
			Else
				Exit;
			End_If;
		End_For;
		iCalculation	:= 	10;


	10:
		iHighLevel		:= 	iLevel + 1;	(* upper threshold *)
		iLowLevel		:= 	iLevel - 1;	(* lower threshold *)
		xSelectScene	:= 	True;
		iCalculation	:= 	20;


	20:
		xCountLevelUp	:= 	False;
		If iHighLevel <= 25 then
			(* reduce the current value with the hysteresic *)
			rCurrentValue	:=	int_to_real(iCurrentValue) * (1.0 - (int_to_real(iHysteric) / 100.0));
			rNextLevel		:=	int_to_real(arrLevels[iLevel]);
			If rCurrentValue >= rNextLevel and arrScenarioActive[iHighLevel] then	(* check next threshold *)
				xCountLevelUp	:= 	True;
			End_If;
		End_If;


		xCountLevelDown	:= 	False;
		If iLowLevel >= 1 then
			(* add the hysteresic to the current value *)
			rCurrentValue	:=	int_to_real(iCurrentValue) * ((int_to_real(iHysteric) / 100.0) + 1.0);
			rNextLevel		:=	int_to_real(arrLevels[iLowLevel]);
			If (rCurrentValue <= rNextLevel) or not arrScenarioActive[iLevel] then	(* check next threshold *)
				xCountLevelDown	:= 	True;
			End_If;
		End_If;


		If xDelayReached then	(* Wait for "Delay between switching scenes" *)
			xDelayReached	:= 	False;
			iTempCountValue	:= 	0;

			If xCountLevelUp then
				iLevel			:= 	iLevel + 1;
			End_If;


			If xCountLevelDown then
				iLevel			:= 	iLevel - 1;
			End_If;
			iCalculation		:= 	10;
		End_If;
End_Case;




If xCountLevelUp or xCountLevelDown then	(* Start delay counter *)
	If xPulseSecond then
		iTempCountValue		:= 	iTempCountValue + 1;


		If iTempCountValue >= iDelay then
			xDelayReached	:= 	True;
		End_If;
	End_If;
Else
	iTempCountValue	:= 	0;
End_If;

(* Hour Counter *)
If xCount and xPulseSecond then
	udtCounterInfo.iSeconds	:=	udtCounterInfo.iSeconds + 1;
	If udtCounterInfo.iSeconds > 59 then
		udtCounterInfo.iSeconds	:= 	0;
		udtCounterInfo.iMinutes	:= 	udtCounterInfo.iMinutes + 1;


		If udtCounterInfo.iMinutes > 59 then
			udtCounterInfo.iMinutes	:= 	0;
			udtCounterInfo.udiHours	:= 	udtCounterInfo.udiHours + udint#1;
		End_If;
	End_If;
End_If;


If udtCounterInfo.xReset then
	udtCounterInfo.iSeconds	:= 	0;
	udtCounterInfo.iMinutes	:= 	0;
	udtCounterInfo.udiHours	:= 	udint#0;
	udtCounterInfo.xReset	:= 	False;
End_If;

(* Reading serial number, IP, subnet, HW, FW *)


Case Index_Read_Version Of


0:	Serial_Send[0]:=Word#16#032A;
    Serial_Send[1]:=Word#0000;
   	Serial_Send[2]:=Word#16#0;
    Serial_Send[3]:=Word#16#0;
    Serial_Send[4]:=Word#16#0;
    Serial_Send[5]:=Word#16#0;
    Serial_Send[6]:=Word#16#0;
    Serial_Send[7]:=Word#16#0;
    Serial_Receive[0]:=Word#16#0;
    Serial_Receive[1]:=Word#16#0;


	Serial_Request:= True;
	Index_Read_Version:= 1;


1:	If Serial_NDR Then
		Serial_Request:= False;
		If Serial_Receive[0] = Word#16#832A & Serial_Receive[1] > Word#16#10 Then

			(*strHW_SerNr:= '';*)
	        For counter_serial:= 78 to 83 Do
	        	WDummy:=(Serial_Receive[counter_serial]); 
	           	WDummy:= Ror_Word(WDummy,8); 
	            ByDummy:= Word_To_Byte(WDummy);
	            If Bydummy <> Byte#32 then
	            	SDummy:= Byte_To_String(ByDummy,'%c');
	                strHW_SerNr:= Concat(strHW_SerNr,SDummy);
	            End_if;
	        	WDummy:=(Serial_Receive[counter_serial]); 
	            ByDummy:= Word_To_Byte(WDummy);
	            If Bydummy <> Byte#32 then
	            	SDummy:= Byte_To_String(ByDummy,'%c');
	                strHW_SerNr:= Concat(strHW_SerNr,SDummy);
	            End_if;
	         End_for;
		End_if;
	Index_Read_Version:= 2;
	End_if;


2:	Version_Send[0] := WORD#16#0295;
	Version_Request:= True;
	Index_Read_Version:= 3;


3:	If Version_NDR Then
		Version_Request:= False;
		If Version_Receive[0] = Word#16#8295 Then

			(*strHW_Version:= '';*)
	        ByDummy:= Version_Receive[62].B0;
	        If Bydummy <> Byte#32 then
	            SDummy:= Byte_To_String(ByDummy,'%c');
	            strHW_Version:= Concat(strHW_Version,SDummy);
	        End_if;


	        ByDummy:= Version_Receive[63].B1;
	        If Bydummy <> Byte#32 then
	            SDummy:= Byte_To_String(ByDummy,'%c');
	            strHW_Version:= Concat(strHW_Version,SDummy);
	        End_if;


			(*strFW_Version:= '';*)
	        ByDummy:= Version_Receive[21].B1;
	        If Bydummy <> Byte#32 then
	            SDummy:= Byte_To_String(ByDummy,'%c');
	            strFW_Version:= Concat(strFW_Version,SDummy);
	        End_if;
	        ByDummy:= Version_Receive[21].B0;
	        If Bydummy <> Byte#32 then
	            SDummy:= Byte_To_String(ByDummy,'%c');
	            strFW_Version:= Concat(strFW_Version,SDummy);
	        End_if;
	        ByDummy:= Version_Receive[22].B1;
	        If Bydummy <> Byte#32 then
	            SDummy:= Byte_To_String(ByDummy,'%c');
	            strFW_Version:= Concat(strFW_Version,SDummy);
	        End_if;
	        ByDummy:= Version_Receive[22].B0;
	        If Bydummy <> Byte#32 then
	            SDummy:= Byte_To_String(ByDummy,'%c');
	            strFW_Version:= Concat(strFW_Version,SDummy);
	        End_if;
		End_if;


	Index_Read_Version:= 4;
	End_if;


4:	Version_Send[0] := WORD#16#029C;
	Version_Request:= True;
	Index_Read_Version:= 5;


5:	If Version_NDR Then
visit.sSubnet:= strSubnetMask;
			udtWebvisit.sGateway:= strGateway;


		End_if;
		Index_Read_Version:= 10;




End_Case;


IB_CONTROL_Serial(REQ:= Serial_Request,
ADD_ERROR:= Serial_Add_Error,
SD_1:= Serial_Send,
RD_1:= Serial_Receive);


Serial_NDR:= IB_CONTROL_Serial.NDR;
Serial_Error:= IB_CONTROL_Serial.ERROR;
Serial_Add_Error:= IB_CONTROL_Serial.ADD_ERROR;
Serial_Send:= IB_CONTROL_Serial.SD_1;
Serial_Receive:= IB_CONTROL_Serial.RD_1;




CPU_CONTROL_Version(REQ:= Version_Request,
ADD_ERROR:= Version_Add_Error,
SD_1:= Version_Send,
RD_1:= Version_Receive);


Version_NDR:= CPU_CONTROL_Version.NDR;
Version_Error:= CPU_CONTROL_Version.ERROR;
Version_Add_Error:= CPU_CONTROL_Version.ADD_ERROR;
Version_Send:= CPU_CONTROL_Version.SD_1;
Version_Receive:= CPU_CONTROL_Version.RD_1;

(* Change network settings, restart has to be done manually *)


FB_Set_IP(Save_Settings:= strNetworkConfiguration.xSave,
Str_IP:= strNetworkConfiguration.sIP,
Str_Subnet:= strNetworkConfiguration.sSubnet,
Str_Gateway:= strNetworkConfiguration.sGateway);


strNetworkConfiguration.xDone:= FB_Set_IP.Done;
strNetworkConfiguration.sMessage:=FB_Set_IP.Message;

(* L20_ChooseScenario *)


If xActivate then	(* only one cycle = Execute *)
	xFirstResult	:=	False;
	iChosenScene	:=	0;


	For iLoop := 1 to 5 do	(* Compare Scene option hour counter *)
		iTempScene	:=	arrSceneConfig[iActualScenario][iLoop];
		If iTempScene > 0 and iTempScene < 51 then
			udiOption	:=	arrSceneHourCounter[iTempScene].udiHours * Udint#1000
						+ Int_To_Udint(arrSceneHourCounter[iTempScene].iMinutes * 10)
						+ Int_To_Udint(arrSceneHourCounter[iTempScene].iSeconds / 10);


			If xFirstResult then
				If udiOption < udiTempOption then	(* Compare Scene hour counters *)
					udiTempOption	:=	udiOption;
					iChosenScene	:=	iLoop;
				End_If;
			Else
				udiTempOption	:=	udiOption;
				xFirstResult	:=	True;
				iChosenScene	:=	iLoop;			(* Select Scene *)
			End_If;
		End_If;
	End_For;


	If iChosenScene = 0 then
		iChosenScene	:=	0;
	Else
		iChosenOption	:=	iChosenScene;
		iChosenScene	:=	arrSceneConfig[iActualScenario][iChosenScene];
	End_If;
	xActivate	:=	False;
End_If;

	(* SelectScene *)
If xActivate then
	xActive	:=	True;

	(* Supply Threshold tables with threshold values *)
	FB_ThresholdTable_01_1(udtLevelConfiguration:= arrLevelConfiguration[1],
	arrSceneConfiguration				:=	arrScenarioConfiguration[1],
	arrSceneHourCounter					:=	arrSceneHourCounter);


	arrSceneHourCounter					:=	FB_ThresholdTable_01_1.arrSceneHourCounter;


	FB_ThresholdTable_01_2(udtLevelConfiguration:= arrLevelConfiguration[2],
	arrSceneConfiguration				:= 	arrScenarioConfiguration[2],
	arrSceneHourCounter					:=	arrSceneHourCounter);


	arrSceneHourCounter					:=	FB_ThresholdTable_01_2.arrSceneHourCounter;


	FB_ThresholdTable_01_3(udtLevelConfiguration:= arrLevelConfiguration[3],
	arrSceneConfiguration				:= 	arrScenarioConfiguration[3],
	arrSceneHourCounter					:=	arrSceneHourCounter);


	arrSceneHourCounter					:=	FB_ThresholdTable_01_3.arrSceneHourCounter;


	FB_ThresholdTable_01_4(udtLevelConfiguration:= arrLevelConfiguration[4],
	arrSceneConfiguration				:= 	arrScenarioConfiguration[4],
	arrSceneHourCounter					:=	arrSceneHourCounter);


	arrSceneHourCounter					:=	FB_ThresholdTable_01_4.arrSceneHourCounter;


	(* Output calculated Scenes, Scenarios and Scene option positon number *)
	udtSceneMode.arrOwnInputScene[1]	:=	FB_ThresholdTable_01_1.iScene;
	udtSceneMode.arrScenario[1]			:=	FB_ThresholdTable_01_1.iScenario;
	udtSceneMode.arrScenarioOption[1]	:=	FB_ThresholdTable_01_1.iChosenScene;
	udtSceneMode.arrOwnInputScene[2]	:=	FB_ThresholdTable_01_2.iScene;
	udtSceneMode.arrScenario[2]			:=	FB_ThresholdTable_01_2.iScenario;
	udtSceneMode.arrScenarioOption[2]	:=	FB_ThresholdTable_01_2.iChosenScene;
	udtSceneMode.arrOwnInputScene[3]	:=	FB_ThresholdTable_01_3.iScene;
	udtSceneMode.arrScenario[3]			:=	FB_ThresholdTable_01_3.iScenario;
	udtSceneMode.arrScenarioOption[3]	:=	FB_ThresholdTable_01_3.iChosenScene;
	udtSceneMode.arrOwnInputScene[4]	:=	FB_ThresholdTable_01_4.iScene;
	udtSceneMode.arrScenario[4]			:=	FB_ThresholdTable_01_4.iScenario;
	udtSceneMode.arrScenarioOption[4]	:=	FB_ThresholdTable_01_4.iChosenScene;


	For iLoop := 1 to 4 do
		iTempMode	:= 	Limit_Int(1,udtSceneMode.arrMode[iLoop],4); (* 1 = Manual, 2 = Own input, 3 = external input, 4 = external input with own input as backup *)
		Case iTempMode of
			1:
				udtSceneMode.arrActualScene[iLoop]:= udtSceneMode.arrManualScene[iLoop];
			2:
				udtSceneMode.arrActualScene[iLoop]:= udtSceneMode.arrOwnInputScene[iLoop];
			3:
				udtSceneMode.arrActualScene[iLoop]:= udtSceneMode.arrExternalInputScene[iLoop];
			4:
				(* not used at the moment *)
				If xDataMasterValid then
					udtSceneMode.arrActualScene[iLoop]:= udtSceneMode.arrExternalInputScene[iLoop];
				Else
					udtSceneMode.arrActualScene[iLoop]:= udtSceneMode.arrOwnInputScene[iLoop];
				End_If;
		End_Case;
	End_For;
	(* Supply Threshold tables with an analog input value *)
	If iSelectAnalogValue1 > 0 then
		FB_ThresholdTable_01_1.arrInputValues[1].rValue  	:=	arrAnalogValues[iSelectAnalogValue1].rValue;
		FB_ThresholdTable_01_1.arrInputValues[1].xValid  	:=	arrAnalogValues[iSelectAnalogValue1].xValid;
	Else
		FB_ThresholdTable_01_1.arrInputValues[1].rValue  	:=	0.0;
		FB_ThresholdTable_01_1.arrInputValues[1].xValid  	:=	False;
	End_if;
	FB_ThresholdTable_01_1.xPulseSecond						:=	xPulseSecond;


	If iSelectAnalogValue2 > 0 then
		FB_ThresholdTable_01_2.arrInputValues[1].rValue  	:=	arrAnalogValues[iSelectAnalogValue2].rValue;
		FB_ThresholdTable_01_2.arrInputValues[1].xValid  	:=	arrAnalogValues[iSelectAnalogValue2].xValid;
	Else
		FB_ThresholdTable_01_2.arrInputValues[1].rValue  	:=	0.0;
		FB_ThresholdTable_01_2.arrInputValues[1].xValid  	:=	False;
	End_if;
	FB_ThresholdTable_01_2.xPulseSecond						:=	xPulseSecond;


	If iSelectAnalogValue3 > 0 then
		FB_ThresholdTable_01_3.arrInputValues[1].rValue  	:=	arrAnalogValues[iSelectAnalogValue3].rValue;
		FB_ThresholdTable_01_3.arrInputValues[1].xValid  	:=	arrAnalogValues[iSelectAnalogValue3].xValid;
	Else
		FB_ThresholdTable_01_3.arrInputValues[1].rValue  	:=	0.0;
		FB_ThresholdTable_01_3.arrInputValues[1].xValid  	:=	False;
	End_if;
	FB_ThresholdTable_01_3.xPulseSecond						:=	xPulseSecond;


	If iSelectAnalogValue4 > 0 then
		FB_ThresholdTable_01_4.arrInputValues[1].rValue  	:=	arrAnalogValues[iSelectAnalogValue4].rValue;
		FB_ThresholdTable_01_4.arrInputValues[1].xValid  	:=	arrAnalogValues[iSelectAnalogValue4].xValid;
	Else
		FB_ThresholdTable_01_4.arrInputValues[1].rValue  	:=	0.0;
		FB_ThresholdTable_01_4.arrInputValues[1].xValid  	:=	False;
	End_if;
	FB_ThresholdTable_01_4.xPulseSecond						:=	xPulseSecond;


Else
	xActive	:=	False;
End_if;

(* ThresholdTable *)


(* Level calculation based on threshold config table 1, config 1 *)
FB_LuminanceLevel_01_1(xActivate	:= 	udtLevelConfiguration.xActive,
xPulseSecond					:= 	xPulseSecond,
iDelay							:= 	udtLevelConfiguration.iDelay,
iHysteric						:=	udtLevelConfiguration.iHysteric,
iValueSelection					:= 	1,
arrValue						:=	arrInputValues,
arrLevels						:= 	udtLevelConfiguration.arrScenarioConfig1,
arrScenarioActive				:= 	udtLevelConfiguration.arrScenarioActive,
xSelectScene					:= 	xSelectSceneConfig1);


iActualLevelConfig1				:=	FB_LuminanceLevel_01_1.iLevel;
xValidSceneConfig1				:=	FB_LuminanceLevel_01_1.xValid;
xSelectSceneConfig1				:=	FB_LuminanceLevel_01_1.xSelectScene;


(* Level calculation based on threshold config table 1, config 2 *)
FB_LuminanceLevel_01_2(xActivate	:= 	udtLevelConfiguration.xActive,
xPulseSecond					:= 	xPulseSecond,
iDelay							:= 	udtLevelConfiguration.iDelay,
iHysteric						:=	udtLevelConfiguration.iHysteric,
iValueSelection					:= 	1,
arrValue						:= 	arrInputValues,
arrLevels						:= 	udtLevelConfiguration.arrScenarioConfig2,
arrScenarioActive				:= 	udtLevelConfiguration.arrScenarioActive,
xSelectScene					:= 	xSelectSceneConfig2);


iActualLevelConfig2				:=	FB_LuminanceLevel_01_2.iLevel;
xValidSceneConfig2				:=	FB_LuminanceLevel_01_2.xValid;
xSelectSceneConfig2				:=	FB_LuminanceLevel_01_2.xSelectScene;


(* Level calculation based on threshold config table 1, config 3 *)
FB_LuminanceLevel_01_3(xActivate	:= 	udtLevelConfiguration.xActive,
xPulseSecond					:=	xPulseSecond,
iDelay							:= 	udtLevelConfiguration.iDelay,
iHysteric						:=	udtLevelConfiguration.iHysteric,
iValueSelection					:= 	1,
arrValue						:= 	arrInputValues,
arrLevels						:= 	udtLevelConfiguration.arrScenarioConfig3,
arrScenarioActive				:= 	udtLevelConfiguration.arrScenarioActive,
xSelectScene					:= 	xSelectSceneConfig3);


iActualLevelConfig3				:=	FB_LuminanceLevel_01_3.iLevel;
xValidSceneConfig3				:=	FB_LuminanceLevel_01_3.xValid;
xSelectSceneConfig3				:=	FB_LuminanceLevel_01_3.xSelectScene;


(* Level calculation based on threshold config table 1, config 4 *)
FB_LuminanceLevel_01_4(xActivate	:=	udtLevelConfiguration.xActive,
iValueSelection					:=	1,
xPulseSecond					:=	xPulseSecond,
iDelay							:=	udtLevelConfiguration.iDelay,
iHysteric						:=	udtLevelConfiguration.iHysteric,
arrValue						:=	arrInputValues,
arrLevels						:=	udtLevelConfiguration.arrScenarioConfig4,
arrScenarioActive				:=	udtLevelConfiguration.arrScenarioActive,
xSelectScene					:=	xSelectSceneConfig4);


iActualLevelConfig4				:=	FB_LuminanceLevel_01_4.iLevel;
xValidSceneConfig4				:=	FB_LuminanceLevel_01_4.xValid;
xSelectSceneConfig4				:=	FB_LuminanceLevel_01_4.xSelectScene;




(* Scenario selection based on level and hour counters *)
FB_L20_ChooseScene_01_1(iActualScenario	:= 	iActualLevelConfig1,
arrSceneConfig							:= 	arrSceneConfiguration,
xActivate								:= 	xSelectSceneConfig1,
arrSceneHourCounter						:=	arrSceneHourCounter);


arrSceneHourCounter						:=	FB_L20_ChooseScene_01_1.arrSceneHourCounter;
xSelectSceneConfig1						:=	FB_L20_ChooseScene_01_1.xActivate;


(* Scenario selection based on level and hour counters *)
FB_L20_ChooseScene_01_2(iActualScenario	:= 	iActualLevelConfig2,
arrSceneConfig							:= 	arrSceneConfiguration,
xActivate								:= 	xSelectSceneConfig2,
arrSceneHourCounter						:=	arrSceneHourCounter);


arrSceneHourCounter						:=	FB_L20_ChooseScene_01_2.arrSceneHourCounter;
xSelectSceneConfig2						:=	FB_L20_ChooseScene_01_2.xActivate;


(* Scenario selection based on level and hour counters *)
FB_L20_ChooseScene_01_3(iActualScenario	:= 	iActualLevelConfig3,
arrSceneConfig							:= 	arrSceneConfiguration,
xActivate								:= 	xSelectSceneConfig3,
arrSceneHourCounter						:=	arrSceneHourCounter);


arrSceneHourCounter						:=	FB_L20_ChooseScene_01_3.arrSceneHourCounter;
xSelectSceneConfig3						:=	FB_L20_ChooseScene_01_3.xActivate;


(* Scenario selection based on level and hour counters *)
FB_L20_ChooseScene_01_4(iActualScenario	:=	iActualLevelConfig4,
arrSceneConfig							:=	arrSceneConfiguration,
xActivate								:=	xSelectSceneConfig4,
arrSceneHourCounter						:=	arrSceneHourCounter);


xSelectSceneConfig4						:=	FB_L20_ChooseScene_01_4.xActivate;
arrSceneHourCounter						:=	FB_L20_ChooseScene_01_4.arrSceneHourCounter;






If not udtLevelConfiguration.xActive then (* Scene calculation is not necessary *)
	iScene		:=	0;
	iScenario	:=	0;
Else
	If udtLevelConfiguration.xActiveConfig1 Then
		If xValidSceneConfig1 Then
			iScene			:=	FB_L20_ChooseScene_01_1.iChosenScene;
			iScenario		:=	iActualLevelConfig1;
			iChosenScene	:=	FB_L20_ChooseScene_01_1.iChosenOption;
		Else
			iScene			:=	-1;	(* Invalid, use failsafe value *)
			iScenario		:=	0;
			iChosenScene	:=	0;
		End_If;
	Elsif udtLevelConfiguration.xActiveConfig2 Then
		If xValidSceneConfig2 Then
			iScene			:=	FB_L20_ChooseScene_01_2.iChosenScene;
			iScenario		:=	iActualLevelConfig2;
			iChosenScene	:=	FB_L20_ChooseScene_01_2.iChosenOption;
		Else
			iScene			:=	-1;
			iScenario		:=	0;
			iChosenScene	:=	0;
		End_If;
	Elsif  udtLevelConfiguration.xActiveConfig3 Then
		If xValidSceneConfig3 Then
			iScene			:=	FB_L20_ChooseScene_01_3.iChosenScene;
			iScenario		:=	iActualLevelConfig3;
			iChosenScene	:=	FB_L20_ChooseScene_01_3.iChosenOption;
		Else
			iScene			:=	-1;
			iScenario		:=	0;
			iChosenScene	:=	0;
		End_If;
	Elsif  udtLevelConfiguration.xActiveConfig4 Then
		If xValidSceneConfig4 Then
			iScene			:=	FB_L20_ChooseScene_01_4.iChosenScene;
			iScenario		:=	iActualLevelConfig4;
			iChosenScene	:=	FB_L20_ChooseScene_01_4.iChosenOption;
		Else
			iScene			:=	-1;
			iScenario		:=	0;
			iChosenScene	:=	0;
		End_If;
	Else
			iScene			:=	-1;
			iScenario		:=	0;
			iChosenScene	:=	0;
	End_If;
End_If;

(*SceneHourCounter *)
(* Maximum 4 scenes are active at the same time *)
If arrScenario[1] > 0 and arrScenario[1] < 51 then
	iTempScene		:= 	arrScenario[1];


	FB_HourCounter_01_1(xCount	:= 	True,
	xPulseSecond	:=	xPulseSecond,
	udtCounterInfo	:= 	arrScenarioHourCounter[iTempScene]);
	arrScenarioHourCounter[iTempScene]	:= 	FB_HourCounter_01_1.udtCounterInfo;
End_If;


If arrScenario[2] > 0 and arrScenario[2] < 51
	and arrScenario[2] <> arrScenario[1] then
	iTempScene	:= 	arrScenario[2];


	FB_HourCounter_01_2(xCount	:= 	True,
	xPulseSecond	:=	xPulseSecond,
	udtCounterInfo	:= 	arrScenarioHourCounter[iTempScene]);
	arrScenarioHourCounter[iTempScene]	:= 	FB_HourCounter_01_2.udtCounterInfo;
End_If;


If arrScenario[3] > 0 and arrScenario[3] < 51
	and arrScenario[3] <> arrScenario[1]
	and arrScenario[3] <> arrScenario[2] then
	iTempScene	:= 	arrScenario[3];


	FB_HourCounter_01_3(xCount	:= 	True,
	xPulseSecond	:=	xPulseSecond,
	udtCounterInfo	:= 	arrScenarioHourCounter[iTempScene]);
	arrScenarioHourCounter[iTempScene]	:= 	FB_HourCounter_01_3.udtCounterInfo;
End_If;


If arrScenario[4] > 0 and arrScenario[4] < 51
	and arrScenario[4] <> arrScenario[1]
	and arrScenario[4] <> arrScenario[2]
	and arrScenario[4] <> arrScenario[3] then
	iTempScene	:= 	arrScenario[4];


	FB_HourCounter_01_4(xCount	:= 	True,
	xPulseSecond	:=	xPulseSecond,
	udtCounterInfo	:= 	arrScenarioHourCounter[iTempScene]);
	arrScenarioHourCounter[iTempScene]	:= 	FB_HourCounter_01_4.udtCounterInfo;
End_If;
