Type
	ARR_IBS_PD_DATA_B_1_16		:	Array	[1..16]	of	Byte;		(* Process data of one IBS module *)


	UDT_IBS_CONF_ENTRY	:	(* Information about IBS module: Read in by service read configuration, read processdata descriptions *)
	struct
		bPosNum				:	Byte;					(*Position of module in bus segment*)
		bBusSegm			:	Byte;					(*Bus segment of module*)
		bModuleID			:	Byte;					(*Module ID*)
		iPdLenCompl			:	int;					(*Process data length in Bytes.	
														Each module have an equal length of input and output data.
														A module with pd length of one uses two Bytes in the global pd field.*)
		xInputPD			:	Bool;					(*Interbus module has input process data*)
		xOutputPD			:	Bool;					(*Interbus module has output process data*)


		xDeviceCopyed		:	Bool;	(*  mark device after copy the modulenumber *)


		iPntrPdBeginOut		:	int;					(*Begin of PD in output Pd Array*)
		iPdLenOut			:	int;					(*Length of input process data*)
		iPntrPdBeginIn		:	int;					(*Begin	of PD in input Pd Array*)
		iPdLenIn			:	int;					(*Length of output process data*)
		iCntPdd				:	int;					(*Number of process	data descriptions*)			
		iReservedBytesIn	:	int;					(*Number of Bytes reserved for input process data*)
		iReservedBytesOut	:	int;					(*Number of Bytes reserved for output process data*)


		strModuleType		:	string;					(*Module type*)
		wModuleLevel		:	Word;


		arrPdIn				:	ARR_IBS_PD_DATA_B_1_16;	(*Input process data of this module*)
		arrPdOut			:	ARR_IBS_PD_DATA_B_1_16;	(*Output process data of this moduel*)
	End_struct;


	ARR_IBS_CONF_ENTRIES_1_256	:	Array	[1..256]	of	UDT_IBS_CONF_ENTRY;	(*Array of IBS modules in the local bus*)
End_type




